package com.vhn.doan.data.model;

import java.util.Map;

/**
 * Model class để ánh xạ dữ liệu video ngắn từ Firebase node /videos/{videoId}
 * Sử dụng cho chức năng hiển thị video ngắn trong ShortVideoFragment
 */
public class ShortVideoModel {

    private String id; // Thêm field ID
    private String title;
    private String caption;
    private Long uploadDate;
    private String videoUrl;
    private String thumbnailUrl;
    private String categoryId;
    private Map<String, Boolean> tags;
    private Long viewCount;
    private Long likeCount;
    private String userId;

    /**
     * Constructor rỗng bắt buộc cho Firebase Realtime Database
     */
    public ShortVideoModel() {
        // Constructor rỗng để Firebase có thể khởi tạo object
    }

    /**
     * Constructor đầy đủ tham số
     */
    public ShortVideoModel(String id, String title, String caption, Long uploadDate, String videoUrl,
                          String thumbnailUrl, String categoryId, Map<String, Boolean> tags,
                          Long viewCount, Long likeCount, String userId) {
        this.id = id;
        this.title = title;
        this.caption = caption;
        this.uploadDate = uploadDate;
        this.videoUrl = videoUrl;
        this.thumbnailUrl = thumbnailUrl;
        this.categoryId = categoryId;
        this.tags = tags;
        this.viewCount = viewCount;
        this.likeCount = likeCount;
        this.userId = userId;
    }

    // Getter methods
    public String getId() {
        return id;
    }

    public String getTitle() {
        return title;
    }

    public String getCaption() {
        return caption;
    }

    public Long getUploadDate() {
        return uploadDate;
    }

    public String getVideoUrl() {
        return videoUrl;
    }

    public String getThumbnailUrl() {
        return thumbnailUrl;
    }

    public String getCategoryId() {
        return categoryId;
    }

    public Map<String, Boolean> getTags() {
        return tags;
    }

    public Long getViewCount() {
        return viewCount;
    }

    public Long getLikeCount() {
        return likeCount;
    }

    public String getUserId() {
        return userId;
    }

    // Setter methods
    public void setId(String id) {
        this.id = id;
    }

    public void setTitle(String title) {
        this.title = title;
    }

    public void setCaption(String caption) {
        this.caption = caption;
    }

    public void setUploadDate(Long uploadDate) {
        this.uploadDate = uploadDate;
    }

    public void setVideoUrl(String videoUrl) {
        this.videoUrl = videoUrl;
    }

    public void setThumbnailUrl(String thumbnailUrl) {
        this.thumbnailUrl = thumbnailUrl;
    }

    public void setCategoryId(String categoryId) {
        this.categoryId = categoryId;
    }

    public void setTags(Map<String, Boolean> tags) {
        this.tags = tags;
    }

    public void setViewCount(Long viewCount) {
        this.viewCount = viewCount;
    }

    public void setLikeCount(Long likeCount) {
        this.likeCount = likeCount;
    }

    public void setUserId(String userId) {
        this.userId = userId;
    }

    /**
     * Kiểm tra xem video có tag cụ thể hay không
     * @param tag Tag cần kiểm tra
     * @return true nếu video có tag này và tag được đánh dấu true
     */
    public boolean hasTag(String tag) {
        return tags != null && Boolean.TRUE.equals(tags.get(tag));
    }

    /**
     * Thêm tag cho video
     * @param tag Tag cần thêm
     */
    public void addTag(String tag) {
        if (tags != null) {
            tags.put(tag, true);
        }
    }

    /**
     * Xóa tag khỏi video
     * @param tag Tag cần xóa
     */
    public void removeTag(String tag) {
        if (tags != null) {
            tags.remove(tag);
        }
    }

    @Override
    public String toString() {
        return "ShortVideoModel{" +
                "id='" + id + '\'' +
                ", title='" + title + '\'' +
                ", caption='" + caption + '\'' +
                ", uploadDate=" + uploadDate +
                ", videoUrl='" + videoUrl + '\'' +
                ", thumbnailUrl='" + thumbnailUrl + '\'' +
                ", categoryId='" + categoryId + '\'' +
                ", tags=" + tags +
                ", viewCount=" + viewCount +
                ", likeCount=" + likeCount +
                ", userId='" + userId + '\'' +
                '}';
    }
}

