package com.vhn.doan.data.model;

import java.util.HashMap;
import java.util.Map;

/**
 * Model class để ánh xạ dữ liệu sở thích người dùng từ Firebase node /users/{userId}/preferences
 * Lưu trữ độ ưa thích của người dùng đối với các chủ đề dưới dạng Map<String, Float>
 * Giá trị Float từ 0.0 đến 1.0, trong đó 1.0 là ưa thích nhất
 */
public class UserPreferenceModel {

    private Map<String, Float> preferences;

    /**
     * Constructor rỗng bắt buộc cho Firebase Realtime Database
     */
    public UserPreferenceModel() {
        this.preferences = new HashMap<>();
    }

    /**
     * Constructor với Map preferences
     * @param preferences Map chứa sở thích người dùng (key: topic, value: preference score 0.0-1.0)
     */
    public UserPreferenceModel(Map<String, Float> preferences) {
        this.preferences = preferences != null ? preferences : new HashMap<>();
    }

    // Getter và Setter
    public Map<String, Float> getPreferences() {
        return preferences;
    }

    public void setPreferences(Map<String, Float> preferences) {
        this.preferences = preferences != null ? preferences : new HashMap<>();
    }

    /**
     * Lấy điểm sở thích cho một chủ đề cụ thể
     * @param topic Chủ đề cần lấy điểm
     * @return Điểm sở thích (0.0-1.0) hoặc 0.0 nếu không tồn tại
     */
    public float getPreferenceScore(String topic) {
        if (preferences == null || topic == null) {
            return 0.0f;
        }
        Float score = preferences.get(topic);
        return score != null ? score : 0.0f;
    }

    /**
     * Cập nhật điểm sở thích cho một chủ đề
     * @param topic Chủ đề cần cập nhật
     * @param score Điểm sở thích mới (0.0-1.0)
     */
    public void setPreferenceScore(String topic, float score) {
        if (preferences == null) {
            preferences = new HashMap<>();
        }
        if (topic != null) {
            // Đảm bảo điểm số trong khoảng 0.0-1.0
            score = Math.max(0.0f, Math.min(1.0f, score));
            preferences.put(topic, score);
        }
    }

    /**
     * Tăng điểm sở thích cho một chủ đề
     * @param topic Chủ đề cần tăng điểm
     * @param increment Mức tăng (có thể âm để giảm điểm)
     */
    public void incrementPreference(String topic, float increment) {
        float currentScore = getPreferenceScore(topic);
        setPreferenceScore(topic, currentScore + increment);
    }

    /**
     * Xóa sở thích cho một chủ đề
     * @param topic Chủ đề cần xóa
     */
    public void removePreference(String topic) {
        if (preferences != null && topic != null) {
            preferences.remove(topic);
        }
    }

    /**
     * Kiểm tra xem có sở thích cho chủ đề nào không
     * @param topic Chủ đề cần kiểm tra
     * @return true nếu có sở thích cho chủ đề này
     */
    public boolean hasPreference(String topic) {
        return preferences != null && topic != null && preferences.containsKey(topic);
    }

    /**
     * Lấy số lượng chủ đề có sở thích
     * @return Số lượng chủ đề
     */
    public int getPreferenceCount() {
        return preferences != null ? preferences.size() : 0;
    }

    /**
     * Xóa tất cả sở thích
     */
    public void clearPreferences() {
        if (preferences != null) {
            preferences.clear();
        }
    }

    @Override
    public String toString() {
        return "UserPreferenceModel{" +
                "preferences=" + preferences +
                '}';
    }
}

package com.vhn.doan.data.model;

import java.text.Normalizer;
import java.util.HashMap;
import java.util.Map;

/**
 * Model class để ánh xạ dữ liệu chủ đề người dùng từ Firebase node /user_topics/{userId}
 * Lưu trữ số lần tương tác của người dùng với các chủ đề dưới dạng Map<String, Long>
 * Key có thể chứa khoảng trắng hoặc tiếng Việt, cần chuẩn hóa khi sử dụng làm tag
 */
public class UserTopicModel {

    private Map<String, Long> topics;

    /**
     * Constructor rỗng bắt buộc cho Firebase Realtime Database
     */
    public UserTopicModel() {
        this.topics = new HashMap<>();
    }

    /**
     * Constructor với Map topics
     * @param topics Map chứa chủ đề và số lần tương tác (key: topic name, value: interaction count)
     */
    public UserTopicModel(Map<String, Long> topics) {
        this.topics = topics != null ? topics : new HashMap<>();
    }

    // Getter và Setter
    public Map<String, Long> getTopics() {
        return topics;
    }

    public void setTopics(Map<String, Long> topics) {
        this.topics = topics != null ? topics : new HashMap<>();
    }

    /**
     * Lấy số lần tương tác với một chủ đề cụ thể
     * @param topic Tên chủ đề
     * @return Số lần tương tác hoặc 0 nếu không tồn tại
     */
    public long getTopicCount(String topic) {
        if (topics == null || topic == null) {
            return 0L;
        }
        Long count = topics.get(topic);
        return count != null ? count : 0L;
    }

    /**
     * Cập nhật số lần tương tác cho một chủ đề
     * @param topic Tên chủ đề
     * @param count Số lần tương tác mới
     */
    public void setTopicCount(String topic, long count) {
        if (topics == null) {
            topics = new HashMap<>();
        }
        if (topic != null && count >= 0) {
            topics.put(topic, count);
        }
    }

    /**
     * Tăng số lần tương tác cho một chủ đề
     * @param topic Tên chủ đề
     * @param increment Số lần tăng (mặc định là 1)
     */
    public void incrementTopicCount(String topic, long increment) {
        long currentCount = getTopicCount(topic);
        setTopicCount(topic, currentCount + increment);
    }

    /**
     * Tăng số lần tương tác cho một chủ đề (tăng 1)
     * @param topic Tên chủ đề
     */
    public void incrementTopicCount(String topic) {
        incrementTopicCount(topic, 1L);
    }

    /**
     * Xóa một chủ đề
     * @param topic Tên chủ đề cần xóa
     */
    public void removeTopic(String topic) {
        if (topics != null && topic != null) {
            topics.remove(topic);
        }
    }

    /**
     * Kiểm tra xem có chủ đề nào không
     * @param topic Tên chủ đề cần kiểm tra
     * @return true nếu có chủ đề này
     */
    public boolean hasTopic(String topic) {
        return topics != null && topic != null && topics.containsKey(topic);
    }

    /**
     * Lấy số lượng chủ đề
     * @return Số lượng chủ đề
     */
    public int getTopicCount() {
        return topics != null ? topics.size() : 0;
    }

    /**
     * Xóa tất cả chủ đề
     */
    public void clearTopics() {
        if (topics != null) {
            topics.clear();
        }
    }

    /**
     * Chuẩn hóa tên chủ đề thành tag sử dụng được
     * Chuyển đổi các ký tự tiếng Việt có dấu thành không dấu
     * Loại bỏ khoảng trắng và ký tự đặc biệt
     * Chuyển thành chữ thường
     * 
     * Ví dụ: "Tim Mạch" → "timmach", "Sức Khỏe" → "suckhoe"
     * 
     * @param topicName Tên chủ đề gốc
     * @return Tag đã được chuẩn hóa
     */
    public static String normalizeTopicToTag(String topicName) {
        if (topicName == null || topicName.trim().isEmpty()) {
            return "";
        }

        // Chuyển về chữ thường
        String normalized = topicName.toLowerCase().trim();

        // Loại bỏ dấu tiếng Việt
        normalized = Normalizer.normalize(normalized, Normalizer.Form.NFD);
        normalized = normalized.replaceAll("\\p{InCombiningDiacriticalMarks}+", "");

        // Thay thế các ký tự đặc biệt của tiếng Việt
        normalized = normalized.replace("đ", "d").replace("Đ", "d");

        // Loại bỏ khoảng trắng và ký tự đặc biệt, chỉ giữ lại chữ cái và số
        normalized = normalized.replaceAll("[^a-zA-Z0-9]", "");

        return normalized;
    }

    /**
     * Lấy tag đã chuẩn hóa từ một chủ đề có trong model
     * @param topic Tên chủ đề
     * @return Tag đã chuẩn hóa nếu chủ đề tồn tại, null nếu không tồn tại
     */
    public String getTopicTag(String topic) {
        if (hasTopic(topic)) {
            return normalizeTopicToTag(topic);
        }
        return null;
    }

    /**
     * Lấy Map với key là tag đã chuẩn hóa và value là số lần tương tác
     * @return Map<tag_chuẩn_hóa, số_lần_tương_tác>
     */
    public Map<String, Long> getTopicsAsTagMap() {
        Map<String, Long> tagMap = new HashMap<>();
        if (topics != null) {
            for (Map.Entry<String, Long> entry : topics.entrySet()) {
                String tag = normalizeTopicToTag(entry.getKey());
                if (!tag.isEmpty()) {
                    tagMap.put(tag, entry.getValue());
                }
            }
        }
        return tagMap;
    }

    @Override
    public String toString() {
        return "UserTopicModel{" +
                "topics=" + topics +
                '}';
    }
}

package com.vhn.doan.data.repository;

import com.google.firebase.database.DataSnapshot;
import com.google.firebase.database.DatabaseError;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.database.Query;
import com.google.firebase.database.ValueEventListener;
import com.vhn.doan.data.ShortVideo;

import java.util.ArrayList;
import java.util.Collections;
import java.util.Comparator;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * Implementation của ShortVideoRepository
 * Xử lý việc lấy và quản lý dữ liệu video ngắn từ Firebase
 */
public class ShortVideoRepositoryImpl implements ShortVideoRepository {

    private final DatabaseReference databaseReference;
    private final DatabaseReference videosRef;
    private final DatabaseReference usersRef;
    private final DatabaseReference userTopicsRef;
    private final DatabaseReference trendingVideosRef;

    public ShortVideoRepositoryImpl() {
        this.databaseReference = FirebaseDatabase.getInstance().getReference();
        this.videosRef = databaseReference.child("videos");
        this.usersRef = databaseReference.child("users");
        this.userTopicsRef = databaseReference.child("user_topics");
        this.trendingVideosRef = databaseReference.child("trendingVideos").child("vietnam");
    }

    @Override
    public void getRecommendedVideos(String userId, int limit, RepositoryCallback<List<ShortVideo>> callback) {
        // Lấy sở thích người dùng trước
        getUserPreferences(userId, new RepositoryCallback<Map<String, Float>>() {
            @Override
            public void onSuccess(Map<String, Float> userPreferences) {
                if (userPreferences == null || userPreferences.isEmpty()) {
                    // Người dùng mới hoặc chưa có sở thích -> lấy video trending
                    getTrendingVideos(limit, callback);
                } else {
                    // Lấy video theo sở thích người dùng
                    getVideosBasedOnPreferences(userPreferences, limit, callback);
                }
            }

            @Override
            public void onError(String error) {
                // Fallback về trending videos nếu có lỗi
                getTrendingVideos(limit, callback);
            }
        });
    }

    @Override
    public void getVideosByCategory(String categoryId, int limit, RepositoryCallback<List<ShortVideo>> callback) {
        Query query = videosRef.orderByChild("categoryId").equalTo(categoryId).limitToLast(limit);

        query.addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                List<ShortVideo> videos = new ArrayList<>();
                for (DataSnapshot snapshot : dataSnapshot.getChildren()) {
                    ShortVideo video = snapshot.getValue(ShortVideo.class);
                    if (video != null) {
                        // Tự động set ID từ Firebase key
                        video.setId(snapshot.getKey());
                        videos.add(video);
                    }
                videos.sort((v1, v2) -> Long.compare(v2.getUploadDate(), v1.getUploadDate()));
                callback.onSuccess(videos);
                        // Tự động set ID từ Firebase key
                        video.setId(snapshot.getKey());
                videos.sort((v1, v2) -> Long.compare(v2.getUploadDate(), v1.getUploadDate()));

        // Kiểm tra videoId không null để tránh crash
