                for (DataSnapshot snapshot : dataSnapshot.getChildren()) {
                        allVideos.add(video);

                for (ShortVideo video : allVideos) {
                }
                Collections.sort(videosWithScore, (v1, v2) -> Float.compare(v2.score, v1.score));

                // Chuyển về List<ShortVideo> và giới hạn số lượng
                List<ShortVideo> recommendedVideos = new ArrayList<>();
                for (int i = 0; i < Math.min(limit, videosWithScore.size()); i++) {
                    recommendedVideos.add(videosWithScore.get(i).video);
                }

                callback.onSuccess(recommendedVideos);
            }

            @Override
            public void onCancelled(DatabaseError databaseError) {
                callback.onError(databaseError.getMessage());
            }
        });
    }

    /**
     * Tính điểm cho video dựa trên sở thích người dùng
     */
    private float calculateVideoScore(ShortVideo video, Map<String, Float> userPreferences) {
        float score = 0.0f;

        // Điểm từ tags khớp với sở thích
        if (video.getTags() != null) {
            for (String tag : video.getTags().keySet()) {
                if (Boolean.TRUE.equals(video.getTags().get(tag)) && userPreferences.containsKey(tag)) {
                    score += userPreferences.get(tag) * 10; // Nhân 10 để tăng trọng số
                }
            }
        }

        // Bonus cho video mới
        long daysSinceUpload = (System.currentTimeMillis() - video.getUploadDate()) / (24 * 60 * 60 * 1000);
        if (daysSinceUpload <= 7) {
            score += 5.0f; // Bonus cho video mới trong 7 ngày
        }

        // Bonus từ engagement (view và like)
        score += video.getViewCount() * 0.01f + video.getLikeCount() * 0.1f;

        return score;
    }

    /**
     * Class helper để lưu video kèm điểm số
     */
    private static class VideoWithScore {
        final ShortVideo video;
        final float score;

        VideoWithScore(ShortVideo video, float score) {
            this.video = video;
            this.score = score;
        }
    }
}

package com.vhn.doan.data.repository;

import com.google.firebase.database.DataSnapshot;
import com.google.firebase.database.DatabaseError;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.database.Query;
import com.google.firebase.database.ValueEventListener;
import com.vhn.doan.data.ShortVideo;

import java.util.ArrayList;
import java.util.Collections;
import java.util.Comparator;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * Implementation của ShortVideoRepository
 * Xử lý việc lấy và quản lý dữ liệu video ngắn từ Firebase
 */
public class ShortVideoRepositoryImpl implements ShortVideoRepository {

    private final DatabaseReference databaseReference;
    private final DatabaseReference videosRef;
    private final DatabaseReference usersRef;
    private final DatabaseReference userTopicsRef;
    private final DatabaseReference trendingVideosRef;

    public ShortVideoRepositoryImpl() {
        this.databaseReference = FirebaseDatabase.getInstance().getReference();
        this.videosRef = databaseReference.child("videos");
        this.usersRef = databaseReference.child("users");
        this.userTopicsRef = databaseReference.child("user_topics");
        this.trendingVideosRef = databaseReference.child("trendingVideos").child("vietnam");
    }

    @Override
    public void getRecommendedVideos(String userId, int limit, RepositoryCallback<List<ShortVideo>> callback) {
        // Lấy sở thích người dùng trước
        getUserPreferences(userId, new RepositoryCallback<Map<String, Float>>() {
            @Override
            public void onSuccess(Map<String, Float> userPreferences) {
                if (userPreferences == null || userPreferences.isEmpty()) {
                    // Người dùng mới hoặc chưa có sở thích -> lấy video trending
                    getTrendingVideos(limit, callback);
                } else {
                    // Lấy video theo sở thích người dùng
                    getVideosBasedOnPreferences(userPreferences, limit, callback);
                }
            }

            @Override
            public void onError(String error) {
                // Fallback về trending videos nếu có lỗi
                getTrendingVideos(limit, callback);
            }
        });
    }

    @Override
    public void getVideosByCategory(String categoryId, int limit, RepositoryCallback<List<ShortVideo>> callback) {
        Query query = videosRef.orderByChild("categoryId").equalTo(categoryId).limitToLast(limit);

        query.addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                List<ShortVideo> videos = new ArrayList<>();
                for (DataSnapshot snapshot : dataSnapshot.getChildren()) {
                    ShortVideo video = snapshot.getValue(ShortVideo.class);
                    if (video != null) {
                        // Tự động set ID từ Firebase key
                        video.setId(snapshot.getKey());
                        videos.add(video);
                    }
                }

                // Sắp xếp theo uploadDate giảm dần (mới nhất trước)
                Collections.sort(videos, (v1, v2) -> Long.compare(v2.getUploadDate(), v1.getUploadDate()));
                callback.onSuccess(videos);
            }

            @Override
            public void onCancelled(DatabaseError databaseError) {
                callback.onError(databaseError.getMessage());
            }
        });
    }

    @Override
    public void getTrendingVideos(int limit, RepositoryCallback<List<ShortVideo>> callback) {
        // Lấy video trending từ node riêng hoặc sắp xếp theo viewCount + likeCount
        videosRef.orderByChild("viewCount").limitToLast(limit * 2)
                .addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                List<ShortVideo> videos = new ArrayList<>();
                for (DataSnapshot snapshot : dataSnapshot.getChildren()) {
                    ShortVideo video = snapshot.getValue(ShortVideo.class);
                    if (video != null) {
                        // Tự động set ID từ Firebase key
                        video.setId(snapshot.getKey());
                        videos.add(video);
                    }
                }

                // Sắp xếp theo điểm trending (kết hợp viewCount, likeCount và thời gian)
                Collections.sort(videos, new Comparator<ShortVideo>() {
                    @Override
                    public int compare(ShortVideo v1, ShortVideo v2) {
                        // Tính điểm trending: viewCount + likeCount * 2 + bonus cho video mới
                        long now = System.currentTimeMillis();
                        long dayInMillis = 24 * 60 * 60 * 1000;

                        int score1 = v1.getViewCount() + v1.getLikeCount() * 2;
                        int score2 = v2.getViewCount() + v2.getLikeCount() * 2;

                        // Bonus cho video được đăng trong 7 ngày gần đây
                        if (now - v1.getUploadDate() < 7 * dayInMillis) {
                            score1 += 50;
                        }
                        if (now - v2.getUploadDate() < 7 * dayInMillis) {
                            score2 += 50;
                        }

                        return Integer.compare(score2, score1); // Giảm dần
                    }
                });

                // Lấy số lượng video theo limit
                if (videos.size() > limit) {
                    videos = videos.subList(0, limit);
                }

                callback.onSuccess(videos);
            }

            @Override
            public void onCancelled(DatabaseError databaseError) {
                callback.onError(databaseError.getMessage());
            }
        });
    }

    @Override
    public void incrementViewCount(String videoId, RepositoryCallback<Void> callback) {
        // Kiểm tra videoId không null để tránh crash
        if (videoId == null || videoId.trim().isEmpty()) {
            callback.onError("Video ID không hợp lệ");
            return;
        }

        DatabaseReference videoRef = videosRef.child(videoId).child("viewCount");

        videoRef.addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                Integer currentCount = dataSnapshot.getValue(Integer.class);
                int newCount = (currentCount != null) ? currentCount + 1 : 1;

                videoRef.setValue(newCount).addOnCompleteListener(task -> {
                    if (task.isSuccessful()) {
                        callback.onSuccess(null);
                    } else {
                        callback.onError("Không thể cập nhật view count");
                    }
                });
            }

            @Override
            public void onCancelled(DatabaseError databaseError) {
                callback.onError(databaseError.getMessage());
            }
        });
    }

    @Override
    public void updateLikeCount(String videoId, boolean isLiked, RepositoryCallback<Void> callback) {
        DatabaseReference videoRef = videosRef.child(videoId).child("likeCount");

        videoRef.addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                Integer currentCount = dataSnapshot.getValue(Integer.class);
                int currentLikes = (currentCount != null) ? currentCount : 0;
                int newCount = isLiked ? currentLikes + 1 : Math.max(0, currentLikes - 1);

                videoRef.setValue(newCount).addOnCompleteListener(task -> {
                    if (task.isSuccessful()) {
                        callback.onSuccess(null);
                    } else {
                        callback.onError("Không thể cập nhật like count");
                    }
                });
            }

            @Override
            public void onCancelled(DatabaseError databaseError) {
                callback.onError(databaseError.getMessage());
            }
        });
    }

    @Override
    public void getUserPreferences(String userId, RepositoryCallback<Map<String, Float>> callback) {
        Map<String, Float> combinedPreferences = new HashMap<>();

        // Lấy preferences từ /users/{userId}/preferences
        usersRef.child(userId).child("preferences").addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot preferencesSnapshot) {
                // Xử lý preferences
                if (preferencesSnapshot.exists()) {
                    for (DataSnapshot child : preferencesSnapshot.getChildren()) {
                        String tag = child.getKey();
                        Object value = child.getValue();

                        if (value instanceof Number) {
                            combinedPreferences.put(tag, ((Number) value).floatValue());
                        }
                    }
                }

                // Tiếp tục lấy user_topics
                getUserTopicsAsPreferences(userId, combinedPreferences, callback);
            }

            @Override
            public void onCancelled(DatabaseError databaseError) {
                // Nếu lỗi khi lấy preferences, vẫn thử lấy user_topics
                getUserTopicsAsPreferences(userId, combinedPreferences, callback);
            }
        });
    }

    /**
     * Lấy user topics và chuyển đổi thành preferences
     */
    private void getUserTopicsAsPreferences(String userId, Map<String, Float> existingPreferences,
                                          RepositoryCallback<Map<String, Float>> callback) {
        userTopicsRef.child(userId).addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot topicsSnapshot) {
                if (topicsSnapshot.exists()) {
                    for (DataSnapshot child : topicsSnapshot.getChildren()) {
                        String topic = child.getKey();
                        String mappedTag = mapTopicToTag(topic);

                        if (mappedTag != null) {
                            // Cho điểm thấp hơn preferences trực tiếp (0.5)
                            existingPreferences.put(mappedTag,
                                existingPreferences.getOrDefault(mappedTag, 0.0f) + 0.5f);
                        }
                    }
                }

                callback.onSuccess(existingPreferences);
            }

            @Override
            public void onCancelled(DatabaseError databaseError) {
                callback.onSuccess(existingPreferences);
            }
        });
    }

    /**
     * Ánh xạ topic từ AI chat thành tag video
     */
    private String mapTopicToTag(String topic) {
        if (topic == null) return null;

        String lowerTopic = topic.toLowerCase();

        // Ánh xạ các chủ đề thành tag tương ứng
        if (lowerTopic.contains("giảm cân") || lowerTopic.contains("giamcan")) {
            return "giamcan";
        } else if (lowerTopic.contains("tập luyện") || lowerTopic.contains("tapluyen")) {
            return "tapluyen";
        } else if (lowerTopic.contains("dinh dưỡng") || lowerTopic.contains("dinhduong")) {
            return "dinhduong";
        } else if (lowerTopic.contains("tim mạch") || lowerTopic.contains("timmach")) {
            return "timmach";
        } else if (lowerTopic.contains("yoga")) {
            return "yoga";
        } else if (lowerTopic.contains("sức khỏe") || lowerTopic.contains("suckhoe")) {
            return "suckhoe";
        }

        return null;
    }

    /**
     * Lấy video dựa trên sở thích người dùng
     */
    private void getVideosBasedOnPreferences(Map<String, Float> userPreferences, int limit,
                                           RepositoryCallback<List<ShortVideo>> callback) {
        videosRef.orderByChild("uploadDate").limitToLast(limit * 3)
                .addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                List<ShortVideo> allVideos = new ArrayList<>();
                for (DataSnapshot snapshot : dataSnapshot.getChildren()) {
                    ShortVideo video = snapshot.getValue(ShortVideo.class);
                    if (video != null) {
                        // Tự động set ID từ Firebase key
                        video.setId(snapshot.getKey());
                        allVideos.add(video);
                    }
                }

                // Tính điểm cho mỗi video dựa trên sở thích người dùng
                List<VideoWithScore> videosWithScore = new ArrayList<>();
                for (ShortVideo video : allVideos) {
                    float score = calculateVideoScore(video, userPreferences);
                    videosWithScore.add(new VideoWithScore(video, score));
                }

                // Sắp xếp theo điểm giảm dần
                Collections.sort(videosWithScore, (v1, v2) -> Float.compare(v2.score, v1.score));

                // Chuyển về List<ShortVideo> và giới hạn số lượng
                List<ShortVideo> recommendedVideos = new ArrayList<>();
                for (int i = 0; i < Math.min(limit, videosWithScore.size()); i++) {
                    recommendedVideos.add(videosWithScore.get(i).video);
                }

                callback.onSuccess(recommendedVideos);
            }

            @Override
            public void onCancelled(DatabaseError databaseError) {
                callback.onError(databaseError.getMessage());
            }
        });
    }

    /**
     * Tính điểm cho video dựa trên sở thích người dùng
     */
    private float calculateVideoScore(ShortVideo video, Map<String, Float> userPreferences) {
        float score = 0.0f;

        // Điểm từ tags khớp với sở thích
        if (video.getTags() != null) {
            for (String tag : video.getTags().keySet()) {
                if (Boolean.TRUE.equals(video.getTags().get(tag)) && userPreferences.containsKey(tag)) {
                    score += userPreferences.get(tag) * 10; // Nhân 10 để tăng trọng số
                }
            }
        }

        // Bonus cho video mới
        long daysSinceUpload = (System.currentTimeMillis() - video.getUploadDate()) / (24 * 60 * 60 * 1000);
        if (daysSinceUpload <= 7) {
            score += 5.0f; // Bonus cho video mới trong 7 ngày
        }

        // Bonus từ engagement (view và like)
        score += video.getViewCount() * 0.01f + video.getLikeCount() * 0.1f;

        return score;
    }

    /**
     * Class helper để lưu video kèm điểm số
     */
    private static class VideoWithScore {
        final ShortVideo video;
        final float score;

        VideoWithScore(ShortVideo video, float score) {
            this.video = video;
            this.score = score;
        }
    }
}

package com.vhn.doan.data.repository;

import com.vhn.doan.data.Video;
import java.util.List;
import java.util.Map;

public interface VideoRepository {

    /**
     * Lấy danh sách video được đề xuất cho người dùng dựa trên preferences và topics
     */
    void getRecommendedVideos(String userId, RepositoryCallback<List<Video>> callback);

    /**
     * Lấy video trending cho người dùng mới
     */
    void getTrendingVideos(String region, RepositoryCallback<List<Video>> callback);

    /**
     * Lấy danh sách video theo category
     */
    void getVideosByCategory(String categoryId, RepositoryCallback<List<Video>> callback);

    /**
     * Lấy danh sách video theo tags
     */
    void getVideosByTags(List<String> tags, RepositoryCallback<List<Video>> callback);

    /**
     * Lấy chi tiết một video
     */
    void getVideoById(String videoId, RepositoryCallback<Video> callback);

    /**
     * Tăng số lượt xem cho video
     */
    void incrementViewCount(String videoId, RepositoryCallback<Void> callback);

    /**
     * Thích/bỏ thích video
     */
    void toggleLikeVideo(String videoId, String userId, boolean isLiked, RepositoryCallback<Void> callback);

    /**
     * Kiểm tra người dùng đã thích video chưa
     */
    void isVideoLiked(String videoId, String userId, RepositoryCallback<Boolean> callback);

    /**
     * Lưu lịch sử xem video của người dùng
     */
    void saveVideoView(String videoId, String userId, RepositoryCallback<Void> callback);

    /**
     * Lấy user preferences để đề xuất video
     */
    void getUserPreferences(String userId, RepositoryCallback<Map<String, Double>> callback);

    /**
     * Lấy user topics từ AI interactions để đề xuất video
     */
    void getUserTopics(String userId, RepositoryCallback<Map<String, Long>> callback);

    /**
     * Cập nhật user preferences dựa trên hành vi xem video
     */
    void updateUserPreferences(String userId, String tag, double score, RepositoryCallback<Void> callback);
}

package com.vhn.doan.data.repository;

import com.google.firebase.database.DataSnapshot;
import com.google.firebase.database.DatabaseError;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.database.Query;
import com.google.firebase.database.ValueEventListener;
import com.vhn.doan.data.Video;

import java.util.ArrayList;
import java.util.Collections;
import java.util.Comparator;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class VideoRepositoryImpl implements VideoRepository {

    private DatabaseReference databaseReference;

    public VideoRepositoryImpl() {
        this.databaseReference = FirebaseDatabase.getInstance().getReference();
    }

    @Override
    public void getRecommendedVideos(String userId, RepositoryCallback<List<Video>> callback) {
        // Lấy preferences và topics của user để đề xuất video
        getUserPreferences(userId, new RepositoryCallback<Map<String, Double>>() {
            @Override
            public void onSuccess(Map<String, Double> preferences) {
                getUserTopics(userId, new RepositoryCallback<Map<String, Long>>() {
                    @Override
                    public void onSuccess(Map<String, Long> topics) {
                        // Kết hợp preferences và topics để tạo danh sách tags ưu tiên
                        Map<String, Double> combinedTags = combinePreferencesAndTopics(preferences, topics);

                        // Lấy tất cả video và lọc theo tags ưu tiên
                        getAllVideos(new RepositoryCallback<List<Video>>() {
                            @Override
                            public void onSuccess(List<Video> allVideos) {
                                List<Video> recommendedVideos = filterAndSortByRecommendation(allVideos, combinedTags);
                                callback.onSuccess(recommendedVideos);
                            }

                            @Override
                            public void onFailure(String error) {
                                // Nếu lỗi, fallback về trending videos
                                getTrendingVideos("vietnam", callback);
                            }
                        });
                    }

                    @Override
                    public void onFailure(String error) {
                        // Nếu không có topics, chỉ dùng preferences
                        getAllVideos(new RepositoryCallback<List<Video>>() {
                            @Override
                            public void onSuccess(List<Video> allVideos) {
                                List<Video> recommendedVideos = filterAndSortByRecommendation(allVideos, preferences);
                                callback.onSuccess(recommendedVideos);
                            }

                            @Override
                            public void onFailure(String error) {
                                getTrendingVideos("vietnam", callback);
                            }
                        });
                    }
                });
            }

            @Override
            public void onFailure(String error) {
                // Nếu người dùng mới, hiển thị trending videos
                getTrendingVideos("vietnam", callback);
            }
        });
    }

    @Override
    public void getTrendingVideos(String region, RepositoryCallback<List<Video>> callback) {
        databaseReference.child("trendingVideos").child(region)
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    List<String> trendingVideoIds = new ArrayList<>();
                    for (DataSnapshot child : snapshot.getChildren()) {
                        trendingVideoIds.add(child.getKey());
                    }

                    if (trendingVideoIds.isEmpty()) {
                        // Fallback: lấy video có viewCount cao nhất
                        getTopViewedVideos(callback);
                    } else {
                        getVideosByIds(trendingVideoIds, callback);
                    }
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    getTopViewedVideos(callback);
                }
            });
    }

    @Override
    public void getVideosByCategory(String categoryId, RepositoryCallback<List<Video>> callback) {
        databaseReference.child("videos")
            .orderByChild("categoryId")
            .equalTo(categoryId)
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    List<Video> videos = new ArrayList<>();
                    for (DataSnapshot child : snapshot.getChildren()) {
                        Video video = child.getValue(Video.class);
                        if (video != null) {
                            videos.add(video);
                        }
                    }

                    // Sắp xếp theo ngày upload mới nhất
                    Collections.sort(videos, (v1, v2) -> Long.compare(v2.getUploadDate(), v1.getUploadDate()));
                    callback.onSuccess(videos);
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onFailure(error.getMessage());
                }
            });
    }

    @Override
    public void getVideosByTags(List<String> tags, RepositoryCallback<List<Video>> callback) {
        getAllVideos(new RepositoryCallback<List<Video>>() {
            @Override
            public void onSuccess(List<Video> allVideos) {
                List<Video> filteredVideos = new ArrayList<>();

                for (Video video : allVideos) {
                    if (video.getTags() != null) {
                        boolean hasMatchingTag = false;
                        for (String tag : tags) {
                            if (video.getTags().containsKey(tag) && video.getTags().get(tag)) {
                                hasMatchingTag = true;
                                break;
                            }
                        }
                        if (hasMatchingTag) {
                            filteredVideos.add(video);
                        }
                    }
                }

                // Sắp xếp theo likeCount và viewCount
                Collections.sort(filteredVideos, (v1, v2) -> {
                    int likeDiff = v2.getLikeCount() - v1.getLikeCount();
                    if (likeDiff == 0) {
                        return v2.getViewCount() - v1.getViewCount();
                    }
                    return likeDiff;
                });

                callback.onSuccess(filteredVideos);
            }

            @Override
            public void onFailure(String error) {
                callback.onFailure(error);
            }
        });
    }

    @Override
    public void getVideoById(String videoId, RepositoryCallback<Video> callback) {
        databaseReference.child("videos").child(videoId)
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    Video video = snapshot.getValue(Video.class);
                    if (video != null) {
                        callback.onSuccess(video);
                    } else {
                        callback.onFailure("Video not found");
                    }
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onFailure(error.getMessage());
                }
            });
    }

    @Override
    public void incrementViewCount(String videoId, RepositoryCallback<Void> callback) {
        DatabaseReference videoRef = databaseReference.child("videos").child(videoId).child("viewCount");
        videoRef.addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot snapshot) {
                int currentCount = snapshot.getValue(Integer.class) != null ? snapshot.getValue(Integer.class) : 0;
                videoRef.setValue(currentCount + 1)
                    .addOnSuccessListener(aVoid -> callback.onSuccess(null))
                    .addOnFailureListener(e -> callback.onFailure(e.getMessage()));
            }

            @Override
            public void onCancelled(DatabaseError error) {
                callback.onFailure(error.getMessage());
            }
        });
    }

    @Override
    public void toggleLikeVideo(String videoId, String userId, boolean isLiked, RepositoryCallback<Void> callback) {
        DatabaseReference userLikesRef = databaseReference.child("user_likes").child(userId).child(videoId);
        DatabaseReference videoLikeCountRef = databaseReference.child("videos").child(videoId).child("likeCount");

        if (isLiked) {
            // Thêm like
            userLikesRef.setValue(true);
            videoLikeCountRef.addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    int currentCount = snapshot.getValue(Integer.class) != null ? snapshot.getValue(Integer.class) : 0;
                    videoLikeCountRef.setValue(currentCount + 1)
                        .addOnSuccessListener(aVoid -> callback.onSuccess(null))
                        .addOnFailureListener(e -> callback.onFailure(e.getMessage()));
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onFailure(error.getMessage());
                }
            });
        } else {
            // Bỏ like
            userLikesRef.removeValue();
            videoLikeCountRef.addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    int currentCount = snapshot.getValue(Integer.class) != null ? snapshot.getValue(Integer.class) : 0;
                    videoLikeCountRef.setValue(Math.max(0, currentCount - 1))
                        .addOnSuccessListener(aVoid -> callback.onSuccess(null))
                        .addOnFailureListener(e -> callback.onFailure(e.getMessage()));
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onFailure(error.getMessage());
                }
            });
        }
    }

    @Override
    public void isVideoLiked(String videoId, String userId, RepositoryCallback<Boolean> callback) {
        databaseReference.child("user_likes").child(userId).child(videoId)
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    boolean isLiked = snapshot.exists() && snapshot.getValue(Boolean.class) != null
                        && snapshot.getValue(Boolean.class);
                    callback.onSuccess(isLiked);
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onFailure(error.getMessage());
                }
            });
    }

    @Override
    public void saveVideoView(String videoId, String userId, RepositoryCallback<Void> callback) {
        long timestamp = System.currentTimeMillis();
        databaseReference.child("user_views").child(userId).child(videoId).setValue(timestamp)
            .addOnSuccessListener(aVoid -> callback.onSuccess(null))
            .addOnFailureListener(e -> callback.onFailure(e.getMessage()));
    }

    @Override
    public void getUserPreferences(String userId, RepositoryCallback<Map<String, Double>> callback) {
        databaseReference.child("users").child(userId).child("preferences")
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    Map<String, Double> preferences = new HashMap<>();
                    if (snapshot.exists()) {
                        for (DataSnapshot child : snapshot.getChildren()) {
                            String tag = child.getKey();
                            Double score = child.getValue(Double.class);
                            if (tag != null && score != null) {
                                preferences.put(tag, score);
                            }
                        }
                    }
                    callback.onSuccess(preferences);
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onFailure(error.getMessage());
                }
            });
    }

    @Override
    public void getUserTopics(String userId, RepositoryCallback<Map<String, Long>> callback) {
        databaseReference.child("user_topics").child(userId)
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    Map<String, Long> topics = new HashMap<>();
                    if (snapshot.exists()) {
                        for (DataSnapshot child : snapshot.getChildren()) {
                            String topic = child.getKey();
                            Long timestamp = child.getValue(Long.class);
                            if (topic != null && timestamp != null) {
                                topics.put(topic, timestamp);
                            }
                        }
                    }
                    callback.onSuccess(topics);
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onFailure(error.getMessage());
                }
            });
    }

    @Override
    public void updateUserPreferences(String userId, String tag, double score, RepositoryCallback<Void> callback) {
        databaseReference.child("users").child(userId).child("preferences").child(tag).setValue(score)
            .addOnSuccessListener(aVoid -> callback.onSuccess(null))
            .addOnFailureListener(e -> callback.onFailure(e.getMessage()));
    }

    // Helper methods
    private void getAllVideos(RepositoryCallback<List<Video>> callback) {
        databaseReference.child("videos")
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    List<Video> videos = new ArrayList<>();
                    for (DataSnapshot child : snapshot.getChildren()) {
                        Video video = child.getValue(Video.class);
                        if (video != null) {
                            videos.add(video);
                        }
                    }
                    callback.onSuccess(videos);
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onFailure(error.getMessage());
                }
            });
    }

    private void getTopViewedVideos(RepositoryCallback<List<Video>> callback) {
        databaseReference.child("videos")
            .orderByChild("viewCount")
            .limitToLast(20)
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    List<Video> videos = new ArrayList<>();
                    for (DataSnapshot child : snapshot.getChildren()) {
                        Video video = child.getValue(Video.class);
                        if (video != null) {
                            videos.add(video);
                        }
                    }

                    // Đảo ngược để có viewCount cao nhất trước
                    Collections.reverse(videos);
                    callback.onSuccess(videos);
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onFailure(error.getMessage());
                }
            });
    }

    private void getVideosByIds(List<String> videoIds, RepositoryCallback<List<Video>> callback) {
        List<Video> videos = new ArrayList<>();
        final int[] completedCount = {0};

        if (videoIds.isEmpty()) {
            callback.onSuccess(videos);
            return;
        }

        for (String videoId : videoIds) {
            getVideoById(videoId, new RepositoryCallback<Video>() {
                @Override
                public void onSuccess(Video video) {
                    synchronized (videos) {
                        videos.add(video);
                        completedCount[0]++;
                        if (completedCount[0] == videoIds.size()) {
                            callback.onSuccess(videos);
                        }
                    }
                }

                @Override
                public void onFailure(String error) {
                    synchronized (videos) {
                        completedCount[0]++;
                        if (completedCount[0] == videoIds.size()) {
                            callback.onSuccess(videos);
                        }
                    }
                }
            });
        }
    }

    private Map<String, Double> combinePreferencesAndTopics(Map<String, Double> preferences, Map<String, Long> topics) {
        Map<String, Double> combined = new HashMap<>(preferences);

        // Chuyển đổi topics thành tags và thêm vào combined
        for (Map.Entry<String, Long> entry : topics.entrySet()) {
            String topic = entry.getKey();
            String tag = mapTopicToTag(topic);

            // Tính điểm dựa trên thời gian gần đây (topics mới có điểm cao hơn)
            long currentTime = System.currentTimeMillis();
            long timeDiff = currentTime - entry.getValue();
            double timeScore = Math.max(0.1, 1.0 - (timeDiff / (7 * 24 * 60 * 60 * 1000.0))); // Giảm theo thời gian, tối thiểu 0.1

            combined.put(tag, combined.getOrDefault(tag, 0.0) + timeScore);
        }

        return combined;
    }

    private String mapTopicToTag(String topic) {
        // Ánh xạ các topic từ AI thành tags video tương ứng
        String lowerTopic = topic.toLowerCase();

        if (lowerTopic.contains("giảm cân") || lowerTopic.contains("giamcan")) {
            return "giamcan";
        } else if (lowerTopic.contains("tập luyện") || lowerTopic.contains("tapluyen") || lowerTopic.contains("thể dục")) {
            return "tapluyen";
        } else if (lowerTopic.contains("tim mạch") || lowerTopic.contains("timmach") || lowerTopic.contains("huyết áp")) {
            return "timmach";
        } else if (lowerTopic.contains("dinh dưỡng") || lowerTopic.contains("dinhduong") || lowerTopic.contains("ăn uống")) {
            return "dinhduong";
        } else if (lowerTopic.contains("yoga") || lowerTopic.contains("thiền")) {
            return "yoga";
        } else if (lowerTopic.contains("ngủ") || lowerTopic.contains("giấc ngủ")) {
            return "giacngu";
        } else {
            return "suckhoe"; // Default tag cho các topic khác
        }
    }

    private List<Video> filterAndSortByRecommendation(List<Video> videos, Map<String, Double> userTags) {
        List<VideoScore> videoScores = new ArrayList<>();

        for (Video video : videos) {
            double score = calculateRecommendationScore(video, userTags);
            if (score > 0) {
                videoScores.add(new VideoScore(video, score));
            }
        }

        // Sắp xếp theo điểm recommendation và thời gian upload
        Collections.sort(videoScores, (vs1, vs2) -> {
            int scoreCompare = Double.compare(vs2.score, vs1.score);
            if (scoreCompare == 0) {
                return Long.compare(vs2.video.getUploadDate(), vs1.video.getUploadDate());
            }
            return scoreCompare;
        });

        List<Video> recommendedVideos = new ArrayList<>();
        for (VideoScore vs : videoScores) {
            recommendedVideos.add(vs.video);
        }

        return recommendedVideos;
    }

    private double calculateRecommendationScore(Video video, Map<String, Double> userTags) {
        double score = 0.0;

        if (video.getTags() != null) {
            for (Map.Entry<String, Boolean> tagEntry : video.getTags().entrySet()) {
                if (tagEntry.getValue() && userTags.containsKey(tagEntry.getKey())) {
                    score += userTags.get(tagEntry.getKey());
                }
            }
        }

        // Thêm điểm bonus cho video có viewCount và likeCount cao
        score += Math.log(video.getViewCount() + 1) * 0.1;
        score += Math.log(video.getLikeCount() + 1) * 0.2;

        // Thêm điểm bonus cho video mới
        long daysSinceUpload = (System.currentTimeMillis() - video.getUploadDate()) / (24 * 60 * 60 * 1000);
        if (daysSinceUpload < 7) {
            score += 0.5; // Bonus cho video mới trong 7 ngày
        }

        return score;
    }

    private static class VideoScore {
        Video video;
        double score;

        VideoScore(Video video, double score) {
            this.video = video;
            this.score = score;
        }
    }
}

package com.vhn.doan.data.repository;

