    @Override
    public void incrementViewCount(String videoId, RepositoryCallback<Void> callback) {
        DatabaseReference videoRef = databaseReference.child("videos").child(videoId).child("viewCount");
        videoRef.addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot snapshot) {
                int currentCount = snapshot.getValue(Integer.class) != null ? snapshot.getValue(Integer.class) : 0;
                videoRef.setValue(currentCount + 1)
                    .addOnSuccessListener(aVoid -> callback.onSuccess(null))
                    .addOnFailureListener(e -> callback.onError(e.getMessage()));
            }

            @Override
            public void onCancelled(DatabaseError error) {
                callback.onError(error.getMessage());
            }
        });
    }

    @Override
    public void toggleLikeVideo(String videoId, String userId, boolean isLiked, RepositoryCallback<Void> callback) {
        DatabaseReference userLikesRef = databaseReference.child("user_likes").child(userId).child(videoId);
        DatabaseReference videoLikeCountRef = databaseReference.child("videos").child(videoId).child("likeCount");

        if (isLiked) {
            // Thêm like
            userLikesRef.setValue(true);
            videoLikeCountRef.addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    int currentCount = snapshot.getValue(Integer.class) != null ? snapshot.getValue(Integer.class) : 0;
                    videoLikeCountRef.setValue(currentCount + 1)
                        .addOnSuccessListener(aVoid -> callback.onSuccess(null))
                        .addOnFailureListener(e -> callback.onError(e.getMessage()));
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onError(error.getMessage());
                }
            });
        } else {
            // Bỏ like
            userLikesRef.removeValue();
            videoLikeCountRef.addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    int currentCount = snapshot.getValue(Integer.class) != null ? snapshot.getValue(Integer.class) : 0;
                    videoLikeCountRef.setValue(Math.max(0, currentCount - 1))
                        .addOnSuccessListener(aVoid -> callback.onSuccess(null))
                        .addOnFailureListener(e -> callback.onError(e.getMessage()));
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onError(error.getMessage());
                }
            });
        }
    }

    @Override
    public void isVideoLiked(String videoId, String userId, RepositoryCallback<Boolean> callback) {
        databaseReference.child("user_likes").child(userId).child(videoId)
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    boolean isLiked = snapshot.exists() && snapshot.getValue(Boolean.class) != null 
                        && snapshot.getValue(Boolean.class);
                    callback.onSuccess(isLiked);
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onError(error.getMessage());
                }
            });
    }

    @Override
    public void saveVideoView(String videoId, String userId, RepositoryCallback<Void> callback) {
        long timestamp = System.currentTimeMillis();
        databaseReference.child("user_views").child(userId).child(videoId).setValue(timestamp)
            .addOnSuccessListener(aVoid -> callback.onSuccess(null))
            .addOnFailureListener(e -> callback.onError(e.getMessage()));
    }

    @Override
    public void getUserPreferences(String userId, RepositoryCallback<Map<String, Double>> callback) {
        databaseReference.child("users").child(userId).child("preferences")
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    Map<String, Double> preferences = new HashMap<>();
                    if (snapshot.exists()) {
                        for (DataSnapshot child : snapshot.getChildren()) {
                            String tag = child.getKey();
                            Double score = child.getValue(Double.class);
                            if (tag != null && score != null) {
                                preferences.put(tag, score);
                            }
                        }
                    }
                    callback.onSuccess(preferences);
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onError(error.getMessage());
                }
            });
    }

    @Override
    public void getUserTopics(String userId, RepositoryCallback<Map<String, Long>> callback) {
        databaseReference.child("user_topics").child(userId)
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    Map<String, Long> topics = new HashMap<>();
                    if (snapshot.exists()) {
                        for (DataSnapshot child : snapshot.getChildren()) {
                            String topic = child.getKey();
                            Long timestamp = child.getValue(Long.class);
                            if (topic != null && timestamp != null) {
                                topics.put(topic, timestamp);
                            }
                        }
                    }
                    callback.onSuccess(topics);
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onError(error.getMessage());
                }
            });
    }

    @Override
    public void updateUserPreferences(String userId, String tag, double score, RepositoryCallback<Void> callback) {
        databaseReference.child("users").child(userId).child("preferences").child(tag).setValue(score)
            .addOnSuccessListener(aVoid -> callback.onSuccess(null))
            .addOnFailureListener(e -> callback.onError(e.getMessage()));
    }

    // Helper methods
    private void getAllVideos(RepositoryCallback<List<Video>> callback) {
        databaseReference.child("videos")
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    List<Video> videos = new ArrayList<>();
                    for (DataSnapshot child : snapshot.getChildren()) {
                        Video video = child.getValue(Video.class);
                        if (video != null) {
                            videos.add(video);
                        }
                    }
                    callback.onSuccess(videos);
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onError(error.getMessage());
                }
            });
    }

    private void getTopViewedVideos(RepositoryCallback<List<Video>> callback) {
        databaseReference.child("videos")
            .orderByChild("viewCount")
            .limitToLast(20)
            .addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot snapshot) {
                    List<Video> videos = new ArrayList<>();
                    for (DataSnapshot child : snapshot.getChildren()) {
                        Video video = child.getValue(Video.class);
                        if (video != null) {
                            videos.add(video);
                        }
                    }

                    // Đảo ngược để có viewCount cao nhất trước
                    Collections.reverse(videos);
                    callback.onSuccess(videos);
                }

                @Override
                public void onCancelled(DatabaseError error) {
                    callback.onError(error.getMessage());
                }
            });
    }

    private void getVideosByIds(List<String> videoIds, RepositoryCallback<List<Video>> callback) {
        List<Video> videos = new ArrayList<>();
        final int[] completedCount = {0};

        if (videoIds.isEmpty()) {
            callback.onSuccess(videos);
            return;
        }

        for (String videoId : videoIds) {
            getVideoById(videoId, new RepositoryCallback<Video>() {
                @Override
                public void onSuccess(Video video) {
                    synchronized (videos) {
                        videos.add(video);
                        completedCount[0]++;
                        if (completedCount[0] == videoIds.size()) {
                            callback.onSuccess(videos);
                        }
                    }
                }

                @Override
                public void onError(String error) {
                    synchronized (videos) {
                        completedCount[0]++;
                        if (completedCount[0] == videoIds.size()) {
                            callback.onSuccess(videos);
                        }
                    }
                }
            });
        }
    }

    private Map<String, Double> combinePreferencesAndTopics(Map<String, Double> preferences, Map<String, Long> topics) {
        Map<String, Double> combined = new HashMap<>(preferences);

        // Chuyển đổi topics thành tags và thêm vào combined
        for (Map.Entry<String, Long> entry : topics.entrySet()) {
            String topic = entry.getKey();
            String tag = mapTopicToTag(topic);

            // Tính điểm dựa trên thời gian gần đây (topics mới có điểm cao hơn)
            long currentTime = System.currentTimeMillis();
            long timeDiff = currentTime - entry.getValue();
            double timeScore = Math.max(0.1, 1.0 - (timeDiff / (7 * 24 * 60 * 60 * 1000.0))); // Giảm theo thời gian, tối thiểu 0.1

            combined.put(tag, combined.getOrDefault(tag, 0.0) + timeScore);
        }

        return combined;
    }

    private String mapTopicToTag(String topic) {
        // Ánh xạ các topic từ AI thành tags video tương ứng
        String lowerTopic = topic.toLowerCase();

        if (lowerTopic.contains("giảm cân") || lowerTopic.contains("giamcan")) {
            return "giamcan";
        } else if (lowerTopic.contains("tập luyện") || lowerTopic.contains("tapluyen") || lowerTopic.contains("thể dục")) {
            return "tapluyen";
        } else if (lowerTopic.contains("tim mạch") || lowerTopic.contains("timmach") || lowerTopic.contains("huyết áp")) {
            return "timmach";
        } else if (lowerTopic.contains("dinh dưỡng") || lowerTopic.contains("dinhduong") || lowerTopic.contains("ăn uống")) {
            return "dinhduong";
        } else if (lowerTopic.contains("yoga") || lowerTopic.contains("thiền")) {
            return "yoga";
        } else if (lowerTopic.contains("ngủ") || lowerTopic.contains("giấc ngủ")) {
            return "giacngu";
        } else {
            return "suckhoe"; // Default tag cho các topic khác
        }
    }

    private List<Video> filterAndSortByRecommendation(List<Video> videos, Map<String, Double> userTags) {
        List<VideoScore> videoScores = new ArrayList<>();

        for (Video video : videos) {
            double score = calculateRecommendationScore(video, userTags);
            if (score > 0) {
                videoScores.add(new VideoScore(video, score));
            }
        }

        // Sắp xếp theo điểm recommendation và thời gian upload
        Collections.sort(videoScores, (vs1, vs2) -> {
            int scoreCompare = Double.compare(vs2.score, vs1.score);
            if (scoreCompare == 0) {
                return Long.compare(vs2.video.getUploadDate(), vs1.video.getUploadDate());
            }
            return scoreCompare;
        });

        List<Video> recommendedVideos = new ArrayList<>();
        for (VideoScore vs : videoScores) {
            recommendedVideos.add(vs.video);
        }

        return recommendedVideos;
    }

    private double calculateRecommendationScore(Video video, Map<String, Double> userTags) {
        double score = 0.0;

        if (video.getTags() != null) {
            for (Map.Entry<String, Boolean> tagEntry : video.getTags().entrySet()) {
                if (tagEntry.getValue() && userTags.containsKey(tagEntry.getKey())) {
                    score += userTags.get(tagEntry.getKey());
                }
            }
        }

        // Thêm điểm bonus cho video có viewCount và likeCount cao
        score += Math.log(video.getViewCount() + 1) * 0.1;
        score += Math.log(video.getLikeCount() + 1) * 0.2;

        // Thêm điểm bonus cho video mới
        long daysSinceUpload = (System.currentTimeMillis() - video.getUploadDate()) / (24 * 60 * 60 * 1000);
        if (daysSinceUpload < 7) {
            score += 0.5; // Bonus cho video mới trong 7 ngày
        }

        return score;
    }

    private static class VideoScore {
        Video video;
        double score;

        VideoScore(Video video, double score) {
            this.video = video;
            this.score = score;
        }
    }
}

package com.vhn.doan.presentation.shortvideo;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.PagerSnapHelper;
import androidx.recyclerview.widget.RecyclerView;
import androidx.swiperefreshlayout.widget.SwipeRefreshLayout;

import com.google.android.material.progressindicator.LinearProgressIndicator;
import com.vhn.doan.R;
import com.vhn.doan.data.ShortVideo;
import com.vhn.doan.data.repository.ShortVideoRepositoryImpl;
import com.vhn.doan.utils.SharedPreferencesHelper;

import java.util.ArrayList;
import java.util.List;

/**
 * Fragment hiển thị danh sách video ngắn theo phong cách TikTok/Facebook Reels
 * Sử dụng RecyclerView với PagerSnapHelper để tạo hiệu ứng vuốt video
 */
public class ShortVideoFragment extends Fragment implements ShortVideoContract.View {

    private RecyclerView recyclerViewVideos;
    private SwipeRefreshLayout swipeRefreshLayout;
    private LinearProgressIndicator progressIndicatorLoading;
    private View viewLoading;
    private View viewEmptyState;

    private ShortVideoAdapter adapter;
    private ShortVideoPresenter presenter;
    private LinearLayoutManager layoutManager;

    private int currentPosition = 0;

    public static ShortVideoFragment newInstance() {
        return new ShortVideoFragment();
    }

    @Override
    public void onCreate(@Nullable Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);

        // Khởi tạo presenter
        SharedPreferencesHelper preferencesHelper = new SharedPreferencesHelper(requireContext());
        presenter = new ShortVideoPresenter(new ShortVideoRepositoryImpl(), preferencesHelper);
        presenter.attachView(this);
    }

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container,
                           @Nullable Bundle savedInstanceState) {
        View view = inflater.inflate(R.layout.fragment_short_video, container, false);
        initViews(view);
        setupRecyclerView();
        setupRefreshLayout();
        return view;
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        presenter.start();
    }

    @Override
    public void onDestroy() {
        super.onDestroy();
        if (presenter != null) {
            presenter.detachView();
        }

        // Dừng tất cả video khi destroy
        if (adapter != null) {
            adapter.pauseAllVideos();
        }
    }

    @Override
    public void onPause() {
        super.onPause();
        // Tạm dừng video hiện tại khi Fragment bị pause
        if (adapter != null) {
            adapter.pauseCurrentVideo();
        }
    }

    @Override
    public void onResume() {
        super.onResume();
        // Tiếp tục phát video hiện tại khi Fragment được resume
        if (adapter != null) {
            adapter.resumeCurrentVideo();
        }
    }

    private void initViews(View view) {
        recyclerViewVideos = view.findViewById(R.id.recyclerViewVideos);
        swipeRefreshLayout = view.findViewById(R.id.swipeRefreshLayout);
        progressIndicatorLoading = view.findViewById(R.id.progressIndicatorLoading);
        viewLoading = view.findViewById(R.id.viewLoading);
        viewEmptyState = view.findViewById(R.id.viewEmptyState);
    }

    private void setupRecyclerView() {
        // Sử dụng LinearLayoutManager theo chiều dọc
        layoutManager = new LinearLayoutManager(getContext(), LinearLayoutManager.VERTICAL, false);
        recyclerViewVideos.setLayoutManager(layoutManager);

        // Khởi tạo adapter
        adapter = new ShortVideoAdapter(new ArrayList<>(), new ShortVideoAdapter.VideoInteractionListener() {
            @Override
            public void onVideoLiked(int position, String videoId, boolean isCurrentlyLiked) {
                presenter.onVideoLiked(position, videoId, isCurrentlyLiked);
            }

            @Override
            public void onVideoShared(int position, String videoId) {
                presenter.onVideoShared(videoId);
            }

            @Override
            public void onVideoCommented(int position, String videoId) {
                // TODO: Implement comment functionality in future
                showSuccess(getString(R.string.comment_feature_coming_soon));
            }

            @Override
            public void onVideoViewed(int position, String videoId) {
                presenter.onVideoViewed(position, videoId);
            }

            @Override
            public void onVideoProfileClicked(int position, String userId) {
                // TODO: Navigate to user profile
                showSuccess(getString(R.string.view_user_profile));
            }
        });

        recyclerViewVideos.setAdapter(adapter);

        // Thêm PagerSnapHelper để tạo hiệu ứng snap giống TikTok
        PagerSnapHelper snapHelper = new PagerSnapHelper();
        snapHelper.attachToRecyclerView(recyclerViewVideos);

        // Lắng nghe sự kiện scroll để auto-play video
        recyclerViewVideos.addOnScrollListener(new RecyclerView.OnScrollListener() {
            @Override
            public void onScrollStateChanged(@NonNull RecyclerView recyclerView, int newState) {
                super.onScrollStateChanged(recyclerView, newState);

                if (newState == RecyclerView.SCROLL_STATE_IDLE) {
                    // Khi scroll dừng lại, xác định video hiện tại và auto-play
                    int newPosition = layoutManager.findFirstCompletelyVisibleItemPosition();
                    if (newPosition != RecyclerView.NO_POSITION && newPosition != currentPosition) {
                        // Pause video cũ
                        adapter.pauseVideoAt(currentPosition);

                        // Play video mới
                        currentPosition = newPosition;
                        adapter.playVideoAt(currentPosition);

                        // Thông báo presenter về việc xem video
                        ShortVideo video = presenter.getVideoAt(currentPosition);
                        if (video != null) {
                            presenter.onVideoViewed(currentPosition, video.getId());
                        }
                    }
                }
            }
        });
    }

    private void setupRefreshLayout() {
        swipeRefreshLayout.setOnRefreshListener(() -> {
            presenter.refreshData();
        });

        // Thiết lập màu sắc cho refresh indicator theo Dark Mode theme
        swipeRefreshLayout.setColorSchemeResources(
            R.color.white,
            R.color.primary_green,
            R.color.accent_orange
        );

        // Thiết lập background color cho refresh indicator
        swipeRefreshLayout.setProgressBackgroundColorSchemeResource(R.color.black);
    }

    // Implement ShortVideoContract.View

    @Override
    public void showVideos(List<ShortVideo> videos) {
        viewEmptyState.setVisibility(View.GONE);
        recyclerViewVideos.setVisibility(View.VISIBLE);

        adapter.updateVideos(videos);

        // Auto-play video đầu tiên
        if (!videos.isEmpty() && currentPosition == 0) {
            recyclerViewVideos.post(() -> {
                adapter.playVideoAt(0);

                // Thông báo presenter về việc xem video đầu tiên
                presenter.onVideoViewed(0, videos.get(0).getId());
            });
        }
    }

    @Override
    public void showLoading() {
        viewLoading.setVisibility(View.VISIBLE);
        recyclerViewVideos.setVisibility(View.GONE);
        viewEmptyState.setVisibility(View.GONE);
        progressIndicatorLoading.setVisibility(View.VISIBLE);
    }

    @Override
    public void hideLoading() {
        viewLoading.setVisibility(View.GONE);
        progressIndicatorLoading.setVisibility(View.GONE);

        // Tắt refresh indicator nếu đang hiển thị
        if (swipeRefreshLayout.isRefreshing()) {
            swipeRefreshLayout.setRefreshing(false);
        }
    }

    @Override
    public void showError(String message) {
        Toast.makeText(getContext(), message, Toast.LENGTH_LONG).show();

        // Hiển thị empty state nếu không có video nào
        if (adapter.getItemCount() == 0) {
            showEmptyState();
        }
    }

    @Override
    public void showEmptyState() {
        recyclerViewVideos.setVisibility(View.GONE);
        viewEmptyState.setVisibility(View.VISIBLE);
    }

    @Override
    public void updateVideoLike(int position, boolean isLiked, int newLikeCount) {
        adapter.updateVideoLike(position, isLiked, newLikeCount);
    }

    @Override
    public void updateVideoView(int position, int newViewCount) {
        adapter.updateVideoView(position, newViewCount);
    }

    @Override
    public void showSuccess(String message) {
        Toast.makeText(getContext(), message, Toast.LENGTH_SHORT).show();
    }

    @Override
    public void refreshVideoList() {
        adapter.notifyDataSetChanged();
    }

    @Override
    public void setPresenter(ShortVideoContract.Presenter presenter) {
        this.presenter = (ShortVideoPresenter) presenter;
    }

    /**
     * Public methods để các Activity/Fragment khác có thể tương tác
     */

    public void loadTrendingVideos() {
        if (presenter != null) {
            presenter.loadTrendingVideos();
        }
    }

    public void loadVideosByCategory(String categoryId) {
        if (presenter != null) {
            presenter.loadVideosByCategory(categoryId);
        }
    }

    public void filterByTag(String tag) {
        if (presenter != null) {
            presenter.filterVideosByTag(tag);
        }
    }
}

