            @Override
            public void onScrollStateChanged(@NonNull RecyclerView recyclerView, int newState) {
                super.onScrollStateChanged(recyclerView, newState);

                if (newState == RecyclerView.SCROLL_STATE_IDLE) {
                    // Khi scroll dừng lại, xác định video hiện tại và auto-play
                    int newPosition = layoutManager.findFirstCompletelyVisibleItemPosition();
                    if (newPosition != RecyclerView.NO_POSITION && newPosition != currentPosition) {
                        // Pause video cũ
                        adapter.pauseVideoAt(currentPosition);

                        // Play video mới
                        currentPosition = newPosition;
                        adapter.playVideoAt(currentPosition);

                        // Thông báo presenter về việc xem video
                        ShortVideo video = presenter.getVideoAt(currentPosition);
                        if (video != null) {
                            presenter.onVideoViewed(currentPosition, video.getId());
                        }
                    }
                }
            }
        });
    }

    private void setupRefreshLayout() {
        swipeRefreshLayout.setOnRefreshListener(() -> {
            presenter.refreshData();
        });

        // Thiết lập màu sắc cho refresh indicator theo Dark Mode theme
        swipeRefreshLayout.setColorSchemeResources(
            R.color.white,
            R.color.primary_green,
            R.color.accent_orange
        );

        // Thiết lập background color cho refresh indicator
        swipeRefreshLayout.setProgressBackgroundColorSchemeResource(R.color.black);
    }

    // Implement ShortVideoContract.View

    @Override
    public void showVideos(List<ShortVideo> videos) {
        viewEmptyState.setVisibility(View.GONE);
        recyclerViewVideos.setVisibility(View.VISIBLE);

        adapter.updateVideos(videos);

        // Auto-play video đầu tiên
        if (!videos.isEmpty() && currentPosition == 0) {
            recyclerViewVideos.post(() -> {
                adapter.playVideoAt(0);

                // Thông báo presenter về việc xem video đầu tiên
                presenter.onVideoViewed(0, videos.get(0).getId());
            });
        }
    }

    @Override
    public void showLoading() {
        // Hiển thị Linear Progress Indicator ở đầu màn hình
        progressIndicatorLoading.setVisibility(View.VISIBLE);

        // Chỉ hiển thị loading overlay nếu chưa có dữ liệu
        if (adapter == null || adapter.getItemCount() == 0) {
            viewLoading.setVisibility(View.VISIBLE);
            recyclerViewVideos.setVisibility(View.GONE);
        }

        viewEmptyState.setVisibility(View.GONE);
    }

    @Override
    public void hideLoading() {
        // Ẩn Linear Progress Indicator
        progressIndicatorLoading.setVisibility(View.GONE);

        // Ẩn loading overlay
        viewLoading.setVisibility(View.GONE);

        // Tắt refresh indicator nếu đang hiển thị
        if (swipeRefreshLayout.isRefreshing()) {
            swipeRefreshLayout.setRefreshing(false);
        }
    }

    @Override
    public void showError(String message) {
        Toast.makeText(getContext(), message, Toast.LENGTH_LONG).show();

        // Hiển thị empty state nếu không có video nào
        if (adapter.getItemCount() == 0) {
            showEmptyState();
        }
    }

    @Override
    public void showEmptyState() {
        recyclerViewVideos.setVisibility(View.GONE);
        viewEmptyState.setVisibility(View.VISIBLE);
    }

    @Override
    public void updateVideoLike(int position, boolean isLiked, int newLikeCount) {
        adapter.updateVideoLike(position, isLiked, newLikeCount);
    }

    @Override
    public void updateVideoView(int position, int newViewCount) {
        adapter.updateVideoView(position, newViewCount);
    }

    @Override
    public void showSuccess(String message) {
        Toast.makeText(getContext(), message, Toast.LENGTH_SHORT).show();
    }

    @Override
    public void refreshVideoList() {
        adapter.notifyDataSetChanged();
    }

    @Override
    public void setPresenter(ShortVideoContract.Presenter presenter) {
        this.presenter = (ShortVideoPresenter) presenter;
    }

    /**
     * Public methods để các Activity/Fragment khác có thể tương tác
     */

    public void loadTrendingVideos() {
        if (presenter != null) {
            presenter.loadTrendingVideos();
        }
    }

    public void loadVideosByCategory(String categoryId) {
        if (presenter != null) {
            presenter.loadVideosByCategory(categoryId);
        }
    }

    public void filterByTag(String tag) {
        if (presenter != null) {
            presenter.filterVideosByTag(tag);
        }
    }
}

package com.vhn.doan.presentation.video;

import android.content.Context;
import android.media.MediaMetadataRetriever;
import android.net.Uri;
import android.text.format.DateUtils;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.LinearLayout;
import android.widget.ProgressBar;
import android.widget.TextView;
import android.widget.VideoView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.bumptech.glide.Glide;
import com.bumptech.glide.load.engine.DiskCacheStrategy;
import com.google.android.flexbox.FlexboxLayout;
import com.vhn.doan.R;
import com.vhn.doan.data.Video;
import com.vhn.doan.utils.FormatUtils;

import java.text.NumberFormat;
import java.util.ArrayList;
import java.util.List;
import java.util.Locale;
import java.util.Map;

public class ShortVideoAdapter extends RecyclerView.Adapter<ShortVideoAdapter.VideoViewHolder> {

    private List<Video> videos;
    private VideoInteractionListener listener;
    private int currentPlayingPosition = -1;

    public interface VideoInteractionListener {
        void onVideoLiked(String videoId, boolean isCurrentlyLiked);
        void onVideoShared(String videoId);
        void onVideoClicked(Video video);
        void onVideoViewed(String videoId);
        void onVideoStarted(String videoId);
        void onVideoCompleted(String videoId);
    }

    public ShortVideoAdapter(VideoInteractionListener listener) {
        this.videos = new ArrayList<>();
        this.listener = listener;
    }

    @NonNull
    @Override
    public VideoViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_short_video, parent, false);
        return new VideoViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull VideoViewHolder holder, int position) {
        Video video = videos.get(position);
        holder.bind(video, position);
    }

    @Override
    public int getItemCount() {
        return videos.size();
    }

    public void updateVideos(List<Video> newVideos) {
        this.videos.clear();
        this.videos.addAll(newVideos);
        notifyDataSetChanged();
    }

    public void addMoreVideos(List<Video> moreVideos) {
        int startPosition = videos.size();
        this.videos.addAll(moreVideos);
        notifyItemRangeInserted(startPosition, moreVideos.size());
    }

    public void updateLikeStatus(String videoId, boolean isLiked, int likeCount) {
        for (int i = 0; i < videos.size(); i++) {
            Video video = videos.get(i);
            if (video.getId().equals(videoId)) {
                video.setLikeCount(likeCount);
                notifyItemChanged(i);
                break;
            }
        }
    }

    public void updateViewCount(String videoId, int viewCount) {
        for (int i = 0; i < videos.size(); i++) {
            Video video = videos.get(i);
            if (video.getId().equals(videoId)) {
                video.setViewCount(viewCount);
                notifyItemChanged(i);
                break;
            }
        }
    }

    public void playVideoAtPosition(int position) {
        if (position >= 0 && position < videos.size()) {
            // Pause video hiện tại nếu có
            pauseAllVideos();

            currentPlayingPosition = position;
            notifyItemChanged(position);
        }
    }

    public void pauseAllVideos() {
        currentPlayingPosition = -1;
        notifyDataSetChanged();
    }

    public class VideoViewHolder extends RecyclerView.ViewHolder {
        private VideoView videoView;
        private ImageView ivThumbnail;
        private ImageView ivPlayButton;
        private ProgressBar progressBarVideo;
        private TextView tvTitle;
        private TextView tvCaption;
        private FlexboxLayout layoutTags;
        private TextView tvViewCount;
        private TextView tvUploadDate;
        private LinearLayout layoutLike;
        private ImageView ivLike;
        private TextView tvLikeCount;
        private LinearLayout layoutShare;
        private LinearLayout layoutMore;
        private ProgressBar progressBarVideoProgress;

        private boolean isLiked = false;
        private boolean isVideoLoaded = false;

        public VideoViewHolder(@NonNull View itemView) {
            super(itemView);
            initViews();
            setupClickListeners();
        }

        private void initViews() {
            videoView = itemView.findViewById(R.id.videoView);
            ivThumbnail = itemView.findViewById(R.id.ivThumbnail);
            ivPlayButton = itemView.findViewById(R.id.ivPlayButton);
            progressBarVideo = itemView.findViewById(R.id.progressBarVideo);
            tvTitle = itemView.findViewById(R.id.tvTitle);
            tvCaption = itemView.findViewById(R.id.tvCaption);
            layoutTags = itemView.findViewById(R.id.layoutTags);
            tvViewCount = itemView.findViewById(R.id.tvViewCount);
            tvUploadDate = itemView.findViewById(R.id.tvUploadDate);
            layoutLike = itemView.findViewById(R.id.layoutLike);
            ivLike = itemView.findViewById(R.id.ivLike);
            tvLikeCount = itemView.findViewById(R.id.tvLikeCount);
            layoutShare = itemView.findViewById(R.id.layoutShare);
            layoutMore = itemView.findViewById(R.id.layoutMore);
            progressBarVideoProgress = itemView.findViewById(R.id.progressBarVideoProgress);
        }

        private void setupClickListeners() {
            // Click vào video để play/pause
            videoView.setOnClickListener(v -> {
                if (videoView.isPlaying()) {
                    videoView.pause();
                    ivPlayButton.setVisibility(View.VISIBLE);
                } else {
                    videoView.start();
                    ivPlayButton.setVisibility(View.GONE);
                    if (listener != null) {
                        Video video = videos.get(getAdapterPosition());
                        listener.onVideoStarted(video.getId());
                    }
                }
            });

            // Click vào thumbnail để bắt đầu phát video
            ivThumbnail.setOnClickListener(v -> {
                loadAndPlayVideo();
            });

            ivPlayButton.setOnClickListener(v -> {
                loadAndPlayVideo();
            });

            // Like button
            layoutLike.setOnClickListener(v -> {
                if (listener != null) {
                    Video video = videos.get(getAdapterPosition());
                    listener.onVideoLiked(video.getId(), isLiked);
                }
            });

            // Share button
            layoutShare.setOnClickListener(v -> {
                if (listener != null) {
                    Video video = videos.get(getAdapterPosition());
                    listener.onVideoShared(video.getId());
                }
            });

            // More options
            layoutMore.setOnClickListener(v -> {
                // TODO: Implement more options menu
            });

            // Double tap to like
            itemView.setOnClickListener(v -> {
                if (listener != null) {
                    Video video = videos.get(getAdapterPosition());
                    listener.onVideoClicked(video);
                }
            });
        }

        public void bind(Video video, int position) {
            // Set video info
            tvTitle.setText(video.getTitle());
            tvCaption.setText(video.getCaption());

            // Format view count
            String viewCountText = FormatUtils.formatCount(video.getViewCount()) + " " + 
                    itemView.getContext().getString(R.string.views);
            tvViewCount.setText(viewCountText);

            // Format upload date
            String uploadDateText = FormatUtils.formatTimeAgo(itemView.getContext(), video.getUploadDate());
            tvUploadDate.setText(uploadDateText);

            // Format like count
            tvLikeCount.setText(FormatUtils.formatCount(video.getLikeCount()));

            // Load thumbnail
            if (video.getThumbnailUrl() != null && !video.getThumbnailUrl().isEmpty()) {
                Glide.with(itemView.getContext())
                        .load(video.getThumbnailUrl())
                        .diskCacheStrategy(DiskCacheStrategy.ALL)
                        .placeholder(R.drawable.placeholder_image)
                        .error(R.drawable.error_image)
                        .into(ivThumbnail);
            }

            // Setup tags
            setupTags(video.getTags());

            // Reset video state
            resetVideoState();

            // Auto play if this is the current position
            if (position == currentPlayingPosition) {
                loadAndPlayVideo();
            }

            // Check like status
            // TODO: Implement like status check với presenter
        }

        private void setupTags(Map<String, Boolean> tags) {
            layoutTags.removeAllViews();

            if (tags != null) {
                for (Map.Entry<String, Boolean> entry : tags.entrySet()) {
                    if (entry.getValue()) {
                        TextView tagView = createTagView(entry.getKey());
                        layoutTags.addView(tagView);
                    }
                }
            }
        }

        private TextView createTagView(String tag) {
            TextView tagView = new TextView(itemView.getContext());
            tagView.setText("#" + tag);
            tagView.setTextColor(itemView.getContext().getColor(android.R.color.white));
            tagView.setTextSize(12);
            tagView.setPadding(16, 8, 16, 8);
            tagView.setBackground(itemView.getContext().getDrawable(R.drawable.bg_category_label));

            // Set margin
            FlexboxLayout.LayoutParams params = new FlexboxLayout.LayoutParams(
                    ViewGroup.LayoutParams.WRAP_CONTENT,
                    ViewGroup.LayoutParams.WRAP_CONTENT
            );
            params.setMargins(0, 0, 12, 8);
            tagView.setLayoutParams(params);

            return tagView;
        }

        private void loadAndPlayVideo() {
            if (!isVideoLoaded) {
                Video video = videos.get(getAdapterPosition());

                progressBarVideo.setVisibility(View.VISIBLE);
                ivPlayButton.setVisibility(View.GONE);

                videoView.setVideoURI(Uri.parse(video.getVideoUrl()));

                videoView.setOnPreparedListener(mp -> {
                    progressBarVideo.setVisibility(View.GONE);
                    ivThumbnail.setVisibility(View.GONE);
                    ivPlayButton.setVisibility(View.GONE);

                    isVideoLoaded = true;
                    videoView.start();

                    // Thông báo video đã được xem
                    if (listener != null) {
                        listener.onVideoViewed(video.getId());
                        listener.onVideoStarted(video.getId());
                    }

                    // Set video to loop
                    mp.setLooping(true);
                });

                videoView.setOnErrorListener((mp, what, extra) -> {
                    progressBarVideo.setVisibility(View.GONE);
                    ivPlayButton.setVisibility(View.VISIBLE);
                    // TODO: Show error message
                    return true;
                });

                videoView.setOnCompletionListener(mp -> {
                    if (listener != null) {
                        listener.onVideoCompleted(video.getId());
                    }
                });

            } else {
                // Video đã load, chỉ cần play
                ivThumbnail.setVisibility(View.GONE);
                ivPlayButton.setVisibility(View.GONE);
                videoView.start();

                if (listener != null) {
                    Video video = videos.get(getAdapterPosition());
                    listener.onVideoStarted(video.getId());
                }
            }
        }

        private void resetVideoState() {
            videoView.stopPlayback();
            isVideoLoaded = false;
            ivThumbnail.setVisibility(View.VISIBLE);
            ivPlayButton.setVisibility(View.VISIBLE);
            progressBarVideo.setVisibility(View.GONE);
            progressBarVideoProgress.setVisibility(View.GONE);
        }

        public void updateLikeState(boolean liked) {
            isLiked = liked;
            ivLike.setImageResource(liked ? R.drawable.ic_favorite : R.drawable.ic_favorite_border);
            ivLike.setColorFilter(liked ? 
                    itemView.getContext().getColor(R.color.error_color) : 
                    itemView.getContext().getColor(android.R.color.white));
        }
    }
}

