package com.vhn.doan.presentation.video;

/**
 * Model đại diện cho một video ngắn
 */
public class ShortVideoItem {
    private String id;
    private String title;
    private String description;
    private String videoUrl;
    private String thumbnailUrl;
    private String duration;
    private int viewCount;
    private int likeCount;
    private int commentCount;

    public ShortVideoItem() {
        // Constructor rỗng cho Firebase
    }

    public ShortVideoItem(String id, String title, String description, String videoUrl, 
                         String thumbnailUrl, String duration, int viewCount, 
                         int likeCount, int commentCount) {
        this.id = id;
        this.title = title;
        this.description = description;
        this.videoUrl = videoUrl;
        this.thumbnailUrl = thumbnailUrl;
        this.duration = duration;
        this.viewCount = viewCount;
        this.likeCount = likeCount;
        this.commentCount = commentCount;
    }

    // Getters
    public String getId() { return id; }
    public String getTitle() { return title; }
    public String getDescription() { return description; }
    public String getVideoUrl() { return videoUrl; }
    public String getThumbnailUrl() { return thumbnailUrl; }
    public String getDuration() { return duration; }
    public int getViewCount() { return viewCount; }
    public int getLikeCount() { return likeCount; }
    public int getCommentCount() { return commentCount; }

    // Setters
    public void setId(String id) { this.id = id; }
    public void setTitle(String title) { this.title = title; }
    public void setDescription(String description) { this.description = description; }
    public void setVideoUrl(String videoUrl) { this.videoUrl = videoUrl; }
    public void setThumbnailUrl(String thumbnailUrl) { this.thumbnailUrl = thumbnailUrl; }
    public void setDuration(String duration) { this.duration = duration; }
    public void setViewCount(int viewCount) { this.viewCount = viewCount; }
    public void setLikeCount(int likeCount) { this.likeCount = likeCount; }
    public void setCommentCount(int commentCount) { this.commentCount = commentCount; }
}

interface View {
    void showLoading();
    void hideLoading();
    void showVideos(List<HealthTipVideo> videos);
    void showError(String message);
    void showNoVideos();
    void updateVideoLikeStatus(String videoId, boolean isLiked, int likeCount);
    void showVideoDetails(HealthTipVideo video);
    void showNetworkError();
    void navigateToVideoPlayer(HealthTipVideo video);
    void showSearchResults(List<HealthTipVideo> videos);
    void clearSearchResults();

    // Thêm method cho TikTok-style pagination
    void addMoreVideos(List<HealthTipVideo> newVideos);
    void showLoadingMore();
    void hideLoadingMore();
}

package com.vhn.doan.presentation.videoshort;

import android.content.Intent;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Toast;
import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.viewpager2.widget.ViewPager2;
import com.google.android.material.progressindicator.CircularProgressIndicator;
import com.vhn.doan.R;
import com.vhn.doan.data.HealthTipVideo;
import javax.inject.Inject;
import java.util.List;

public class VideoShortFragment extends Fragment implements VideoShortContract.View {

    private static final String TAG = "VideoShortFragment";

    @Inject
    VideoShortPresenter presenter;

    // Views - TikTok style vertical ViewPager
    private ViewPager2 vpVideos;
    private CircularProgressIndicator progressBar;
    private View layoutErrorState;

    // Adapter - TikTok style vertical video adapter
    private VideoShortVerticalAdapter videoAdapter;

    // State
    private boolean isLoading = false;
    private int currentPosition = 0;

    public static VideoShortFragment newInstance() {
        return new VideoShortFragment();
    }

    @Override
    public void onCreate(@Nullable Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        // Dagger injection sẽ được thực hiện ở đây
    }

    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container,
                            @Nullable Bundle savedInstanceState) {
        return inflater.inflate(R.layout.fragment_video_short_vertical, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        initViews(view);
        setupVerticalViewPager();
        setupPresenter();

        // Load dữ liệu ban đầu
        loadInitialData();
    }

    private void initViews(View view) {
        vpVideos = view.findViewById(R.id.vp_videos);
        progressBar = view.findViewById(R.id.progress_bar);
        layoutErrorState = view.findViewById(R.id.layout_error_state);
    }

    private void setupVerticalViewPager() {
        videoAdapter = new VideoShortVerticalAdapter(requireContext());
        vpVideos.setAdapter(videoAdapter);
        vpVideos.setOrientation(ViewPager2.ORIENTATION_VERTICAL);

        // Thiết lập listeners cho adapter
        videoAdapter.setOnVideoInteractionListener(new VideoShortVerticalAdapter.OnVideoInteractionListener() {
            @Override
            public void onVideoLike(HealthTipVideo video, boolean isLiked) {
                if (presenter != null) {
                    presenter.onVideoLiked(video, isLiked);
                }
            }

            @Override
            public void onVideoShare(HealthTipVideo video) {
                shareVideo(video);
            }

            @Override
            public void onVideoComment(HealthTipVideo video) {
                // TODO: Implement comment functionality
                Toast.makeText(requireContext(), "Chức năng bình luận đang phát triển", Toast.LENGTH_SHORT).show();
            }

            @Override
            public void onVideoView(HealthTipVideo video) {
                if (presenter != null) {
                    presenter.onVideoViewed(video);
                }
            }
        });

        // Thiết lập page change callback để load more và pause/play video
        vpVideos.registerOnPageChangeCallback(new ViewPager2.OnPageChangeCallback() {
            @Override
            public void onPageSelected(int position) {
                super.onPageSelected(position);
                currentPosition = position;

                // Pause video ở vị trí cũ và play video ở vị trí mới
                videoAdapter.pauseAllVideos();
                videoAdapter.playVideoAt(position);

                // Load more videos khi gần hết
                if (position >= videoAdapter.getItemCount() - 3) {
                    loadMoreVideos();
                }
            }

            @Override
            public void onPageScrollStateChanged(int state) {
                super.onPageScrollStateChanged(state);
                // Có thể thêm logic để pause video khi đang scroll
                if (state == ViewPager2.SCROLL_STATE_DRAGGING) {
                    videoAdapter.pauseAllVideos();
                } else if (state == ViewPager2.SCROLL_STATE_IDLE) {
                    videoAdapter.playVideoAt(currentPosition);
                }
            }
        });
    }

    private void setupPresenter() {
        if (presenter != null) {
            presenter.attachView(this);
        }
    }

    private void loadInitialData() {
        if (presenter != null) {
            presenter.loadRecommendedVideos();
        }
    }

    private void loadMoreVideos() {
        if (!isLoading && presenter != null) {
            presenter.loadMoreVideos();
        }
    }

    private void shareVideo(HealthTipVideo video) {
        Intent shareIntent = new Intent(Intent.ACTION_SEND);
        shareIntent.setType("text/plain");
        shareIntent.putExtra(Intent.EXTRA_TEXT,
            "Xem video mẹo sức khỏe hay này: " + video.getTitle() + "\n" + video.getVideoUrl());
        shareIntent.putExtra(Intent.EXTRA_SUBJECT, "Mẹo sức khỏe từ HealthTips");

        Intent chooser = Intent.createChooser(shareIntent, "Chia sẻ video");
        if (shareIntent.resolveActivity(requireContext().getPackageManager()) != null) {
            startActivity(chooser);
        }
    }

    @Override
    public void onResume() {
        super.onResume();
        if (presenter != null) {
            presenter.attachView(this);
        }
        // Resume video hiện tại
        if (videoAdapter != null) {
            videoAdapter.playVideoAt(currentPosition);
        }
    }

    @Override
    public void onPause() {
        super.onPause();
        // Pause tất cả video khi fragment bị pause
        if (videoAdapter != null) {
            videoAdapter.pauseAllVideos();
        }
    }

    @Override
    public void onDestroyView() {
        super.onDestroyView();
        if (presenter != null) {
            presenter.detachView();
        }
        if (videoAdapter != null) {
            videoAdapter.releaseAllPlayers();
        }
    }

    // Implement VideoShortContract.View methods

    @Override
    public void showLoading() {
        if (!isLoading) {
            isLoading = true;
            if (progressBar != null) {
                progressBar.setVisibility(View.VISIBLE);
            }
        }
        hideErrorState();
    }

    @Override
    public void hideLoading() {
        isLoading = false;
        if (progressBar != null) {
            progressBar.setVisibility(View.GONE);
        }
    }

    @Override
    public void showVideos(List<HealthTipVideo> videos) {
        hideErrorState();
        if (videoAdapter != null) {
            videoAdapter.setVideos(videos);
            // Auto play video đầu tiên
            if (!videos.isEmpty()) {
                videoAdapter.playVideoAt(0);
            }
        }
    }

    @Override
    public void showError(String message) {
        hideLoading();
        if (layoutErrorState != null) {
            layoutErrorState.setVisibility(View.VISIBLE);
        }
        Toast.makeText(requireContext(), message, Toast.LENGTH_SHORT).show();
    }

    @Override
    public void showNoVideos() {
        hideLoading();
        Toast.makeText(requireContext(), "Không có video nào để hiển thị", Toast.LENGTH_SHORT).show();
    }

    @Override
    public void updateVideoLikeStatus(String videoId, boolean isLiked, int likeCount) {
        if (videoAdapter != null) {
            videoAdapter.updateVideoLikeStatus(videoId, isLiked, likeCount);
        }
    }

    @Override
    public void showVideoDetails(HealthTipVideo video) {
        // Có thể implement bottom sheet hiển thị chi tiết video
    }

    @Override
    public void showNetworkError() {
        showError(getString(R.string.network_error_message));
    }

    @Override
    public void navigateToVideoPlayer(HealthTipVideo video) {
        // Trong TikTok style, video đã được phát ngay trong fragment
        // Có thể implement fullscreen mode hoặc detail view
    }

    @Override
    public void showSearchResults(List<HealthTipVideo> videos) {
        showVideos(videos);
    }

    @Override
    public void clearSearchResults() {
        loadInitialData();
    }

    // Implement các method mới cho TikTok-style pagination
    @Override
    public void addMoreVideos(List<HealthTipVideo> newVideos) {
        if (videoAdapter != null && newVideos != null && !newVideos.isEmpty()) {
            videoAdapter.addVideos(newVideos);
        }
    }

    @Override
    public void showLoadingMore() {
        // Có thể hiển thị loading indicator nhỏ ở cuối danh sách
        // hoặc để trống vì TikTok style thường không hiển thị loading khi load more
    }

    @Override
    public void hideLoadingMore() {
        // Ẩn loading indicator nếu có
    }

    // Public methods for external access
    public void refreshVideos() {
        if (presenter != null) {
            presenter.refreshVideos();
        }
    }

    public void searchVideos(String query) {
        if (presenter != null) {
            presenter.searchVideos(query);
        }
    }

    // Method để handle double tap like trên video (như TikTok)
    public void handleDoubleTapLike(HealthTipVideo video) {
        if (presenter != null && video != null) {
            presenter.onVideoLiked(video, true);
            // Show heart animation effect
            showLikeAnimation();
        }
    }

    private void showLikeAnimation() {
        // TODO: Implement heart animation effect khi double tap
        // Có thể sử dụng Lottie animation hoặc custom animation
    }

    // Method để pause/resume video khi app vào background
    @Override
    public void onStop() {
        super.onStop();
        if (videoAdapter != null) {
            videoAdapter.pauseAllVideos();
        }
    }

    @Override
    public void onStart() {
        super.onStart();
        if (videoAdapter != null && currentPosition >= 0) {
            videoAdapter.playVideoAt(currentPosition);
        }
    }

    private void hideErrorState() {
        if (layoutErrorState != null) {
            layoutErrorState.setVisibility(View.GONE);
        }
    }
}

@Override
public void loadMoreVideos() {
    // TODO: Implement pagination for loading more videos
    Log.d(TAG, "Load more videos - chưa implement");
}

// Phương thức hỗ trợ
private void recordVideoView(HealthTipVideo video) {
    videoRepository.recordVideoView(video.getId(), new RepositoryCallback<Boolean>() {
        @Override
        public void onSuccess(Boolean success) {
            if (success) {
                // Tăng view count
                videoRepository.incrementViewCount(video.getId(), new RepositoryCallback<Boolean>() {
                    @Override
                    public void onSuccess(Boolean incrementSuccess) {
                        if (incrementSuccess) {
                            video.setViewCount(video.getViewCount() + 1);
                            Log.d(TAG, "Đã tăng view count cho video: " + video.getId());
                        }
                    }

                    @Override
                    public void onError(String error) {
                        Log.e(TAG, "Lỗi khi tăng view count: " + error);
                    }
                });
            }
        }

        @Override
        public void onError(String error) {
            Log.e(TAG, "Lỗi khi ghi nhận lượt xem: " + error);
        }
    });
}

public List<HealthTipVideo> getCurrentVideos() {
    return new ArrayList<>(currentVideos);
}

public boolean isCurrentlyLoading() {
    return isLoading;
}

// Pagination support
private int currentPage = 0;
private static final int PAGE_SIZE = 5;
private boolean hasMoreVideos = true;

@Override
public void loadMoreVideos() {
    if (isLoading || !hasMoreVideos || view == null) return;

    isLoading = true;
    currentPage++;

    // Load videos theo page hiện tại
    loadVideosWithPagination(currentPage, PAGE_SIZE);
}

private void loadVideosWithPagination(int page, int pageSize) {
    // Tạm thời sử dụng recommended videos, có thể customize theo nhu cầu
    videoRepository.getRecommendedVideos(pageSize, new RepositoryCallback<List<HealthTipVideo>>() {
        @Override
        public void onSuccess(List<HealthTipVideo> newVideos) {
            isLoading = false;
            if (view != null) {
                if (newVideos.isEmpty() || newVideos.size() < pageSize) {
                    hasMoreVideos = false;
                }

                if (!newVideos.isEmpty()) {
                    // Filter để tránh duplicate videos
                    List<HealthTipVideo> filteredVideos = filterDuplicateVideos(newVideos);
                    if (!filteredVideos.isEmpty()) {
                        currentVideos.addAll(filteredVideos);
                        addMoreVideosToView(filteredVideos);
                    }
                }
            }
        }

        @Override
        public void onError(String error) {
            isLoading = false;
            currentPage--; // Rollback page nếu lỗi
            Log.e(TAG, "Lỗi khi tải thêm video: " + error);
            if (view != null) {
                view.showError("Không thể tải thêm video. Vui lòng thử lại.");
            }
        }
    });
}

private List<HealthTipVideo> filterDuplicateVideos(List<HealthTipVideo> newVideos) {
    List<HealthTipVideo> filteredVideos = new ArrayList<>();
    for (HealthTipVideo newVideo : newVideos) {
        boolean isDuplicate = false;
        for (HealthTipVideo existingVideo : currentVideos) {
            if (existingVideo.getId().equals(newVideo.getId())) {
                isDuplicate = true;
                break;
            }
        }
        if (!isDuplicate) {
            filteredVideos.add(newVideo);
        }
    }
    return filteredVideos;
}

private void addMoreVideosToView(List<HealthTipVideo> newVideos) {
    // Gọi method mới trong view để add videos thay vì replace
    if (view instanceof VideoShortFragment) {
        VideoShortFragment fragment = (VideoShortFragment) view;
        // Sẽ cần thêm method này vào contract
    }
}

// Reset pagination khi refresh
@Override
public void refreshVideos() {
    currentVideos.clear();
    currentPage = 0;
    hasMoreVideos = true;
    if (currentCategoryId != null) {
        loadVideosByCategory(currentCategoryId);
    } else {
        loadRecommendedVideos();
    }
}

package com.vhn.doan.presentation.videoshort;

import android.content.Context;
import android.net.Uri;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;
import com.bumptech.glide.Glide;
import com.google.android.exoplayer2.ExoPlayer;
import com.google.android.exoplayer2.MediaItem;
import com.google.android.exoplayer2.Player;
import com.google.android.exoplayer2.ui.StyledPlayerView;
import com.vhn.doan.R;
import com.vhn.doan.data.HealthTipVideo;
import java.text.SimpleDateFormat;
import java.util.ArrayList;
import java.util.Date;
import java.util.List;
import java.util.Locale;

public class VideoShortVerticalAdapter extends RecyclerView.Adapter<VideoShortVerticalAdapter.VideoViewHolder> {

    private List<HealthTipVideo> videos;
    private Context context;
    private OnVideoInteractionListener listener;
    private List<ExoPlayer> players; // Quản lý multiple players

    public interface OnVideoInteractionListener {
        void onVideoLike(HealthTipVideo video, boolean isLiked);
        void onVideoShare(HealthTipVideo video);
        void onVideoComment(HealthTipVideo video);
        void onVideoView(HealthTipVideo video);
    }

    public VideoShortVerticalAdapter(Context context) {
        this.context = context;
        this.videos = new ArrayList<>();
        this.players = new ArrayList<>();
    }

    public void setOnVideoInteractionListener(OnVideoInteractionListener listener) {
        this.listener = listener;
    }

    public void setVideos(List<HealthTipVideo> videos) {
        // Release old players
        releaseAllPlayers();

        this.videos.clear();
        this.players.clear();

        if (videos != null) {
            this.videos.addAll(videos);
            // Tạo players cho mỗi video
            for (int i = 0; i < videos.size(); i++) {
                ExoPlayer player = new ExoPlayer.Builder(context).build();
                players.add(player);
            }
        }
        notifyDataSetChanged();
    }

    public void addVideos(List<HealthTipVideo> newVideos) {
        if (newVideos != null && !newVideos.isEmpty()) {
            int startPosition = this.videos.size();
            this.videos.addAll(newVideos);

            // Tạo players cho videos mới
            for (int i = 0; i < newVideos.size(); i++) {
                ExoPlayer player = new ExoPlayer.Builder(context).build();
                players.add(player);
            }

            notifyItemRangeInserted(startPosition, newVideos.size());
        }
    }

    public void updateVideoLikeStatus(String videoId, boolean isLiked, int likeCount) {
        for (int i = 0; i < videos.size(); i++) {
            HealthTipVideo video = videos.get(i);
            if (video.getId().equals(videoId)) {
                video.setLikeCount(likeCount);
                notifyItemChanged(i);
                break;
            }
        }
    }

    public void playVideoAt(int position) {
        if (position >= 0 && position < players.size()) {
            ExoPlayer player = players.get(position);
            if (player != null) {
                player.setPlayWhenReady(true);

                // Gọi callback cho presenter để record view
                if (listener != null && position < videos.size()) {
                    listener.onVideoView(videos.get(position));
                }
            }
        }
    }

    public void pauseAllVideos() {
        for (ExoPlayer player : players) {
            if (player != null) {
                player.setPlayWhenReady(false);
            }
        }
    }

    public void releaseAllPlayers() {
        for (ExoPlayer player : players) {
            if (player != null) {
                player.release();
            }
        }
        players.clear();
    }

    @NonNull
    @Override
    public VideoViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_video_short_vertical, parent, false);
        return new VideoViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull VideoViewHolder holder, int position) {
        HealthTipVideo video = videos.get(position);
        ExoPlayer player = players.get(position);
        holder.bind(video, player, position);
    }

    @Override
    public int getItemCount() {
        return videos.size();
    }

    public class VideoViewHolder extends RecyclerView.ViewHolder {
        private StyledPlayerView playerView;
        private TextView tvTitle;
        private TextView tvDescription;
        private TextView tvAuthor;
        private TextView tvViewCount;
        private TextView tvLikeCount;
        private TextView tvCommentCount;
        private ImageView ivLike;
        private ImageView ivComment;
        private ImageView ivShare;
        private ImageView ivUserAvatar;
        private View btnFollow;

        // State
        private boolean isLiked = false;

        public VideoViewHolder(@NonNull View itemView) {
            super(itemView);
            initViews(itemView);
        }

        private void initViews(View itemView) {
            playerView = itemView.findViewById(R.id.player_view);
            tvTitle = itemView.findViewById(R.id.tv_title);
            tvDescription = itemView.findViewById(R.id.tv_description);
            tvAuthor = itemView.findViewById(R.id.tv_author);
            tvViewCount = itemView.findViewById(R.id.tv_view_count);
            tvLikeCount = itemView.findViewById(R.id.tv_like_count);
            tvCommentCount = itemView.findViewById(R.id.tv_comment_count);
            ivLike = itemView.findViewById(R.id.iv_like);
            ivComment = itemView.findViewById(R.id.iv_comment);
            ivShare = itemView.findViewById(R.id.iv_share);
            ivUserAvatar = itemView.findViewById(R.id.iv_user_avatar);
            btnFollow = itemView.findViewById(R.id.btn_follow);
        }

        public void bind(HealthTipVideo video, ExoPlayer player, int position) {
            // Thiết lập thông tin video
            tvTitle.setText(video.getTitle());
            tvDescription.setText(video.getDescription());
            tvAuthor.setText("@healthtips"); // Có thể lấy từ authorId
            tvViewCount.setText(formatCount(video.getViewCount()) + " lượt xem");
            tvLikeCount.setText(formatCount(video.getLikeCount()));
            tvCommentCount.setText("0"); // TODO: Implement comment count

            // Thiết lập video player
            setupVideoPlayer(video, player);

            // Thiết lập user avatar
            setupUserAvatar();

            // Thiết lập click listeners
            setupClickListeners(video, position);

            // Check like status (tạm thời set false, sẽ được update từ presenter)
            updateLikeButton(false);
        }

        private void setupVideoPlayer(HealthTipVideo video, ExoPlayer player) {
            playerView.setPlayer(player);
            playerView.setUseController(false); // Tắt controls mặc định như TikTok
            playerView.setShowBuffering(StyledPlayerView.SHOW_BUFFERING_WHEN_PLAYING);

            // Thiết lập media item
            if (video.getVideoUrl() != null && !video.getVideoUrl().isEmpty()) {
                MediaItem mediaItem = MediaItem.fromUri(Uri.parse(video.getVideoUrl()));
                player.setMediaItem(mediaItem);
                player.prepare();
                player.setRepeatMode(Player.REPEAT_MODE_ONE); // Loop video như TikTok
            }

            // Double tap để like (như TikTok)
            playerView.setOnClickListener(v -> {
                // Single tap pause/play
                if (player.isPlaying()) {
                    player.setPlayWhenReady(false);
                } else {
                    player.setPlayWhenReady(true);
                }
            });
        }

        private void setupUserAvatar() {
            // Load avatar placeholder
            Glide.with(context)
                    .load(R.drawable.ic_user_avatar_placeholder)
                    .circleCrop()
                    .into(ivUserAvatar);
        }

        private void setupClickListeners(HealthTipVideo video, int position) {
            // Like button
            ivLike.setOnClickListener(v -> {
                isLiked = !isLiked;
                updateLikeButton(isLiked);
                if (listener != null) {
                    listener.onVideoLike(video, isLiked);
                }

                // Animation effect như TikTok
                animateLikeButton();
            });

            // Comment button
            ivComment.setOnClickListener(v -> {
                if (listener != null) {
                    listener.onVideoComment(video);
                }
            });

            // Share button
            ivShare.setOnClickListener(v -> {
                if (listener != null) {
                    listener.onVideoShare(video);
                }
            });

            // Follow button
            btnFollow.setOnClickListener(v -> {
                // TODO: Implement follow functionality
            });

            // User avatar click
            ivUserAvatar.setOnClickListener(v -> {
                // TODO: Navigate to user profile
            });
        }

        private void updateLikeButton(boolean isLiked) {
            if (isLiked) {
                ivLike.setImageResource(R.drawable.ic_heart_filled);
                ivLike.setColorFilter(context.getResources().getColor(R.color.red_like));
            } else {
                ivLike.setImageResource(R.drawable.ic_heart_outline);
                ivLike.setColorFilter(context.getResources().getColor(R.color.white));
            }
        }

        private void animateLikeButton() {
            // Simple scale animation
            ivLike.animate()
                    .scaleX(1.2f)
                    .scaleY(1.2f)
                    .setDuration(100)
                    .withEndAction(() -> {
                        ivLike.animate()
                                .scaleX(1.0f)
                                .scaleY(1.0f)
                                .setDuration(100)
                                .start();
                    })
                    .start();
        }

        private String formatCount(int count) {
            if (count < 1000) {
                return String.valueOf(count);
            } else if (count < 1000000) {
                return String.format(Locale.getDefault(), "%.1fK", count / 1000.0);
            } else {
                return String.format(Locale.getDefault(), "%.1fM", count / 1000000.0);
            }
        }
    }

    public List<HealthTipVideo> getVideos() {
        return new ArrayList<>(videos);
    }

    public HealthTipVideo getVideoAt(int position) {
        if (position >= 0 && position < videos.size()) {
            return videos.get(position);
        }
        return null;
    }
}

package com.vhn.doan.utils;

import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.vhn.doan.data.model.ShortVideoModel;

import java.util.HashMap;
import java.util.Map;

/**
 * Utility class để upload dữ liệu test lên Firebase
 * CHỈ SỬ DỤNG CHO TESTING - XÓA SAU KHI HOÀN THÀNH
 */
public class FirebaseTestDataUploader {

    private static final String FIREBASE_URL = "https://reminderwater-84694-default-rtdb.firebaseio.com/";

    /**
     * Upload dữ liệu video test lên Firebase
     * GỌI METHOD NÀY MỘT LẦN DUY NHẤT để upload dữ liệu test
     */
    public static void uploadTestVideoData() {
        FirebaseDatabase database = FirebaseDatabase.getInstance(FIREBASE_URL);
        DatabaseReference videosRef = database.getReference("videos");

        // Tạo dữ liệu video test
        Map<String, ShortVideoModel> videosData = createTestVideoData();

        // Upload lên Firebase
        videosRef.setValue(videosData)
                .addOnSuccessListener(aVoid -> {
                    android.util.Log.d("FirebaseUploader", "✅ Upload video data thành công!");
                })
                .addOnFailureListener(e -> {
                    android.util.Log.e("FirebaseUploader", "❌ Lỗi upload video data: " + e.getMessage());
                });
    }

    /**
     * Tạo dữ liệu video test giống như trong firebase_test_data.json
     */
    private static Map<String, ShortVideoModel> createTestVideoData() {
        Map<String, ShortVideoModel> videos = new HashMap<>();

        // Video 001
