package com.vhn.doan.utils;

import android.content.Context;
import com.vhn.doan.R;

import java.text.NumberFormat;
import java.util.Locale;

public class FormatUtils {

    /**
     * Format số lượng (view count, like count) thành định dạng ngắn gọn
     * Ví dụ: 1200 -> 1.2K, 1500000 -> 1.5M
     */
    public static String formatCount(int count) {
        if (count < 1000) {
            return String.valueOf(count);
        } else if (count < 1000000) {
            if (count % 1000 == 0) {
                return (count / 1000) + "K";
            } else {
                return String.format(Locale.getDefault(), "%.1fK", count / 1000.0);
            }
        } else if (count < 1000000000) {
            if (count % 1000000 == 0) {
                return (count / 1000000) + "M";
            } else {
                return String.format(Locale.getDefault(), "%.1fM", count / 1000000.0);
            }
        } else {
            if (count % 1000000000 == 0) {
                return (count / 1000000000) + "B";
            } else {
                return String.format(Locale.getDefault(), "%.1fB", count / 1000000000.0);
            }
        }
    }

    /**
     * Format thời gian upload thành chuỗi "x ngày trước", "x giờ trước", v.v
     */
    public static String formatTimeAgo(Context context, long timestamp) {
        long currentTime = System.currentTimeMillis();
        long timeDiff = currentTime - timestamp;

        // Chuyển đổi thành giây
        long seconds = timeDiff / 1000;

        if (seconds < 60) {
            return context.getString(R.string.just_now);
        }

        // Chuyển đổi thành phút
        long minutes = seconds / 60;
        if (minutes < 60) {
            return context.getString(R.string.minutes_ago, (int) minutes);
        }

        // Chuyển đổi thành giờ
        long hours = minutes / 60;
        if (hours < 24) {
            return context.getString(R.string.hours_ago, (int) hours);
        }

        // Chuyển đổi thành ngày
        long days = hours / 24;
        if (days < 30) {
            return context.getString(R.string.days_ago, (int) days);
        }

        // Nếu quá 30 ngày, hiển thị số tháng
        long months = days / 30;
        if (months < 12) {
            return months + " tháng trước";
        }

        // Nếu quá 12 tháng, hiển thị số năm
        long years = months / 12;
        return years + " năm trước";
    }

    /**
     * Format thời lượng video từ milliseconds thành định dạng mm:ss hoặc hh:mm:ss
     */
    public static String formatVideoDuration(long durationMs) {
        long seconds = durationMs / 1000;
        long minutes = seconds / 60;
        long hours = minutes / 60;

        seconds = seconds % 60;
        minutes = minutes % 60;

        if (hours > 0) {
            return String.format(Locale.getDefault(), "%02d:%02d:%02d", hours, minutes, seconds);
        } else {
            return String.format(Locale.getDefault(), "%02d:%02d", minutes, seconds);
        }
    }

    /**
     * Format số với định dạng địa phương (có dấu phẩy phân cách)
     */
    public static String formatNumber(int number) {
        NumberFormat numberFormat = NumberFormat.getNumberInstance(Locale.getDefault());
        return numberFormat.format(number);
    }

    /**
     * Kiểm tra và trả về chuỗi an toàn (không null)
     */
    public static String getSafeString(String input) {
        return input != null ? input : "";
    }

    /**
     * Cắt ngắn chuỗi nếu quá dài
     */
    public static String truncateString(String input, int maxLength) {
        if (input == null) {
            return "";
        }

        if (input.length() <= maxLength) {
            return input;
        }

        return input.substring(0, maxLength - 3) + "...";
    }

    /**
     * Format file size thành định dạng dễ đọc
     */
    public static String formatFileSize(long bytes) {
        if (bytes < 1024) {
            return bytes + " B";
        } else if (bytes < 1024 * 1024) {
            return String.format(Locale.getDefault(), "%.1f KB", bytes / 1024.0);
        } else if (bytes < 1024 * 1024 * 1024) {
            return String.format(Locale.getDefault(), "%.1f MB", bytes / (1024.0 * 1024.0));
        } else {
            return String.format(Locale.getDefault(), "%.1f GB", bytes / (1024.0 * 1024.0 * 1024.0));
        }
    }
}

package com.vhn.doan.utils;

import com.vhn.doan.data.model.ShortVideoModel;
import com.vhn.doan.data.model.UserTopicModel;

import java.util.ArrayList;
import java.util.Collections;
import java.util.Comparator;
import java.util.HashMap;
import java.util.HashSet;
import java.util.List;
import java.util.Map;
import java.util.Set;

/**
 * Utility class để xử lý logic đề xuất video cho người dùng
 * Dựa trên sở thích (preferences), chủ đề quan tâm (topics) và các yếu tố khác
 */
public class VideoRecommendationUtils {

    /**
     * Hàm chính để đề xuất video cho người dùng
     * 
     * @param allVideos Danh sách tất cả video có sẵn
     * @param preferences Map sở thích từ /users/{userId}/preferences (key: topic, value: preference score 0.0-1.0)
     * @param topics Map chủ đề từ /user_topics/{userId} (key: topic name, value: interaction count)
     * @param limit Số lượng video tối đa cần lấy
     * @return Danh sách video được đề xuất, đã sắp xếp theo độ ưu tiên
     */
    public static List<ShortVideoModel> recommendVideos(
            List<ShortVideoModel> allVideos,
            Map<String, Float> preferences,
            Map<String, Long> topics,
            int limit) {

        if (allVideos == null || allVideos.isEmpty() || limit <= 0) {
            return new ArrayList<>();
        }

        // Chuẩn hóa input parameters
        Map<String, Float> userPreferences = preferences != null ? preferences : new HashMap<>();
        Map<String, Long> userTopics = topics != null ? topics : new HashMap<>();

        // Tạo set các tag từ preferences và topics đã chuẩn hóa
        Set<String> userInterestTags = getUserInterestTags(userPreferences, userTopics);

        List<ShortVideoModel> result = new ArrayList<>();
        List<ShortVideoModel> remainingVideos = new ArrayList<>(allVideos);

        // B1: Ưu tiên video có tag trùng với tag trong preferences hoặc topics
        List<ShortVideoModel> matchingVideos = getVideosWithMatchingTags(remainingVideos, userInterestTags);

        // Sắp xếp video có tag trùng theo điểm ưu tiên
        matchingVideos = sortVideosByPriority(matchingVideos, userPreferences, userTopics);

        // Thêm video matching vào kết quả
        int addedCount = Math.min(matchingVideos.size(), limit);
        result.addAll(matchingVideos.subList(0, addedCount));

        // Loại bỏ các video đã thêm khỏi danh sách còn lại
        remainingVideos.removeAll(result);

        // B2: Nếu chưa đủ số lượng, ưu tiên video có viewCount hoặc likeCount cao
        if (result.size() < limit && !remainingVideos.isEmpty()) {
            List<ShortVideoModel> popularVideos = sortVideosByPopularity(remainingVideos);
            int needMore = limit - result.size();
            int addMore = Math.min(popularVideos.size(), needMore);
            result.addAll(popularVideos.subList(0, addMore));

            // Cập nhật danh sách còn lại
            remainingVideos.removeAll(result.subList(result.size() - addMore, result.size()));
        }

        // B3: Nếu không đủ số lượng, bổ sung thêm video cùng categoryId
        if (result.size() < limit && !remainingVideos.isEmpty()) {
            List<ShortVideoModel> sameCategoryVideos = getVideosBySameCategory(remainingVideos, result);
            sameCategoryVideos = sortVideosByPopularity(sameCategoryVideos);

            int needMore = limit - result.size();
            int addMore = Math.min(sameCategoryVideos.size(), needMore);
            result.addAll(sameCategoryVideos.subList(0, addMore));

            // Cập nhật danh sách còn lại
            remainingVideos.removeAll(result.subList(result.size() - addMore, result.size()));
        }

        // B4: Nếu không có sở thích hoặc vẫn chưa đủ, trả về video trending
        if (result.size() < limit && !remainingVideos.isEmpty()) {
            List<ShortVideoModel> trendingVideos = getTrendingVideos(remainingVideos);

            int needMore = limit - result.size();
            int addMore = Math.min(trendingVideos.size(), needMore);
            result.addAll(trendingVideos.subList(0, addMore));
        }

        return result;
    }

    /**
     * Tạo set các tag quan tâm từ preferences và topics
     */
    private static Set<String> getUserInterestTags(Map<String, Float> preferences, Map<String, Long> topics) {
        Set<String> tags = new HashSet<>();

        // Thêm tags từ preferences (chỉ lấy những tag có điểm > 0.3)
        for (Map.Entry<String, Float> entry : preferences.entrySet()) {
            if (entry.getValue() != null && entry.getValue() > 0.3f) {
                String normalizedTag = UserTopicModel.normalizeTopicToTag(entry.getKey());
                if (!normalizedTag.isEmpty()) {
                    tags.add(normalizedTag);
                }
            }
        }

        // Thêm tags từ topics (chỉ lấy những topic có tương tác >= 3 lần)
        for (Map.Entry<String, Long> entry : topics.entrySet()) {
            if (entry.getValue() != null && entry.getValue() >= 3L) {
                String normalizedTag = UserTopicModel.normalizeTopicToTag(entry.getKey());
                if (!normalizedTag.isEmpty()) {
                    tags.add(normalizedTag);
                }
            }
        }

        return tags;
    }

    /**
     * Lấy danh sách video có tag trùng với sở thích người dùng
     */
    private static List<ShortVideoModel> getVideosWithMatchingTags(
            List<ShortVideoModel> videos, Set<String> userTags) {

        List<ShortVideoModel> matchingVideos = new ArrayList<>();

        for (ShortVideoModel video : videos) {
            if (video.getTags() != null && hasMatchingTag(video.getTags(), userTags)) {
                matchingVideos.add(video);
            }
        }

        return matchingVideos;
    }

    /**
     * Kiểm tra xem video có tag trùng với sở thích người dùng không
     */
    private static boolean hasMatchingTag(Map<String, Boolean> videoTags, Set<String> userTags) {
        for (Map.Entry<String, Boolean> entry : videoTags.entrySet()) {
            if (Boolean.TRUE.equals(entry.getValue())) {
                String normalizedTag = UserTopicModel.normalizeTopicToTag(entry.getKey());
                if (userTags.contains(normalizedTag)) {
                    return true;
                }
            }
        }
        return false;
    }

    /**
     * Sắp xếp video theo độ ưu tiên dựa trên preferences và topics
     */
    private static List<ShortVideoModel> sortVideosByPriority(
            List<ShortVideoModel> videos,
            Map<String, Float> preferences,
            Map<String, Long> topics) {

        List<ShortVideoModel> sortedVideos = new ArrayList<>(videos);

        Collections.sort(sortedVideos, new Comparator<ShortVideoModel>() {
            @Override
            public int compare(ShortVideoModel v1, ShortVideoModel v2) {
                // Tính điểm ưu tiên cho từng video
                double score1 = calculatePriorityScore(v1, preferences, topics);
                double score2 = calculatePriorityScore(v2, preferences, topics);

                // Sắp xếp giảm dần theo điểm
                return Double.compare(score2, score1);
            }
        });

        return sortedVideos;
    }

    /**
     * Tính điểm ưu tiên cho video dựa trên preferences và topics
     */
    private static double calculatePriorityScore(
            ShortVideoModel video,
            Map<String, Float> preferences,
            Map<String, Long> topics) {

        double score = 0.0;

        if (video.getTags() != null) {
            for (Map.Entry<String, Boolean> tagEntry : video.getTags().entrySet()) {
                if (Boolean.TRUE.equals(tagEntry.getValue())) {
                    String tag = tagEntry.getKey();
                    String normalizedTag = UserTopicModel.normalizeTopicToTag(tag);

                    // Điểm từ preferences (trọng số 0.7)
                    Float prefScore = preferences.get(tag);
                    if (prefScore != null) {
                        score += prefScore * 0.7;
                    }

                    // Điểm từ topics (trọng số 0.3, chuẩn hóa theo log)
                    for (Map.Entry<String, Long> topicEntry : topics.entrySet()) {
                        String topicNormalized = UserTopicModel.normalizeTopicToTag(topicEntry.getKey());
                        if (normalizedTag.equals(topicNormalized)) {
                            Long count = topicEntry.getValue();
                            if (count != null && count > 0) {
                                // Sử dụng log để giảm ảnh hưởng của số lần tương tác quá lớn
                                double topicScore = Math.log(count + 1) / Math.log(100); // Chuẩn hóa về 0-1
                                score += Math.min(topicScore, 1.0) * 0.3;
                            }
                        }
                    }
                }
            }
        }

        // Thêm điểm bonus nhỏ cho video có popularity cao
        long viewCount = video.getViewCount() != null ? video.getViewCount() : 0L;
        long likeCount = video.getLikeCount() != null ? video.getLikeCount() : 0L;
        double popularityBonus = Math.log(viewCount + likeCount + 1) / Math.log(10000); // Bonus nhỏ
        score += Math.min(popularityBonus, 0.2); // Tối đa 0.2 điểm bonus

        return score;
    }

    /**
     * Sắp xếp video theo độ phổ biến (viewCount + likeCount)
     */
    private static List<ShortVideoModel> sortVideosByPopularity(List<ShortVideoModel> videos) {
        List<ShortVideoModel> sortedVideos = new ArrayList<>(videos);

        Collections.sort(sortedVideos, new Comparator<ShortVideoModel>() {
            @Override
            public int compare(ShortVideoModel v1, ShortVideoModel v2) {
                long popularity1 = getVideoPopularity(v1);
                long popularity2 = getVideoPopularity(v2);
                return Long.compare(popularity2, popularity1); // Sắp xếp giảm dần
            }
        });

        return sortedVideos;
    }

    /**
     * Tính độ phổ biến của video
     */
    private static long getVideoPopularity(ShortVideoModel video) {
        long viewCount = video.getViewCount() != null ? video.getViewCount() : 0L;
        long likeCount = video.getLikeCount() != null ? video.getLikeCount() : 0L;

        // Trọng số: view = 1, like = 3 (like quan trọng hơn view)
        return viewCount + (likeCount * 3);
    }

    /**
     * Lấy video cùng category với các video đã được chọn
     */
    private static List<ShortVideoModel> getVideosBySameCategory(
            List<ShortVideoModel> remainingVideos,
            List<ShortVideoModel> selectedVideos) {

        // Lấy tất cả categoryId từ video đã chọn
        Set<String> selectedCategories = new HashSet<>();
        for (ShortVideoModel video : selectedVideos) {
            if (video.getCategoryId() != null) {
                selectedCategories.add(video.getCategoryId());
            }
        }

        // Lọc video có cùng category
        List<ShortVideoModel> sameCategoryVideos = new ArrayList<>();
        for (ShortVideoModel video : remainingVideos) {
            if (video.getCategoryId() != null && selectedCategories.contains(video.getCategoryId())) {
                sameCategoryVideos.add(video);
            }
        }

        return sameCategoryVideos;
    }

    /**
     * Lấy video trending (mock data - có thể thay thế bằng logic thực từ Firebase)
     */
    private static List<ShortVideoModel> getTrendingVideos(List<ShortVideoModel> videos) {
        // Hiện tại sử dụng logic đơn giản: video có (viewCount + likeCount * 2) cao nhất
        List<ShortVideoModel> trendingVideos = new ArrayList<>(videos);

        Collections.sort(trendingVideos, new Comparator<ShortVideoModel>() {
            @Override
            public int compare(ShortVideoModel v1, ShortVideoModel v2) {
                long trend1 = getTrendingScore(v1);
                long trend2 = getTrendingScore(v2);
                return Long.compare(trend2, trend1); // Sắp xếp giảm dần
            }
        });

        return trendingVideos;
    }

    /**
     * Tính điểm trending cho video
     */
    private static long getTrendingScore(ShortVideoModel video) {
        long viewCount = video.getViewCount() != null ? video.getViewCount() : 0L;
        long likeCount = video.getLikeCount() != null ? video.getLikeCount() : 0L;
        Long uploadDate = video.getUploadDate();

        // Điểm cơ bản
        long baseScore = viewCount + (likeCount * 2);

        // Bonus cho video mới (trong vòng 7 ngày gần đây)
        if (uploadDate != null) {
            long currentTime = System.currentTimeMillis();
            long daysSinceUpload = (currentTime - uploadDate) / (1000 * 60 * 60 * 24);

            if (daysSinceUpload <= 7) {
                // Video mới được bonus
                double recencyBonus = (7 - daysSinceUpload) / 7.0; // Từ 0 đến 1
                baseScore += (long) (baseScore * recencyBonus * 0.3); // Tối đa 30% bonus
            }
        }

        return baseScore;
    }
}

