package com.vhn.doan.data;

import java.util.List;

/**
 * Model cho phản hồi từ OpenRouter API
 */
public class ChatApiResponse {
    private List<Choice> choices;
    private String error;

    public ChatApiResponse() {
    }

    public List<Choice> getChoices() {
        return choices;
    }

    public void setChoices(List<Choice> choices) {
        this.choices = choices;
    }

    public String getError() {
        return error;
    }

    public void setError(String error) {
        this.error = error;
    }

    public static class Choice {
        private Message message;

        public Message getMessage() {
            return message;
        }

        public void setMessage(Message message) {
            this.message = message;
        }
    }

    public static class Message {
        private String role;
        private String content;

        public String getRole() {
            return role;
        }

        public void setRole(String role) {
            this.role = role;
        }

        public String getContent() {
            return content;
        }

        public void setContent(String content) {
            this.content = content;
        }
    }
}

package com.vhn.doan.data;

import java.util.HashMap;
import java.util.Map;

/**
 * Model cho tin nhắn chat giữa người dùng và AI
 */
public class ChatMessage {
    private String id;
    private String userId;
    private String content;
    private boolean isFromUser; // true nếu tin nhắn từ user, false nếu từ AI
    private long timestamp;
    private String topic; // Chủ đề được trích xuất từ nội dung

    public ChatMessage() {
        // Constructor rỗng cho Firebase
    }

    public ChatMessage(String userId, String content, boolean isFromUser, long timestamp) {
        this.userId = userId;
        this.content = content;
        this.isFromUser = isFromUser;
        this.timestamp = timestamp;
    }

    public ChatMessage(String id, String userId, String content, boolean isFromUser, long timestamp, String topic) {
        this.id = id;
        this.userId = userId;
        this.content = content;
        this.isFromUser = isFromUser;
        this.timestamp = timestamp;
        this.topic = topic;
    }

    // Getters
    public String getId() {
        return id;
    }

    public String getUserId() {
        return userId;
    }

    public String getContent() {
        return content;
    }

    public boolean isFromUser() {
        return isFromUser;
    }

    public long getTimestamp() {
        return timestamp;
    }

    public String getTopic() {
        return topic;
    }

    // Setters
    public void setId(String id) {
        this.id = id;
    }

    public void setUserId(String userId) {
        this.userId = userId;
    }

    public void setContent(String content) {
        this.content = content;
    }

    public void setFromUser(boolean fromUser) {
        isFromUser = fromUser;
    }

    public void setTimestamp(long timestamp) {
        this.timestamp = timestamp;
    }

    public void setTopic(String topic) {
        this.topic = topic;
    }

    /**
     * Chuyển đổi object thành Map để lưu vào Firebase
     */
    public Map<String, Object> toMap() {
        Map<String, Object> map = new HashMap<>();
        map.put("userId", userId);
        map.put("content", content);
        map.put("isFromUser", isFromUser);
        map.put("timestamp", timestamp);
        map.put("topic", topic);
        return map;
    }

    @Override
    public String toString() {
        return "ChatMessage{" +
                "id='" + id + '\'' +
                ", userId='" + userId + '\'' +
                ", content='" + content + '\'' +
                ", isFromUser=" + isFromUser +
                ", timestamp=" + timestamp +
                ", topic='" + topic + '\'' +
                '}';
    }
}

package com.vhn.doan.data;

import java.util.HashMap;
import java.util.Map;

/**
 * Model cho tin nhắn chat giữa người dùng và AI
 */
public class ChatMessage {
    private String id;
    private String conversationId; // ID của cuộc trò chuyện chứa tin nhắn này
    private String userId;
    private String content;
    private boolean isFromUser; // true nếu tin nhắn từ user, false nếu từ AI
    private long timestamp;
    private String topic; // Chủ đề được trích xuất từ nội dung

    public ChatMessage() {
        // Constructor rỗng cho Firebase
    }

    public ChatMessage(String conversationId, String userId, String content, boolean isFromUser, long timestamp) {
        this.conversationId = conversationId;
        this.userId = userId;
        this.content = content;
        this.isFromUser = isFromUser;
        this.timestamp = timestamp;
    }

    public ChatMessage(String id, String conversationId, String userId, String content, boolean isFromUser, long timestamp, String topic) {
        this.id = id;
        this.conversationId = conversationId;
        this.userId = userId;
        this.content = content;
        this.isFromUser = isFromUser;
        this.timestamp = timestamp;
        this.topic = topic;
    }

    // Getters
    public String getId() {
        return id;
    }

    public String getConversationId() {
        return conversationId;
    }

    public String getUserId() {
        return userId;
    }

    public String getContent() {
        return content;
    }

    public boolean isFromUser() {
        return isFromUser;
    }

    public long getTimestamp() {
        return timestamp;
    }

    public String getTopic() {
        return topic;
    }

    // Setters
    public void setId(String id) {
        this.id = id;
    }

    public void setConversationId(String conversationId) {
        this.conversationId = conversationId;
    }

    public void setUserId(String userId) {
        this.userId = userId;
    }

    public void setContent(String content) {
        this.content = content;
    }

    public void setFromUser(boolean fromUser) {
        isFromUser = fromUser;
    }

    public void setTimestamp(long timestamp) {
        this.timestamp = timestamp;
    }

    public void setTopic(String topic) {
        this.topic = topic;
    }

    /**
     * Chuyển đổi object thành Map để lưu vào Firebase
     */
    public Map<String, Object> toMap() {
        Map<String, Object> map = new HashMap<>();
        map.put("conversationId", conversationId);
        map.put("userId", userId);
        map.put("content", content);
        map.put("isFromUser", isFromUser);
        map.put("timestamp", timestamp);
        map.put("topic", topic);
        return map;
    }

    @Override
    public String toString() {
        return "ChatMessage{" +
                "id='" + id + '\'' +
                ", conversationId='" + conversationId + '\'' +
                ", userId='" + userId + '\'' +
                ", content='" + content + '\'' +
                ", isFromUser=" + isFromUser +
                ", timestamp=" + timestamp +
                ", topic='" + topic + '\'' +
                '}';
    }
}

package com.vhn.doan.data;

import java.util.HashMap;
import java.util.Map;

/**
 * Model cho một cuộc trò chuyện với AI
 */
public class Conversation {
    private String id;
    private String userId;
    private String title; // Tiêu đề cuộc trò chuyện (tự động tạo từ tin nhắn đầu tiên)
    private String lastMessage; // Tin nhắn cuối cùng trong cuộc trò chuyện
    private long lastMessageTime; // Thời gian tin nhắn cuối cùng
    private boolean isFromUser; // Tin nhắn cuối cùng có phải từ user không
    private int messageCount; // Số lượng tin nhắn trong cuộc trò chuyện
    private long createdTime; // Thời gian tạo cuộc trò chuyện
    private String topic; // Chủ đề chính của cuộc trò chuyện

    public Conversation() {
        // Constructor rỗng cho Firebase
    }

    public Conversation(String userId, String title, long createdTime) {
        this.userId = userId;
        this.title = title;
        this.createdTime = createdTime;
        this.lastMessageTime = createdTime;
        this.messageCount = 0;
    }

    public Conversation(String id, String userId, String title, String lastMessage,
                       long lastMessageTime, boolean isFromUser, int messageCount,
                       long createdTime, String topic) {
        this.id = id;
        this.userId = userId;
        this.title = title;
        this.lastMessage = lastMessage;
        this.lastMessageTime = lastMessageTime;
        this.isFromUser = isFromUser;
        this.messageCount = messageCount;
        this.createdTime = createdTime;
        this.topic = topic;
    }

    // Getters
    public String getId() {
        return id;
    }

    public String getUserId() {
        return userId;
    }

    public String getTitle() {
        return title;
    }

    public String getLastMessage() {
        return lastMessage;
    }

    public long getLastMessageTime() {
        return lastMessageTime;
    }

    public boolean isFromUser() {
        return isFromUser;
    }

    public int getMessageCount() {
        return messageCount;
    }

    public long getCreatedTime() {
        return createdTime;
    }

    public String getTopic() {
        return topic;
    }

    // Setters
    public void setId(String id) {
        this.id = id;
    }

    public void setUserId(String userId) {
        this.userId = userId;
    }

    public void setTitle(String title) {
        this.title = title;
    }

    public void setLastMessage(String lastMessage) {
        this.lastMessage = lastMessage;
    }

    public void setLastMessageTime(long lastMessageTime) {
        this.lastMessageTime = lastMessageTime;
    }

    public void setFromUser(boolean fromUser) {
        isFromUser = fromUser;
    }

    public void setMessageCount(int messageCount) {
        this.messageCount = messageCount;
    }

    public void setCreatedTime(long createdTime) {
        this.createdTime = createdTime;
    }

    public void setTopic(String topic) {
        this.topic = topic;
    }

    /**
     * Chuyển đổi object thành Map để lưu vào Firebase
     */
    public Map<String, Object> toMap() {
        Map<String, Object> map = new HashMap<>();
        map.put("userId", userId);
        map.put("title", title);
        map.put("lastMessage", lastMessage);
        map.put("lastMessageTime", lastMessageTime);
        map.put("isFromUser", isFromUser);
        map.put("messageCount", messageCount);
        map.put("createdTime", createdTime);
        map.put("topic", topic);
        return map;
    }

    /**
     * Tạo tiêu đề tự động từ nội dung tin nhắn đầu tiên
     */
    public static String generateTitle(String firstMessage) {
        if (firstMessage == null || firstMessage.trim().isEmpty()) {
            return "Cuộc trò chuyện mới";
        }

        String trimmed = firstMessage.trim();
        // Lấy tối đa 30 ký tự đầu tiên
        if (trimmed.length() <= 30) {
            return trimmed;
        } else {
            return trimmed.substring(0, 27) + "...";
        }
    }

    @Override
    public String toString() {
        return "Conversation{" +
                "id='" + id + '\'' +
                ", userId='" + userId + '\'' +
                ", title='" + title + '\'' +
                ", lastMessage='" + lastMessage + '\'' +
                ", lastMessageTime=" + lastMessageTime +
                ", isFromUser=" + isFromUser +
                ", messageCount=" + messageCount +
                ", createdTime=" + createdTime +
                ", topic='" + topic + '\'' +
                '}';
    }
}

package com.vhn.doan.data;

import java.util.HashMap;
import java.util.Map;

/**
 * Model cho một cuộc trò chuyện với AI
 */
public class Conversation {
    private String id;
    private String userId;
    private String title; // Tiêu đề cuộc trò chuyện (tự động tạo từ tin nhắn đầu tiên)
    private String lastMessage; // Tin nhắn cuối cùng trong cuộc trò chuyện
    private long lastMessageTime; // Thời gian tin nhắn cuối cùng
    private boolean isFromUser; // Tin nhắn cuối cùng có phải từ user không
    private int messageCount; // Số lượng tin nhắn trong cuộc trò chuyện
    private long createdTime; // Thời gian tạo cuộc trò chuyện
    private String topic; // Chủ đề chính của cuộc trò chuyện
    private boolean isPinned; // Cuộc trò chuyện có được ghim không
    private boolean isMuted; // Cuộc trò chuyện có bị tắt thông báo không
    private boolean isRead; // Cuộc trò chuyện đã được đọc chưa

    public Conversation() {
        // Constructor rỗng cho Firebase
    }

    public Conversation(String userId, String title, long createdTime) {
        this.userId = userId;
        this.title = title;
        this.createdTime = createdTime;
        this.lastMessageTime = createdTime;
        this.messageCount = 0;
    }

    public Conversation(String id, String userId, String title, String lastMessage,
                       long lastMessageTime, boolean isFromUser, int messageCount,
                       long createdTime, String topic, boolean isPinned, boolean isMuted, boolean isRead) {
        this.id = id;
        this.userId = userId;
        this.title = title;
        this.lastMessage = lastMessage;
        this.lastMessageTime = lastMessageTime;
        this.isFromUser = isFromUser;
        this.messageCount = messageCount;
        this.createdTime = createdTime;
        this.topic = topic;
        this.isPinned = isPinned;
        this.isMuted = isMuted;
        this.isRead = isRead;
    }

    // Getters
    public String getId() {
        return id;
    }

    public String getUserId() {
        return userId;
    }

    public String getTitle() {
        return title;
    }

    public String getLastMessage() {
        return lastMessage;
    }

    public long getLastMessageTime() {
        return lastMessageTime;
    }

    public boolean isFromUser() {
        return isFromUser;
    }

    public int getMessageCount() {
        return messageCount;
    }

    public long getCreatedTime() {
        return createdTime;
    }

    public String getTopic() {
        return topic;
    }

    public boolean isPinned() {
        return isPinned;
    }

    public boolean isMuted() {
        return isMuted;
    }

    public boolean isRead() {
        return isRead;
    }

    // Setters
    public void setId(String id) {
        this.id = id;
    }

    public void setUserId(String userId) {
        this.userId = userId;
    }

    public void setTitle(String title) {
        this.title = title;
    }

    public void setLastMessage(String lastMessage) {
        this.lastMessage = lastMessage;
    }

    public void setLastMessageTime(long lastMessageTime) {
        this.lastMessageTime = lastMessageTime;
    }

    public void setFromUser(boolean fromUser) {
        isFromUser = fromUser;
    }

    public void setMessageCount(int messageCount) {
        this.messageCount = messageCount;
    }

    public void setCreatedTime(long createdTime) {
        this.createdTime = createdTime;
    }

    public void setTopic(String topic) {
        this.topic = topic;
    }

    public void setPinned(boolean pinned) {
        isPinned = pinned;
    }

    public void setMuted(boolean muted) {
        isMuted = muted;
    }

    public void setRead(boolean read) {
        isRead = read;
    }

    /**
     * Chuyển đổi object thành Map để lưu vào Firebase
     */
    public Map<String, Object> toMap() {
        Map<String, Object> map = new HashMap<>();
        map.put("userId", userId);
        map.put("title", title);
        map.put("lastMessage", lastMessage);
        map.put("lastMessageTime", lastMessageTime);
        map.put("isFromUser", isFromUser);
        map.put("messageCount", messageCount);
        map.put("createdTime", createdTime);
        map.put("topic", topic);
        map.put("isPinned", isPinned);
        map.put("isMuted", isMuted);
        map.put("isRead", isRead);
        return map;
    }

    /**
     * Tạo tiêu đề tự động từ nội dung tin nhắn đầu tiên
     */
    public static String generateTitle(String firstMessage) {
        if (firstMessage == null || firstMessage.trim().isEmpty()) {
            return "Cuộc trò chuyện mới";
        }

        String trimmed = firstMessage.trim();
        // Lấy tối đa 30 ký tự đầu tiên
        if (trimmed.length() <= 30) {
            return trimmed;
        } else {
            return trimmed.substring(0, 27) + "...";
        }
    }

    @Override
    public String toString() {
        return "Conversation{" +
                "id='" + id + '\'' +
                ", userId='" + userId + '\'' +
                ", title='" + title + '\'' +
                ", lastMessage='" + lastMessage + '\'' +
                ", lastMessageTime=" + lastMessageTime +
                ", isFromUser=" + isFromUser +
                ", messageCount=" + messageCount +
                ", createdTime=" + createdTime +
                ", topic='" + topic + '\'' +
                ", isPinned=" + isPinned +
                ", isMuted=" + isMuted +
                ", isRead=" + isRead +
                '}';
    }
}

package com.vhn.doan.data.repository;

import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.Conversation;

import java.util.List;

/**
 * Repository interface cho chức năng Chat với hỗ trợ multiple conversations
 */
public interface ChatRepository {

    /**
     * Gửi tin nhắn tới AI qua OpenRouter API
     * @param message Nội dung tin nhắn
     * @param callback Callback để nhận kết quả
     */
    void sendMessageToAI(String message, RepositoryCallback<String> callback);

    // ========== CONVERSATION MANAGEMENT ==========

    /**
     * Tạo cuộc trò chuyện mới
     * @param userId ID người dùng
     * @param firstMessage Tin nhắn đầu tiên để tạo tiêu đề
     * @param callback Callback để nhận kết quả
     */
    void createConversation(String userId, String firstMessage, RepositoryCallback<Conversation> callback);

    /**
     * Lấy danh sách cuộc trò chuyện của người dùng (có phân trang)
     * @param userId ID người dùng
     * @param limit Số lượng cuộc trò chuyện cần lấy (tối đa 8 cho lần đầu)
     * @param lastConversationTime Thời gian của cuộc trò chuyện cuối cùng (để phân trang)
     * @param callback Callback để nhận kết quả
     */
    void getConversations(String userId, int limit, Long lastConversationTime, RepositoryCallback<List<Conversation>> callback);

    /**
     * Cập nhật thông tin cuộc trò chuyện (tin nhắn cuối, số lượng tin nhắn)
     * @param conversation Cuộc trò chuyện cần cập nhật
     * @param callback Callback để nhận kết quả
     */
    void updateConversation(Conversation conversation, RepositoryCallback<Conversation> callback);

    /**
     * Xóa cuộc trò chuyện và tất cả tin nhắn trong đó
     * @param conversationId ID cuộc trò chuyện
     * @param callback Callback để nhận kết quả
     */
    void deleteConversation(String conversationId, RepositoryCallback<Boolean> callback);

    // ========== MESSAGE MANAGEMENT ==========

    /**
     * Lưu tin nhắn vào Firebase
     * @param chatMessage Tin nhắn cần lưu
     * @param callback Callback để nhận kết quả
     */
    void saveChatMessage(ChatMessage chatMessage, RepositoryCallback<ChatMessage> callback);

    /**
     * Lấy danh sách tin nhắn của một cuộc trò chuyện cụ thể
     * @param conversationId ID cuộc trò chuyện
     * @param callback Callback để nhận kết quả
     */
    void getChatMessages(String conversationId, RepositoryCallback<List<ChatMessage>> callback);

    /**
     * Xóa tất cả tin nhắn trong một cuộc trò chuyện
     * @param conversationId ID cuộc trò chuyện
     * @param callback Callback để nhận kết quả
     */
    void clearChatMessages(String conversationId, RepositoryCallback<Boolean> callback);

    // ========== UTILITY METHODS ==========

    /**
     * Trích xuất chủ đề từ nội dung tin nhắn
     * @param content Nội dung tin nhắn
     * @return Chủ đề được trích xuất
     */
    String extractTopic(String content);

    /**
     * Kiểm tra xem còn cuộc trò chuyện nào để load không
     * @param userId ID người dùng
     * @param lastConversationTime Thời gian cuộc trò chuyện cuối cùng đã load
     * @param callback Callback để nhận kết quả
     */
    void hasMoreConversations(String userId, Long lastConversationTime, RepositoryCallback<Boolean> callback);
}

package com.vhn.doan.data.repository;

import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.Conversation;

import java.util.List;

/**
 * Repository interface cho chức năng Chat với hỗ trợ multiple conversations
 */
public interface ChatRepository {

    /**
     * Gửi tin nhắn tới AI qua OpenRouter API
     * @param message Nội dung tin nhắn
     * @param callback Callback để nhận kết quả
     */
    void sendMessageToAI(String message, RepositoryCallback<String> callback);

    // ========== CONVERSATION MANAGEMENT ==========

    /**
     * Tạo cuộc trò chuyện mới
     * @param userId ID người dùng
     * @param firstMessage Tin nhắn đầu tiên để tạo tiêu đề
     * @param callback Callback để nhận kết quả
     */
    void createConversation(String userId, String firstMessage, RepositoryCallback<Conversation> callback);

    /**
     * Lấy danh sách cuộc trò chuyện của người dùng (có phân trang)
     * @param userId ID người dùng
     * @param limit Số lượng cuộc trò chuyện cần lấy (tối đa 8 cho lần đầu)
     * @param lastConversationTime Thời gian của cuộc trò chuyện cuối cùng (để phân trang)
     * @param callback Callback để nhận kết quả
     */
    void getConversations(String userId, int limit, Long lastConversationTime, RepositoryCallback<List<Conversation>> callback);

    /**
     * Cập nhật thông tin cuộc trò chuyện (tin nhắn cuối, số lượng tin nhắn)
     * @param conversation Cuộc trò chuyện cần cập nhật
     * @param callback Callback để nhận kết quả
     */
    void updateConversation(Conversation conversation, RepositoryCallback<Conversation> callback);

    /**
     * Xóa cuộc trò chuyện và tất cả tin nhắn trong đó
     * @param conversationId ID cuộc trò chuyện
     * @param callback Callback để nhận kết quả
     */
    void deleteConversation(String conversationId, RepositoryCallback<Boolean> callback);

    // ========== MESSAGE MANAGEMENT ==========

    /**
     * Lưu tin nhắn vào Firebase
     * @param chatMessage Tin nhắn cần lưu
     * @param callback Callback để nhận kết quả
     */
    void saveChatMessage(ChatMessage chatMessage, RepositoryCallback<ChatMessage> callback);

    /**
     * Lấy danh sách tin nhắn của một cuộc trò chuyện cụ thể
     * @param conversationId ID cuộc trò chuyện
     * @param callback Callback để nhận kết quả
     */
    void getChatMessages(String conversationId, RepositoryCallback<List<ChatMessage>> callback);

    /**
     * Xóa tất cả tin nhắn trong một cuộc trò chuyện
     * @param conversationId ID cuộc trò chuyện
     * @param callback Callback để nhận kết quả
     */
    void clearChatMessages(String conversationId, RepositoryCallback<Boolean> callback);

    /**
     * Xóa lịch sử chat của người dùng (tất cả cuộc trò chuyện)
     * @param userId ID người dùng  
     * @param callback Callback để nhận kết quả
     */
    void clearChatHistory(String userId, RepositoryCallback<Boolean> callback);

    // ========== UTILITY METHODS ==========

    /**
     * Trích xuất chủ đề từ nội dung tin nhắn
     * @param content Nội dung tin nhắn
     * @return Chủ đề được trích xuất
     */
    String extractTopic(String content);

    /**
     * Kiểm tra xem còn cuộc trò chuyện nào để load không
     * @param userId ID người dùng
     * @param lastConversationTime Thời gian cuộc trò chuyện cuối cùng đã load
     * @param callback Callback để nhận kết quả
     */
    void hasMoreConversations(String userId, Long lastConversationTime, RepositoryCallback<Boolean> callback);
}

package com.vhn.doan.data.repository;

import android.os.Handler;
import android.os.Looper;
import android.util.Log;

import com.google.firebase.database.DataSnapshot;
import com.google.firebase.database.DatabaseError;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.database.Query;
import com.google.firebase.database.ValueEventListener;
import com.google.gson.Gson;
import com.google.gson.JsonObject;
import com.google.gson.JsonArray;
import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.ChatApiResponse;
import com.vhn.doan.data.Conversation;

import java.io.IOException;
import java.util.ArrayList;
import java.util.Collections;
import java.util.List;
import java.util.concurrent.TimeUnit;

import okhttp3.Call;
import okhttp3.Callback;
import okhttp3.MediaType;
import okhttp3.OkHttpClient;
import okhttp3.Request;
import okhttp3.RequestBody;
import okhttp3.Response;

/**
 * Implementation của ChatRepository với hỗ trợ multiple conversations
 */
public class ChatRepositoryImpl implements ChatRepository {

    private static final String TAG = "ChatRepositoryImpl";
    private static final String OPENROUTER_API_URL = "https://openrouter.ai/api/v1/chat/completions";
    // Sử dụng API key mới
    private static final String API_KEY = "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";

    // Firebase paths
    private static final String CONVERSATIONS_PATH = "conversations";
    private static final String CHAT_MESSAGES_PATH = "chat_messages";
    private static final String USER_TOPICS_PATH = "user_topics";

    // Pagination constants
    private static final int DEFAULT_CONVERSATIONS_LIMIT = 8;
    private static final int LOAD_MORE_CONVERSATIONS_LIMIT = 3;

    private final DatabaseReference database;
    private final OkHttpClient httpClient;
    private final Gson gson;
    private final Handler mainHandler;

    public ChatRepositoryImpl() {
        this.database = FirebaseDatabase.getInstance().getReference();
        this.httpClient = new OkHttpClient.Builder()
                .connectTimeout(30, TimeUnit.SECONDS)
                .readTimeout(30, TimeUnit.SECONDS)
                .writeTimeout(30, TimeUnit.SECONDS)
                .build();
        this.gson = new Gson();
        this.mainHandler = new Handler(Looper.getMainLooper());
    }

    // ========== CONVERSATION MANAGEMENT ==========

    @Override
    public void createConversation(String userId, String firstMessage, RepositoryCallback<Conversation> callback) {
        try {
            long currentTime = System.currentTimeMillis();
            String title = Conversation.generateTitle(firstMessage);
            String topic = extractTopic(firstMessage);

            Conversation conversation = new Conversation(userId, title, currentTime);
            conversation.setTopic(topic);

            String conversationId = database.child(CONVERSATIONS_PATH)
                    .child(userId)
                    .push().getKey();

            if (conversationId != null) {
                conversation.setId(conversationId);

                database.child(CONVERSATIONS_PATH)
                        .child(userId)
                        .child(conversationId)
                        .setValue(conversation.toMap())
                        .addOnSuccessListener(aVoid -> {
                            Log.d(TAG, "Conversation created successfully: " + conversationId);
                            callback.onSuccess(conversation);
                        })
                        .addOnFailureListener(e -> {
                            Log.e(TAG, "Failed to create conversation", e);
                            callback.onError("Không thể tạo cuộc trò chuyện: " + e.getMessage());
                        });
            } else {
                callback.onError("Không thể tạo ID cho cuộc trò chuyện");
            }
        } catch (Exception e) {
            Log.e(TAG, "Error creating conversation", e);
            callback.onError("Có lỗi xảy ra khi tạo cuộc trò chuyện: " + e.getMessage());
        }
    }

    @Override
    public void getConversations(String userId, int limit, Long lastConversationTime, RepositoryCallback<List<Conversation>> callback) {
        try {
            DatabaseReference conversationsRef = database.child(CONVERSATIONS_PATH).child(userId);

            // Sắp xếp theo lastMessageTime giảm dần (mới nhất trước)
            Query query = conversationsRef.orderByChild("lastMessageTime");

            // Nếu có lastConversationTime, query từ thời điểm đó trở về trước
            if (lastConversationTime != null) {
                query = query.endBefore(lastConversationTime);
            }

            // Giới hạn số lượng kết quả
            query = query.limitToLast(limit);

            query.addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot dataSnapshot) {
                    List<Conversation> conversations = new ArrayList<>();

                    for (DataSnapshot conversationSnapshot : dataSnapshot.getChildren()) {
                        try {
                            Conversation conversation = parseConversationFromSnapshot(conversationSnapshot);
                            if (conversation != null) {
                                conversations.add(conversation);
                            }
                        } catch (Exception e) {
                            Log.e(TAG, "Error parsing conversation", e);
                        }
                    }

                    // Sắp xếp lại theo thời gian giảm dần (mới nhất trước)
                    Collections.sort(conversations, (c1, c2) ->
                        Long.compare(c2.getLastMessageTime(), c1.getLastMessageTime()));

                    Log.d(TAG, "Loaded " + conversations.size() + " conversations");
                    callback.onSuccess(conversations);
                }

                @Override
                public void onCancelled(DatabaseError databaseError) {
                    Log.e(TAG, "Failed to load conversations", databaseError.toException());
                    callback.onError("Không thể tải danh sách cuộc trò chuyện: " + databaseError.getMessage());
                }
            });
        } catch (Exception e) {
            Log.e(TAG, "Error loading conversations", e);
            callback.onError("Có lỗi xảy ra khi tải danh sách cuộc trò chuyện: " + e.getMessage());
        }
    }

    @Override
    public void updateConversation(Conversation conversation, RepositoryCallback<Conversation> callback) {
        try {
            if (conversation.getId() == null || conversation.getUserId() == null) {
                callback.onError("Thông tin cuộc trò chuyện không hợp lệ");
                return;
            }

            database.child(CONVERSATIONS_PATH)
                    .child(conversation.getUserId())
                    .child(conversation.getId())
                    .setValue(conversation.toMap())
                    .addOnSuccessListener(aVoid -> {
                        Log.d(TAG, "Conversation updated successfully: " + conversation.getId());
                        callback.onSuccess(conversation);
                    })
                    .addOnFailureListener(e -> {
                        Log.e(TAG, "Failed to update conversation", e);
                        callback.onError("Không thể cập nhật cuộc trò chuyện: " + e.getMessage());
                    });
        } catch (Exception e) {
            Log.e(TAG, "Error updating conversation", e);
            callback.onError("Có lỗi xảy ra khi cập nhật cuộc trò chuyện: " + e.getMessage());
        }
    }

    @Override
    public void deleteConversation(String conversationId, RepositoryCallback<Boolean> callback) {
        // TODO: Implement delete conversation and all its messages
        callback.onError("Chức năng xóa cuộc trò chuyện sẽ được triển khai sau");
    }

    @Override
    public void hasMoreConversations(String userId, Long lastConversationTime, RepositoryCallback<Boolean> callback) {
        try {
            DatabaseReference conversationsRef = database.child(CONVERSATIONS_PATH).child(userId);

            Query query = conversationsRef.orderByChild("lastMessageTime");
            if (lastConversationTime != null) {
                query = query.endBefore(lastConversationTime);
            }
            query = query.limitToLast(1);

            query.addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot dataSnapshot) {
                    boolean hasMore = dataSnapshot.exists() && dataSnapshot.getChildrenCount() > 0;
                    callback.onSuccess(hasMore);
                }

                @Override
                public void onCancelled(DatabaseError databaseError) {
                    Log.e(TAG, "Failed to check more conversations", databaseError.toException());
                    callback.onError("Không thể kiểm tra cuộc trò chuyện: " + databaseError.getMessage());
                }
            });
        } catch (Exception e) {
            Log.e(TAG, "Error checking more conversations", e);
            callback.onError("Có lỗi xảy ra khi kiểm tra cuộc trò chuyện: " + e.getMessage());
        }
    }

    // ========== MESSAGE MANAGEMENT ==========

    @Override
    public void sendMessageToAI(String message, RepositoryCallback<String> callback) {
        try {
            // Tạo JSON request body theo format chính xác của OpenRouter
            JsonObject requestJson = new JsonObject();
            requestJson.addProperty("model", "openai/gpt-3.5-turbo");

            // Thêm các parameters bổ sung theo yêu cầu OpenRouter
            requestJson.addProperty("max_tokens", 1000);
            requestJson.addProperty("temperature", 0.7);

            JsonArray messagesArray = new JsonArray();

            // System message
            JsonObject systemMessage = new JsonObject();
            systemMessage.addProperty("role", "system");
            systemMessage.addProperty("content", "Bạn là trợ lý AI chuyên về sức khỏe. Chỉ trả lời các câu hỏi liên quan đến sức khỏe, y tế, dinh dưỡng, thể dục thể thao. Nếu người dùng hỏi ngoài lĩnh vực sức khỏe thì từ chối một cách lịch sự và đề nghị họ hỏi về sức khỏe.");
            messagesArray.add(systemMessage);

            // User message
            JsonObject userMessage = new JsonObject();
            userMessage.addProperty("role", "user");
            userMessage.addProperty("content", message);
            messagesArray.add(userMessage);

            requestJson.add("messages", messagesArray);

            String jsonString = gson.toJson(requestJson);
            Log.d(TAG, "Request JSON: " + jsonString);

            RequestBody body = RequestBody.create(
                    MediaType.parse("application/json"),
                    jsonString
            );

            Request request = new Request.Builder()
                    .url(OPENROUTER_API_URL)
                    .post(body)
                    .addHeader("Authorization", "Bearer " + API_KEY)
                    .addHeader("Content-Type", "application/json")
                    .addHeader("HTTP-Referer", "https://healthtips-vn.app")
                    .addHeader("X-Title", "HealthTips Vietnam")
                    .build();

            Log.d(TAG, "Sending request to: " + OPENROUTER_API_URL);
            Log.d(TAG, "Authorization header: Bearer " + API_KEY.substring(0, 20) + "...");

            httpClient.newCall(request).enqueue(new Callback() {
                @Override
                public void onFailure(Call call, IOException e) {
                    Log.e(TAG, "Network request failed", e);
                    mainHandler.post(() -> {
                        callback.onError("Không thể kết nối đến máy chủ AI. Kiểm tra kết nối mạng của bạn.");
                    });
                }

                @Override
                public void onResponse(Call call, Response response) throws IOException {
                    String responseBody = "";
                    try {
                        if (response.body() != null) {
                            responseBody = response.body().string();
                        }

                        Log.d(TAG, "Response Code: " + response.code());
                        Log.d(TAG, "Response Headers: " + response.headers().toString());
                        Log.d(TAG, "Response Body: " + responseBody);

                        if (response.isSuccessful()) {
                            ChatApiResponse apiResponse = gson.fromJson(responseBody, ChatApiResponse.class);

                            if (apiResponse != null &&
                                apiResponse.getChoices() != null &&
                                !apiResponse.getChoices().isEmpty() &&
                                apiResponse.getChoices().get(0).getMessage() != null) {

                                String aiMessage = apiResponse.getChoices().get(0).getMessage().getContent();
                                if (aiMessage != null && !aiMessage.trim().isEmpty()) {
                                    mainHandler.post(() -> callback.onSuccess(aiMessage.trim()));
                                } else {
                                    mainHandler.post(() -> callback.onError("AI trả về phản hồi trống."));
                                }
                            } else {
                                mainHandler.post(() -> callback.onError("Định dạng phản hồi từ AI không hợp lệ."));
                            }
                        } else {
                            // Xử lý các lỗi HTTP cụ thể
                            String errorMessage = parseErrorMessage(response.code(), responseBody);
                            Log.e(TAG, "API Error: " + errorMessage);

                            mainHandler.post(() -> callback.onError(errorMessage));
                        }
                    } catch (Exception e) {
                        Log.e(TAG, "Error parsing response", e);
                        Log.e(TAG, "Response body was: " + responseBody);

                        mainHandler.post(() -> {
                            callback.onError("Lỗi xử lý phản hồi từ AI. Vui lòng thử lại.");
                        });
                    }
                }
            });

        } catch (Exception e) {
            Log.e(TAG, "Error creating request", e);
            callback.onError("Lỗi tạo yêu cầu gửi đến AI.");
        }
    }

    /**
     * Phân tích thông báo lỗi từ API response
     */
    private String parseErrorMessage(int responseCode, String responseBody) {
        try {
            // Thử parse JSON error response
            JsonObject errorResponse = gson.fromJson(responseBody, JsonObject.class);
            if (errorResponse.has("error")) {
                JsonObject error = errorResponse.getAsJsonObject("error");
                if (error.has("message")) {
                    String apiErrorMessage = error.get("message").getAsString();
                    Log.d(TAG, "API Error Message: " + apiErrorMessage);

                    // Dịch một số lỗi phổ biến
                    if (apiErrorMessage.contains("No auth credentials")) {
                        return "Lỗi xác thực API. Vui lòng liên hệ quản trị viên.";
                    } else if (apiErrorMessage.contains("Rate limit")) {
                        return "Đã vượt quá giới hạn yêu cầu. Vui lòng thử lại sau ít phút.";
                    } else if (apiErrorMessage.contains("Invalid model")) {
                        return "Mô hình AI không hợp lệ. Vui lòng thử lại.";
                    }
                    return "Lỗi từ AI: " + apiErrorMessage;
                }
            }
        } catch (Exception e) {
            Log.e(TAG, "Error parsing error response", e);
        }

        // Fallback cho các HTTP status codes
        switch (responseCode) {
            case 401:
                return "Lỗi xác thực. API key có thể đã hết hạn hoặc không hợp lệ.";
            case 403:
                return "Không có quyền truy cập. Vui lòng kiểm tra cấu hình API.";
            case 429:
                return "Quá nhiều yêu cầu. Vui lòng chờ một chút rồi thử lại.";
            case 500:
                return "Lỗi máy chủ AI. Vui lòng thử lại sau.";
            case 502:
            case 503:
            case 504:
                return "Máy chủ AI tạm thời không khả dụng. Vui lòng thử lại sau.";
            default:
                return "Lỗi không xác định từ AI (Mã: " + responseCode + "). Vui lòng thử lại.";
        }
    }

    @Override
    public String extractTopic(String content) {
        try {
            // Các từ khóa chủ đề về sức khỏe
            String[] healthTopics = {
                "tim mạch", "huyết áp", "cholesterol", "đường huyết", "tiểu đường",
                "dinh dưỡng", "vitamin", "protein", "carb", "chất béo",
                "tập luyện", "thể dục", "yoga", "cardio", "cơ bắp",
                "giảm cân", "tăng cân", "béo phì", "ăn kiêng",
                "stress", "lo âu", "trầm cảm", "tâm lý", "tinh thần",
                "giấc ngủ", "mất ngủ", "ngủ", "nghỉ ngơi",
                "da", "tóc", "móng", "mỹ phẩm", "chăm sóc da",
                "mang thai", "sinh sản", "kinh nguyệt", "phụ khoa",
                "trẻ em", "em bé", "sức khỏe trẻ", "phát triển",
                "người cao tuổi", "lão hóa", "xương khớp", "cột sống",
                "mắt", "thị lực", "tai", "thính giác",
                "răng", "miệng", "nha khoa", "vệ sinh răng miệng",
                "cảm cúm", "sốt", "ho", "viêm họng", "virus",
                "thuốc", "dược phẩm", "tác dụng phụ", "liều dùng"
            };

            content = content.toLowerCase();

            for (String topic : healthTopics) {
                if (content.contains(topic)) {
                    return topic;
                }
            }

            // Nếu không tìm thấy chủ đề cụ thể, trả về chủ đề chung
            return "sức khỏe tổng quát";

        } catch (Exception e) {
            Log.e(TAG, "Error extracting topic", e);
            return "sức khỏe tổng quát";
        }
    }

    /**
     * Lưu chủ đề mà người dùng quan tâm vào Firebase
     */
    private void saveUserTopic(String userId, String topic) {
        try {
            database.child(USER_TOPICS_PATH)
                    .child(userId)
                    .child(topic)
                    .setValue(System.currentTimeMillis())
                    .addOnSuccessListener(aVoid -> Log.d(TAG, "User topic saved: " + topic))
                    .addOnFailureListener(e -> Log.e(TAG, "Failed to save user topic", e));
        } catch (Exception e) {
            Log.e(TAG, "Error saving user topic", e);
        }
    }

    // ========== HELPER METHODS ==========

    @Override
    public void saveChatMessage(ChatMessage chatMessage, RepositoryCallback<ChatMessage> callback) {
        try {
            if (chatMessage.getConversationId() == null || chatMessage.getUserId() == null || chatMessage.getContent() == null) {
                callback.onError("Thông tin tin nhắn không hợp lệ");
                return;
            }

            String messageId = database.child(CHAT_MESSAGES_PATH)
                    .child(chatMessage.getConversationId())
                    .push().getKey();

            if (messageId != null) {
                chatMessage.setId(messageId);

                database.child(CHAT_MESSAGES_PATH)
                        .child(chatMessage.getConversationId())
                        .child(messageId)
                        .setValue(chatMessage.toMap())
                        .addOnSuccessListener(aVoid -> {
                            Log.d(TAG, "Chat message saved successfully: " + messageId);
                            callback.onSuccess(chatMessage);
                        })
                        .addOnFailureListener(e -> {
                            Log.e(TAG, "Failed to save chat message", e);
                            callback.onError("Không thể lưu tin nhắn: " + e.getMessage());
                        });
            } else {
                callback.onError("Không thể tạo ID cho tin nhắn");
            }
        } catch (Exception e) {
            Log.e(TAG, "Error saving chat message", e);
            callback.onError("Có lỗi xảy ra khi lưu tin nhắn: " + e.getMessage());
        }
    }

    @Override
    public void getChatMessages(String conversationId, RepositoryCallback<List<ChatMessage>> callback) {
        try {
            DatabaseReference messagesRef = database.child(CHAT_MESSAGES_PATH).child(conversationId);

            messagesRef.orderByChild("timestamp").addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot dataSnapshot) {
                    List<ChatMessage> messages = new ArrayList<>();

                    for (DataSnapshot messageSnapshot : dataSnapshot.getChildren()) {
                        try {
                            ChatMessage message = parseChatMessageFromSnapshot(messageSnapshot);
                            if (message != null) {
                                messages.add(message);
                            }
                        } catch (Exception e) {
                            Log.e(TAG, "Error parsing chat message", e);
                        }
                    }

                    Log.d(TAG, "Loaded " + messages.size() + " chat messages");
                    callback.onSuccess(messages);
                }

                @Override
                public void onCancelled(DatabaseError databaseError) {
                    Log.e(TAG, "Failed to load chat messages", databaseError.toException());
                    callback.onError("Không thể tải tin nhắn: " + databaseError.getMessage());
                }
            });
        } catch (Exception e) {
            Log.e(TAG, "Error loading chat messages", e);
            callback.onError("Có lỗi xảy ra khi tải tin nhắn: " + e.getMessage());
        }
    }

    @Override
    public void clearChatMessages(String conversationId, RepositoryCallback<Boolean> callback) {
        try {
            database.child(CHAT_MESSAGES_PATH)
                    .child(conversationId)
                    .removeValue()
                    .addOnSuccessListener(aVoid -> {
                        Log.d(TAG, "Chat messages cleared successfully for conversation: " + conversationId);
                        callback.onSuccess(true);
                    })
                    .addOnFailureListener(e -> {
                        Log.e(TAG, "Failed to clear chat messages", e);
                        callback.onError("Không thể xóa tin nhắn: " + e.getMessage());
                    });
        } catch (Exception e) {
            Log.e(TAG, "Error clearing chat messages", e);
            callback.onError("Có lỗi xảy ra khi xóa tin nhắn: " + e.getMessage());
        }
    }

    /**
     * Parse Conversation từ Firebase DataSnapshot
     */
    private Conversation parseConversationFromSnapshot(DataSnapshot snapshot) {
        try {
            Conversation conversation = new Conversation();
            conversation.setId(snapshot.getKey());
            conversation.setUserId(snapshot.child("userId").getValue(String.class));
            conversation.setTitle(snapshot.child("title").getValue(String.class));
            conversation.setLastMessage(snapshot.child("lastMessage").getValue(String.class));

            Long lastMessageTime = snapshot.child("lastMessageTime").getValue(Long.class);
            if (lastMessageTime != null) {
                conversation.setLastMessageTime(lastMessageTime);
            }

            Boolean isFromUser = snapshot.child("isFromUser").getValue(Boolean.class);
            if (isFromUser != null) {
                conversation.setFromUser(isFromUser);
            }

            Integer messageCount = snapshot.child("messageCount").getValue(Integer.class);
            if (messageCount != null) {
                conversation.setMessageCount(messageCount);
            }

            Long createdTime = snapshot.child("createdTime").getValue(Long.class);
            if (createdTime != null) {
                conversation.setCreatedTime(createdTime);
            }

            conversation.setTopic(snapshot.child("topic").getValue(String.class));

            // Parse các thuộc tính mới cho menu ngữ cảnh
            Boolean isPinned = snapshot.child("isPinned").getValue(Boolean.class);
            if (isPinned != null) {
                conversation.setPinned(isPinned);
            }

            Boolean isMuted = snapshot.child("isMuted").getValue(Boolean.class);
            if (isMuted != null) {
                conversation.setMuted(isMuted);
            }

            Boolean isRead = snapshot.child("isRead").getValue(Boolean.class);
            if (isRead != null) {
                conversation.setRead(isRead);
            }

            return conversation;
        } catch (Exception e) {
            Log.e(TAG, "Error parsing conversation from snapshot", e);
            return null;
        }
    }

    /**
     * Parse ChatMessage từ Firebase DataSnapshot
     */
    private ChatMessage parseChatMessageFromSnapshot(DataSnapshot snapshot) {
        try {
            ChatMessage message = new ChatMessage();
            message.setId(snapshot.getKey());
            message.setConversationId(snapshot.child("conversationId").getValue(String.class));
            message.setUserId(snapshot.child("userId").getValue(String.class));
            message.setContent(snapshot.child("content").getValue(String.class));

            Boolean isFromUser = snapshot.child("isFromUser").getValue(Boolean.class);
            if (isFromUser != null) {
                message.setFromUser(isFromUser);
            }

            Long timestamp = snapshot.child("timestamp").getValue(Long.class);
            if (timestamp != null) {
                message.setTimestamp(timestamp);
            }

            message.setTopic(snapshot.child("topic").getValue(String.class));
            return message;
        } catch (Exception e) {
            Log.e(TAG, "Error parsing message from snapshot", e);
            return null;
        }
    }

    @Override
    public void clearChatHistory(String userId, RepositoryCallback<Boolean> callback) {
        try {
            // Xóa tất cả cuộc trò chuyện của user
            DatabaseReference userConversationsRef = database.child(CONVERSATIONS_PATH).child(userId);

            userConversationsRef.addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot dataSnapshot) {
                    try {
                        List<String> conversationIds = new ArrayList<>();

                        // Lấy danh sách ID các cuộc trò chuyện
                        for (DataSnapshot conversationSnapshot : dataSnapshot.getChildren()) {
                            conversationIds.add(conversationSnapshot.getKey());
                        }

                        if (conversationIds.isEmpty()) {
                            callback.onSuccess(true);
                            return;
                        }

                        // Xóa từng cuộc trò chuyện và tin nhắn của nó
                        deleteConversationsRecursively(conversationIds, 0, userId, callback);

                    } catch (Exception e) {
                        Log.e(TAG, "Error processing chat history deletion", e);
                        callback.onError("Lỗi xử lý xóa lịch sử chat: " + e.getMessage());
                    }
                }

                @Override
                public void onCancelled(DatabaseError databaseError) {
                    Log.e(TAG, "Error clearing chat history", databaseError.toException());
                    callback.onError("Lỗi xóa lịch sử chat: " + databaseError.getMessage());
                }
            });
        } catch (Exception e) {
            Log.e(TAG, "Error initiating chat history clear", e);
            callback.onError("Lỗi khởi tạo xóa lịch sử chat: " + e.getMessage());
        }
    }

    /**
     * Xóa các cuộc trò chuyện một cách đệ quy
     */
    private void deleteConversationsRecursively(List<String> conversationIds, int index, String userId, RepositoryCallback<Boolean> callback) {
        if (index >= conversationIds.size()) {
            // Xóa xong tất cả cuộc trò chuyện, xóa thư mục user trong conversations
            database.child(CONVERSATIONS_PATH).child(userId).removeValue()
                    .addOnSuccessListener(aVoid -> callback.onSuccess(true))
                    .addOnFailureListener(e -> {
                        Log.e(TAG, "Error removing user conversations folder", e);
                        callback.onError("Lỗi xóa thư mục cuộc trò chuyện: " + e.getMessage());
                    });
            return;
        }

        String conversationId = conversationIds.get(index);

        // Xóa tin nhắn của cuộc trò chuyện này trước
        clearChatMessages(conversationId, new RepositoryCallback<Boolean>() {
            @Override
            public void onSuccess(Boolean result) {
                // Sau khi xóa tin nhắn, xóa cuộc trò chuyện
                database.child(CONVERSATIONS_PATH).child(userId).child(conversationId).removeValue()
                        .addOnSuccessListener(aVoid -> {
                            // Tiếp tục xóa cuộc trò chuyện tiếp theo
                            deleteConversationsRecursively(conversationIds, index + 1, userId, callback);
                        })
                        .addOnFailureListener(e -> {
                            Log.e(TAG, "Error deleting conversation: " + conversationId, e);
                            callback.onError("Lỗi xóa cuộc trò chuyện: " + e.getMessage());
                        });
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Error clearing messages for conversation: " + conversationId + ", error: " + error);
                callback.onError("Lỗi xóa tin nhắn cuộc trò chuyện: " + error);
            }
        });
    }
}

package com.vhn.doan.data.repository;

import android.os.Handler;
import android.os.Looper;
import android.util.Log;

import com.google.firebase.database.DataSnapshot;
import com.google.firebase.database.DatabaseError;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.database.Query;
import com.google.firebase.database.ValueEventListener;
import com.google.gson.Gson;
import com.google.gson.JsonObject;
import com.google.gson.JsonArray;
import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.ChatApiResponse;
import com.vhn.doan.data.Conversation;

import java.io.IOException;
import java.util.ArrayList;
import java.util.Collections;
import java.util.List;
import java.util.concurrent.TimeUnit;

import okhttp3.Call;
import okhttp3.Callback;
import okhttp3.MediaType;
import okhttp3.OkHttpClient;
import okhttp3.Request;
import okhttp3.RequestBody;
import okhttp3.Response;

/**
 * Implementation của ChatRepository với hỗ trợ multiple conversations
 */
public class ChatRepositoryImpl implements ChatRepository {

    private static final String TAG = "ChatRepositoryImpl";
    private static final String OPENROUTER_API_URL = "https://openrouter.ai/api/v1/chat/completions";
    // Sử dụng API key mới
    private static final String API_KEY = "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";

    // Firebase paths
    private static final String CONVERSATIONS_PATH = "conversations";
    private static final String CHAT_MESSAGES_PATH = "chat_messages";
    private static final String USER_TOPICS_PATH = "user_topics";

    // Pagination constants
    private static final int DEFAULT_CONVERSATIONS_LIMIT = 8;
    private static final int LOAD_MORE_CONVERSATIONS_LIMIT = 3;

    private final DatabaseReference database;
    private final OkHttpClient httpClient;
    private final Gson gson;
    private final Handler mainHandler;

    public ChatRepositoryImpl() {
        this.database = FirebaseDatabase.getInstance().getReference();
        this.httpClient = new OkHttpClient.Builder()
                .connectTimeout(30, TimeUnit.SECONDS)
                .readTimeout(30, TimeUnit.SECONDS)
                .writeTimeout(30, TimeUnit.SECONDS)
                .build();
        this.gson = new Gson();
        this.mainHandler = new Handler(Looper.getMainLooper());
    }

    // ========== CONVERSATION MANAGEMENT ==========

    @Override
    public void createConversation(String userId, String firstMessage, RepositoryCallback<Conversation> callback) {
        try {
            long currentTime = System.currentTimeMillis();
            String title = Conversation.generateTitle(firstMessage);
            String topic = extractTopic(firstMessage);

            Conversation conversation = new Conversation(userId, title, currentTime);
            conversation.setTopic(topic);

            String conversationId = database.child(CONVERSATIONS_PATH)
                    .child(userId)
                    .push().getKey();

            if (conversationId != null) {
                conversation.setId(conversationId);

                database.child(CONVERSATIONS_PATH)
                        .child(userId)
                        .child(conversationId)
                        .setValue(conversation.toMap())
                        .addOnSuccessListener(aVoid -> {
                            Log.d(TAG, "Conversation created successfully: " + conversationId);
                            callback.onSuccess(conversation);
                        })
                        .addOnFailureListener(e -> {
                            Log.e(TAG, "Failed to create conversation", e);
                            callback.onError("Không thể tạo cuộc trò chuyện: " + e.getMessage());
                        });
            } else {
                callback.onError("Không thể tạo ID cho cuộc trò chuyện");
            }
        } catch (Exception e) {
            Log.e(TAG, "Error creating conversation", e);
            callback.onError("Có lỗi xảy ra khi tạo cuộc trò chuyện: " + e.getMessage());
        }
    }

    @Override
    public void getConversations(String userId, int limit, Long lastConversationTime, RepositoryCallback<List<Conversation>> callback) {
        try {
            DatabaseReference conversationsRef = database.child(CONVERSATIONS_PATH).child(userId);

            // Sắp xếp theo lastMessageTime giảm dần (mới nhất trước)
            Query query = conversationsRef.orderByChild("lastMessageTime");

            // Nếu có lastConversationTime, query từ thời điểm đó trở về trước
            if (lastConversationTime != null) {
                query = query.endBefore(lastConversationTime);
            }

            // Giới hạn số lượng kết quả
            query = query.limitToLast(limit);

            query.addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot dataSnapshot) {
                    List<Conversation> conversations = new ArrayList<>();

                    for (DataSnapshot conversationSnapshot : dataSnapshot.getChildren()) {
                        try {
                            Conversation conversation = parseConversationFromSnapshot(conversationSnapshot);
                            if (conversation != null) {
                                conversations.add(conversation);
                            }
                        } catch (Exception e) {
                            Log.e(TAG, "Error parsing conversation", e);
                        }
                    }

                    // Sắp xếp lại theo thời gian giảm dần (mới nhất trước)
                    Collections.sort(conversations, (c1, c2) ->
                        Long.compare(c2.getLastMessageTime(), c1.getLastMessageTime()));

                    Log.d(TAG, "Loaded " + conversations.size() + " conversations");
                    callback.onSuccess(conversations);
                }

                @Override
                public void onCancelled(DatabaseError databaseError) {
                    Log.e(TAG, "Failed to load conversations", databaseError.toException());
                    callback.onError("Không thể tải danh sách cuộc trò chuyện: " + databaseError.getMessage());
                }
            });
        } catch (Exception e) {
            Log.e(TAG, "Error loading conversations", e);
            callback.onError("Có lỗi xảy ra khi tải danh sách cuộc trò chuyện: " + e.getMessage());
        }
    }

    @Override
    public void updateConversation(Conversation conversation, RepositoryCallback<Conversation> callback) {
        try {
            if (conversation.getId() == null || conversation.getUserId() == null) {
                callback.onError("Thông tin cuộc trò chuyện không hợp lệ");
                return;
            }

            database.child(CONVERSATIONS_PATH)
                    .child(conversation.getUserId())
                    .child(conversation.getId())
                    .setValue(conversation.toMap())
                    .addOnSuccessListener(aVoid -> {
                        Log.d(TAG, "Conversation updated successfully: " + conversation.getId());
                        callback.onSuccess(conversation);
                    })
                    .addOnFailureListener(e -> {
                        Log.e(TAG, "Failed to update conversation", e);
                        callback.onError("Không thể cập nhật cuộc trò chuyện: " + e.getMessage());
                    });
        } catch (Exception e) {
            Log.e(TAG, "Error updating conversation", e);
            callback.onError("Có lỗi xảy ra khi cập nhật cuộc trò chuyện: " + e.getMessage());
        }
    }

    @Override
    public void deleteConversation(String conversationId, RepositoryCallback<Boolean> callback) {
        try {
            if (conversationId == null || conversationId.isEmpty()) {
                callback.onError("ID cuộc trò chuyện không hợp lệ");
                return;
            }

            // Đầu tiên, xóa tất cả tin nhắn trong cuộc trò chuyện
            clearChatMessages(conversationId, new RepositoryCallback<Boolean>() {
                @Override
                public void onSuccess(Boolean result) {
                    // Sau khi xóa tin nhắn thành công, tìm và xóa cuộc trò chuyện
                    deleteConversationFromAllUsers(conversationId, callback);
                }

                @Override
                public void onError(String error) {
                    Log.e(TAG, "Failed to clear messages before deleting conversation: " + error);
                    callback.onError("Không thể xóa tin nhắn: " + error);
                }
            });

        } catch (Exception e) {
            Log.e(TAG, "Error deleting conversation", e);
            callback.onError("Có lỗi xảy ra khi xóa cuộc trò chuyện: " + e.getMessage());
        }
    }

    /**
     * Tìm và xóa cuộc trò chuyện từ tất cả người dùng
     */
    private void deleteConversationFromAllUsers(String conversationId, RepositoryCallback<Boolean> callback) {
        // Query tất cả users để tìm cuộc trò chuyện
        DatabaseReference conversationsRef = database.child(CONVERSATIONS_PATH);

        conversationsRef.addListenerForSingleValueEvent(new ValueEventListener() {
            @Override
            public void onDataChange(DataSnapshot dataSnapshot) {
                boolean conversationFound = false;

                for (DataSnapshot userSnapshot : dataSnapshot.getChildren()) {
                    String userId = userSnapshot.getKey();

                    if (userSnapshot.hasChild(conversationId)) {
                        conversationFound = true;

                        // Xóa cuộc trò chuyện từ user này
                        database.child(CONVERSATIONS_PATH)
                                .child(userId)
                                .child(conversationId)
                                .removeValue()
                                .addOnSuccessListener(aVoid -> {
                                    Log.d(TAG, "Conversation deleted successfully: " + conversationId);
                                    callback.onSuccess(true);
                                })
                                .addOnFailureListener(e -> {
                                    Log.e(TAG, "Failed to delete conversation: " + conversationId, e);
                                    callback.onError("Không thể xóa cuộc trò chuyện: " + e.getMessage());
                                });
                        break;
                    }
                }

                if (!conversationFound) {
                    callback.onError("Không tìm thấy cuộc trò chuyện để xóa");
                }
            }

            @Override
            public void onCancelled(DatabaseError databaseError) {
                Log.e(TAG, "Error finding conversation to delete", databaseError.toException());
                callback.onError("Lỗi tìm kiếm cuộc trò chuyện: " + databaseError.getMessage());
            }
        });
    }

    @Override
    public void hasMoreConversations(String userId, Long lastConversationTime, RepositoryCallback<Boolean> callback) {
        try {
            DatabaseReference conversationsRef = database.child(CONVERSATIONS_PATH).child(userId);

            Query query = conversationsRef.orderByChild("lastMessageTime");
            if (lastConversationTime != null) {
                query = query.endBefore(lastConversationTime);
            }
            query = query.limitToLast(1);

            query.addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot dataSnapshot) {
                    boolean hasMore = dataSnapshot.exists() && dataSnapshot.getChildrenCount() > 0;
                    callback.onSuccess(hasMore);
                }

                @Override
                public void onCancelled(DatabaseError databaseError) {
                    Log.e(TAG, "Failed to check more conversations", databaseError.toException());
                    callback.onError("Không thể kiểm tra cuộc trò chuyện: " + databaseError.getMessage());
                }
            });
        } catch (Exception e) {
            Log.e(TAG, "Error checking more conversations", e);
            callback.onError("Có lỗi xảy ra khi kiểm tra cuộc trò chuyện: " + e.getMessage());
        }
    }

    // ========== MESSAGE MANAGEMENT ==========

    @Override
    public void sendMessageToAI(String message, RepositoryCallback<String> callback) {
        try {
            // Tạo JSON request body theo format chính xác của OpenRouter
            JsonObject requestJson = new JsonObject();
            requestJson.addProperty("model", "openai/gpt-3.5-turbo");

            // Thêm các parameters bổ sung theo yêu cầu OpenRouter
            requestJson.addProperty("max_tokens", 1000);
            requestJson.addProperty("temperature", 0.7);

            JsonArray messagesArray = new JsonArray();

            // System message
            JsonObject systemMessage = new JsonObject();
            systemMessage.addProperty("role", "system");
            systemMessage.addProperty("content", "Bạn là trợ lý AI chuyên về sức khỏe. Chỉ trả lời các câu hỏi liên quan đến sức khỏe, y tế, dinh dưỡng, thể dục thể thao. Nếu người dùng hỏi ngoài lĩnh vực sức khỏe thì từ chối một cách lịch sự và đề nghị họ hỏi về sức khỏe.");
            messagesArray.add(systemMessage);

            // User message
            JsonObject userMessage = new JsonObject();
            userMessage.addProperty("role", "user");
            userMessage.addProperty("content", message);
            messagesArray.add(userMessage);

            requestJson.add("messages", messagesArray);

            String jsonString = gson.toJson(requestJson);
            Log.d(TAG, "Request JSON: " + jsonString);

            RequestBody body = RequestBody.create(
                    MediaType.parse("application/json"),
                    jsonString
            );

            Request request = new Request.Builder()
                    .url(OPENROUTER_API_URL)
                    .post(body)
                    .addHeader("Authorization", "Bearer " + API_KEY)
                    .addHeader("Content-Type", "application/json")
                    .addHeader("HTTP-Referer", "https://healthtips-vn.app")
                    .addHeader("X-Title", "HealthTips Vietnam")
                    .build();

            Log.d(TAG, "Sending request to: " + OPENROUTER_API_URL);
            Log.d(TAG, "Authorization header: Bearer " + API_KEY.substring(0, 20) + "...");

            httpClient.newCall(request).enqueue(new Callback() {
                @Override
                public void onFailure(Call call, IOException e) {
                    Log.e(TAG, "Network request failed", e);
                    mainHandler.post(() -> {
                        callback.onError("Không thể kết nối đến máy chủ AI. Kiểm tra kết nối mạng của bạn.");
                    });
                }

                @Override
                public void onResponse(Call call, Response response) throws IOException {
                    String responseBody = "";
                    try {
                        if (response.body() != null) {
                            responseBody = response.body().string();
                        }

                        Log.d(TAG, "Response Code: " + response.code());
                        Log.d(TAG, "Response Headers: " + response.headers().toString());
                        Log.d(TAG, "Response Body: " + responseBody);

                        if (response.isSuccessful()) {
                            ChatApiResponse apiResponse = gson.fromJson(responseBody, ChatApiResponse.class);

                            if (apiResponse != null &&
                                apiResponse.getChoices() != null &&
                                !apiResponse.getChoices().isEmpty() &&
                                apiResponse.getChoices().get(0).getMessage() != null) {

                                String aiMessage = apiResponse.getChoices().get(0).getMessage().getContent();
                                if (aiMessage != null && !aiMessage.trim().isEmpty()) {
                                    mainHandler.post(() -> callback.onSuccess(aiMessage.trim()));
                                } else {
                                    mainHandler.post(() -> callback.onError("AI trả về phản hồi trống."));
                                }
                            } else {
                                mainHandler.post(() -> callback.onError("Định dạng phản hồi từ AI không hợp lệ."));
                            }
                        } else {
                            // Xử lý các lỗi HTTP cụ thể
                            String errorMessage = parseErrorMessage(response.code(), responseBody);
                            Log.e(TAG, "API Error: " + errorMessage);

                            mainHandler.post(() -> callback.onError(errorMessage));
                        }
                    } catch (Exception e) {
                        Log.e(TAG, "Error parsing response", e);
                        Log.e(TAG, "Response body was: " + responseBody);

                        mainHandler.post(() -> {
                            callback.onError("Lỗi xử lý phản hồi từ AI. Vui lòng thử lại.");
                        });
                    }
                }
            });

        } catch (Exception e) {
            Log.e(TAG, "Error creating request", e);
            callback.onError("Lỗi tạo yêu cầu gửi đến AI.");
        }
    }

    /**
     * Phân tích thông báo lỗi từ API response
     */
    private String parseErrorMessage(int responseCode, String responseBody) {
        try {
            // Thử parse JSON error response
            JsonObject errorResponse = gson.fromJson(responseBody, JsonObject.class);
            if (errorResponse.has("error")) {
                JsonObject error = errorResponse.getAsJsonObject("error");
                if (error.has("message")) {
                    String apiErrorMessage = error.get("message").getAsString();
                    Log.d(TAG, "API Error Message: " + apiErrorMessage);

                    // Dịch một số lỗi phổ biến
                    if (apiErrorMessage.contains("No auth credentials")) {
                        return "Lỗi xác thực API. Vui lòng liên hệ quản trị viên.";
                    } else if (apiErrorMessage.contains("Rate limit")) {
                        return "Đã vượt quá giới hạn yêu cầu. Vui lòng thử lại sau ít phút.";
                    } else if (apiErrorMessage.contains("Invalid model")) {
                        return "Mô hình AI không hợp lệ. Vui lòng thử lại.";
                    }
                    return "Lỗi từ AI: " + apiErrorMessage;
                }
            }
        } catch (Exception e) {
            Log.e(TAG, "Error parsing error response", e);
        }

        // Fallback cho các HTTP status codes
        switch (responseCode) {
            case 401:
                return "Lỗi xác thực. API key có thể đã hết hạn hoặc không hợp lệ.";
            case 403:
                return "Không có quyền truy cập. Vui lòng kiểm tra cấu hình API.";
            case 429:
                return "Quá nhiều yêu cầu. Vui lòng chờ một chút rồi thử lại.";
            case 500:
                return "Lỗi máy chủ AI. Vui lòng thử lại sau.";
            case 502:
            case 503:
            case 504:
                return "Máy chủ AI tạm thời không khả dụng. Vui lòng thử lại sau.";
            default:
                return "Lỗi không xác định từ AI (Mã: " + responseCode + "). Vui lòng thử lại.";
        }
    }

    @Override
    public String extractTopic(String content) {
        try {
            // Các từ khóa chủ đề về sức khỏe
            String[] healthTopics = {
                "tim mạch", "huyết áp", "cholesterol", "đường huyết", "tiểu đường",
                "dinh dưỡng", "vitamin", "protein", "carb", "chất béo",
                "tập luyện", "thể dục", "yoga", "cardio", "cơ bắp",
                "giảm cân", "tăng cân", "béo phì", "ăn kiêng",
                "stress", "lo âu", "trầm cảm", "tâm lý", "tinh thần",
                "giấc ngủ", "mất ngủ", "ngủ", "nghỉ ngơi",
                "da", "tóc", "móng", "mỹ phẩm", "chăm sóc da",
                "mang thai", "sinh sản", "kinh nguyệt", "phụ khoa",
                "trẻ em", "em bé", "sức khỏe trẻ", "phát triển",
                "người cao tuổi", "lão hóa", "xương khớp", "cột sống",
                "mắt", "thị lực", "tai", "thính giác",
                "răng", "miệng", "nha khoa", "vệ sinh răng miệng",
                "cảm cúm", "sốt", "ho", "viêm họng", "virus",
                "thuốc", "dược phẩm", "tác dụng phụ", "liều dùng"
            };

            content = content.toLowerCase();

            for (String topic : healthTopics) {
                if (content.contains(topic)) {
                    return topic;
                }
            }

            // Nếu không tìm thấy chủ đề cụ thể, trả về chủ đề chung
            return "sức khỏe tổng quát";

        } catch (Exception e) {
            Log.e(TAG, "Error extracting topic", e);
            return "sức khỏe tổng quát";
        }
    }

    /**
     * Lưu chủ đề mà người dùng quan tâm vào Firebase
     */
    private void saveUserTopic(String userId, String topic) {
        try {
            database.child(USER_TOPICS_PATH)
                    .child(userId)
                    .child(topic)
                    .setValue(System.currentTimeMillis())
                    .addOnSuccessListener(aVoid -> Log.d(TAG, "User topic saved: " + topic))
                    .addOnFailureListener(e -> Log.e(TAG, "Failed to save user topic", e));
        } catch (Exception e) {
            Log.e(TAG, "Error saving user topic", e);
        }
    }

    // ========== HELPER METHODS ==========

    @Override
    public void saveChatMessage(ChatMessage chatMessage, RepositoryCallback<ChatMessage> callback) {
        try {
            if (chatMessage.getConversationId() == null || chatMessage.getUserId() == null || chatMessage.getContent() == null) {
                callback.onError("Thông tin tin nhắn không hợp lệ");
                return;
            }

            String messageId = database.child(CHAT_MESSAGES_PATH)
                    .child(chatMessage.getConversationId())
                    .push().getKey();

            if (messageId != null) {
                chatMessage.setId(messageId);

                database.child(CHAT_MESSAGES_PATH)
                        .child(chatMessage.getConversationId())
                        .child(messageId)
                        .setValue(chatMessage.toMap())
                        .addOnSuccessListener(aVoid -> {
                            Log.d(TAG, "Chat message saved successfully: " + messageId);
                            callback.onSuccess(chatMessage);
                        })
                        .addOnFailureListener(e -> {
                            Log.e(TAG, "Failed to save chat message", e);
                            callback.onError("Không thể lưu tin nhắn: " + e.getMessage());
                        });
            } else {
                callback.onError("Không thể tạo ID cho tin nhắn");
            }
        } catch (Exception e) {
            Log.e(TAG, "Error saving chat message", e);
            callback.onError("Có lỗi xảy ra khi lưu tin nhắn: " + e.getMessage());
        }
    }

    @Override
    public void getChatMessages(String conversationId, RepositoryCallback<List<ChatMessage>> callback) {
        try {
            DatabaseReference messagesRef = database.child(CHAT_MESSAGES_PATH).child(conversationId);

            messagesRef.orderByChild("timestamp").addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot dataSnapshot) {
                    List<ChatMessage> messages = new ArrayList<>();

                    for (DataSnapshot messageSnapshot : dataSnapshot.getChildren()) {
                        try {
                            ChatMessage message = parseChatMessageFromSnapshot(messageSnapshot);
                            if (message != null) {
                                messages.add(message);
                            }
                        } catch (Exception e) {
                            Log.e(TAG, "Error parsing chat message", e);
                        }
                    }

                    Log.d(TAG, "Loaded " + messages.size() + " chat messages");
                    callback.onSuccess(messages);
                }

                @Override
                public void onCancelled(DatabaseError databaseError) {
                    Log.e(TAG, "Failed to load chat messages", databaseError.toException());
                    callback.onError("Không thể tải tin nhắn: " + databaseError.getMessage());
                }
            });
        } catch (Exception e) {
            Log.e(TAG, "Error loading chat messages", e);
            callback.onError("Có lỗi xảy ra khi tải tin nhắn: " + e.getMessage());
        }
    }

    @Override
    public void clearChatMessages(String conversationId, RepositoryCallback<Boolean> callback) {
        try {
            database.child(CHAT_MESSAGES_PATH)
                    .child(conversationId)
                    .removeValue()
                    .addOnSuccessListener(aVoid -> {
                        Log.d(TAG, "Chat messages cleared successfully for conversation: " + conversationId);
                        callback.onSuccess(true);
                    })
                    .addOnFailureListener(e -> {
                        Log.e(TAG, "Failed to clear chat messages", e);
                        callback.onError("Không thể xóa tin nhắn: " + e.getMessage());
                    });
        } catch (Exception e) {
            Log.e(TAG, "Error clearing chat messages", e);
            callback.onError("Có lỗi xảy ra khi xóa tin nhắn: " + e.getMessage());
        }
    }

    /**
     * Parse Conversation từ Firebase DataSnapshot
     */
    private Conversation parseConversationFromSnapshot(DataSnapshot snapshot) {
        try {
            Conversation conversation = new Conversation();
            conversation.setId(snapshot.getKey());
            conversation.setUserId(snapshot.child("userId").getValue(String.class));
            conversation.setTitle(snapshot.child("title").getValue(String.class));
            conversation.setLastMessage(snapshot.child("lastMessage").getValue(String.class));

            Long lastMessageTime = snapshot.child("lastMessageTime").getValue(Long.class);
            if (lastMessageTime != null) {
                conversation.setLastMessageTime(lastMessageTime);
            }

            Boolean isFromUser = snapshot.child("isFromUser").getValue(Boolean.class);
            if (isFromUser != null) {
                conversation.setFromUser(isFromUser);
            }

            Integer messageCount = snapshot.child("messageCount").getValue(Integer.class);
            if (messageCount != null) {
                conversation.setMessageCount(messageCount);
            }

            Long createdTime = snapshot.child("createdTime").getValue(Long.class);
            if (createdTime != null) {
                conversation.setCreatedTime(createdTime);
            }

            conversation.setTopic(snapshot.child("topic").getValue(String.class));

            // Parse các thuộc tính mới cho menu ngữ cảnh
            Boolean isPinned = snapshot.child("isPinned").getValue(Boolean.class);
            if (isPinned != null) {
                conversation.setPinned(isPinned);
            }

            Boolean isMuted = snapshot.child("isMuted").getValue(Boolean.class);
            if (isMuted != null) {
                conversation.setMuted(isMuted);
            }

            Boolean isRead = snapshot.child("isRead").getValue(Boolean.class);
            if (isRead != null) {
                conversation.setRead(isRead);
            }

            return conversation;
        } catch (Exception e) {
            Log.e(TAG, "Error parsing conversation from snapshot", e);
            return null;
        }
    }

    /**
     * Parse ChatMessage từ Firebase DataSnapshot
     */
    private ChatMessage parseChatMessageFromSnapshot(DataSnapshot snapshot) {
        try {
            ChatMessage message = new ChatMessage();
            message.setId(snapshot.getKey());
            message.setConversationId(snapshot.child("conversationId").getValue(String.class));
            message.setUserId(snapshot.child("userId").getValue(String.class));
            message.setContent(snapshot.child("content").getValue(String.class));

            Boolean isFromUser = snapshot.child("isFromUser").getValue(Boolean.class);
            if (isFromUser != null) {
                message.setFromUser(isFromUser);
            }

            Long timestamp = snapshot.child("timestamp").getValue(Long.class);
            if (timestamp != null) {
                message.setTimestamp(timestamp);
            }

            message.setTopic(snapshot.child("topic").getValue(String.class));
            return message;
        } catch (Exception e) {
            Log.e(TAG, "Error parsing message from snapshot", e);
            return null;
        }
    }

    @Override
    public void clearChatHistory(String userId, RepositoryCallback<Boolean> callback) {
        try {
            // Xóa tất cả cuộc trò chuyện của user
            DatabaseReference userConversationsRef = database.child(CONVERSATIONS_PATH).child(userId);

            userConversationsRef.addListenerForSingleValueEvent(new ValueEventListener() {
                @Override
                public void onDataChange(DataSnapshot dataSnapshot) {
                    try {
                        List<String> conversationIds = new ArrayList<>();

                        // Lấy danh sách ID các cuộc trò chuyện
                        for (DataSnapshot conversationSnapshot : dataSnapshot.getChildren()) {
                            conversationIds.add(conversationSnapshot.getKey());
                        }

                        if (conversationIds.isEmpty()) {
                            callback.onSuccess(true);
                            return;
                        }

                        // Xóa từng cuộc trò chuyện và tin nhắn của nó
                        deleteConversationsRecursively(conversationIds, 0, userId, callback);

                    } catch (Exception e) {
                        Log.e(TAG, "Error processing chat history deletion", e);
                        callback.onError("Lỗi xử lý xóa lịch sử chat: " + e.getMessage());
                    }
                }

                @Override
                public void onCancelled(DatabaseError databaseError) {
                    Log.e(TAG, "Error clearing chat history", databaseError.toException());
                    callback.onError("Lỗi xóa lịch sử chat: " + databaseError.getMessage());
                }
            });
        } catch (Exception e) {
            Log.e(TAG, "Error initiating chat history clear", e);
            callback.onError("Lỗi khởi tạo xóa lịch sử chat: " + e.getMessage());
        }
    }

    /**
     * Xóa các cuộc trò chuyện một cách đệ quy
     */
    private void deleteConversationsRecursively(List<String> conversationIds, int index, String userId, RepositoryCallback<Boolean> callback) {
        if (index >= conversationIds.size()) {
            // Xóa xong tất cả cuộc trò chuyện, xóa thư mục user trong conversations
            database.child(CONVERSATIONS_PATH).child(userId).removeValue()
                    .addOnSuccessListener(aVoid -> callback.onSuccess(true))
                    .addOnFailureListener(e -> {
                        Log.e(TAG, "Error removing user conversations folder", e);
                        callback.onError("Lỗi xóa thư mục cuộc trò chuyện: " + e.getMessage());
                    });
            return;
        }

        String conversationId = conversationIds.get(index);

        // Xóa tin nhắn của cuộc trò chuyện này trước
        clearChatMessages(conversationId, new RepositoryCallback<Boolean>() {
            @Override
            public void onSuccess(Boolean result) {
                // Sau khi xóa tin nhắn, xóa cuộc trò chuyện
                database.child(CONVERSATIONS_PATH).child(userId).child(conversationId).removeValue()
                        .addOnSuccessListener(aVoid -> {
                            // Tiếp tục xóa cuộc trò chuyện tiếp theo
                            deleteConversationsRecursively(conversationIds, index + 1, userId, callback);
                        })
                        .addOnFailureListener(e -> {
                            Log.e(TAG, "Error deleting conversation: " + conversationId, e);
                            callback.onError("Lỗi xóa cuộc trò chuyện: " + e.getMessage());
                        });
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Error clearing messages for conversation: " + conversationId + ", error: " + error);
                callback.onError("Lỗi xóa tin nhắn cuộc trò chuyện: " + error);
            }
        });
    }
}

package com.vhn.doan.presentation.chat;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.vhn.doan.R;
import com.vhn.doan.data.ChatMessage;

import java.text.SimpleDateFormat;
import java.util.ArrayList;
import java.util.Date;
import java.util.List;
import java.util.Locale;

/**
 * Adapter cho danh sách tin nhắn chat
 */
public class ChatAdapter extends RecyclerView.Adapter<RecyclerView.ViewHolder> {

    private static final int VIEW_TYPE_USER_MESSAGE = 1;
    private static final int VIEW_TYPE_AI_MESSAGE = 2;
    private static final int VIEW_TYPE_TYPING = 3;

    private List<ChatMessage> messages;
    private boolean isAiTyping = false;
    private final SimpleDateFormat timeFormat;

    public ChatAdapter() {
        this.messages = new ArrayList<>();
        this.timeFormat = new SimpleDateFormat("HH:mm", Locale.getDefault());
    }

    @Override
    public int getItemViewType(int position) {
        if (isAiTyping && position == getItemCount() - 1) {
            return VIEW_TYPE_TYPING;
        }

        ChatMessage message = messages.get(position);
        return message.isFromUser() ? VIEW_TYPE_USER_MESSAGE : VIEW_TYPE_AI_MESSAGE;
    }

    @NonNull
    @Override
    public RecyclerView.ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        LayoutInflater inflater = LayoutInflater.from(parent.getContext());

        switch (viewType) {
            case VIEW_TYPE_USER_MESSAGE:
                View userView = inflater.inflate(R.layout.item_chat_user_message, parent, false);
                return new UserMessageViewHolder(userView);

            case VIEW_TYPE_AI_MESSAGE:
                View aiView = inflater.inflate(R.layout.item_chat_ai_message, parent, false);
                return new AiMessageViewHolder(aiView);

            case VIEW_TYPE_TYPING:
                View typingView = inflater.inflate(R.layout.item_chat_typing, parent, false);
                return new TypingViewHolder(typingView);

            default:
                throw new IllegalArgumentException("Unknown view type: " + viewType);
        }
    }

    @Override
    public void onBindViewHolder(@NonNull RecyclerView.ViewHolder holder, int position) {
        if (holder instanceof TypingViewHolder) {
            // Không cần bind data cho typing indicator
            return;
        }

        ChatMessage message = messages.get(position);

        if (holder instanceof UserMessageViewHolder) {
            ((UserMessageViewHolder) holder).bind(message);
        } else if (holder instanceof AiMessageViewHolder) {
            ((AiMessageViewHolder) holder).bind(message);
        }
    }

    @Override
    public int getItemCount() {
        int count = messages.size();
        if (isAiTyping) {
            count++;
        }
        return count;
    }

    /**
     * Cập nhật danh sách tin nhắn
     */
    public void setMessages(List<ChatMessage> messages) {
        this.messages.clear();
        this.messages.addAll(messages);
        notifyDataSetChanged();
    }

    /**
     * Thêm tin nhắn mới
     */
    public void addMessage(ChatMessage message) {
        this.messages.add(message);
        notifyItemInserted(messages.size() - 1);
    }

    /**
     * Hiển thị trạng thái AI đang gõ
     */
    public void showAiTyping() {
        if (!isAiTyping) {
            isAiTyping = true;
            notifyItemInserted(getItemCount() - 1);
        }
    }

    /**
     * Ẩn trạng thái AI đang gõ
     */
    public void hideAiTyping() {
        if (isAiTyping) {
            isAiTyping = false;
            notifyItemRemoved(getItemCount());
        }
    }

    /**
     * Xóa tất cả tin nhắn
     */
    public void clearMessages() {
        int size = messages.size();
        messages.clear();
        notifyItemRangeRemoved(0, size);
    }

    // ViewHolder cho tin nhắn người dùng
    class UserMessageViewHolder extends RecyclerView.ViewHolder {
        private TextView messageText;
        private TextView timeText;

        public UserMessageViewHolder(@NonNull View itemView) {
            super(itemView);
            messageText = itemView.findViewById(R.id.tv_user_message);
            timeText = itemView.findViewById(R.id.tv_user_time);
        }

        public void bind(ChatMessage message) {
            messageText.setText(message.getContent());
            timeText.setText(timeFormat.format(new Date(message.getTimestamp())));
        }
    }

    // ViewHolder cho tin nhắn AI
    class AiMessageViewHolder extends RecyclerView.ViewHolder {
        private TextView messageText;
        private TextView timeText;

        public AiMessageViewHolder(@NonNull View itemView) {
            super(itemView);
            messageText = itemView.findViewById(R.id.tv_ai_message);
            timeText = itemView.findViewById(R.id.tv_ai_time);
        }

        public void bind(ChatMessage message) {
            messageText.setText(message.getContent());
            timeText.setText(timeFormat.format(new Date(message.getTimestamp())));
        }
    }

    // ViewHolder cho typing indicator
    class TypingViewHolder extends RecyclerView.ViewHolder {
        public TypingViewHolder(@NonNull View itemView) {
            super(itemView);
        }
    }
}

