    @Override
    public void showLoadMoreButton(boolean show) {
        layoutLoadMore.setVisibility(show ? View.VISIBLE : View.GONE);
        if (show) {
            btnLoadMore.setVisibility(View.VISIBLE);
            layoutLoadingMore.setVisibility(View.GONE);
        }
    }

    @Override
    public void showEmptyState() {
        layoutEmpty.setVisibility(View.VISIBLE);
        rvConversations.setVisibility(View.GONE);
    }

    @Override
    public void hideEmptyState() {
        layoutEmpty.setVisibility(View.GONE);
        rvConversations.setVisibility(View.VISIBLE);
    }

    @Override
    public void showLoadConversationsError(String error) {
        showError("Lỗi tải cuộc trò chuyện: " + error);
    }

    @Override
    public void showLoadMoreError(String error) {
        showError("Lỗi tải thêm: " + error);
    }

    @Override
    public void navigateToChatDetail(String conversationId, String conversationTitle) {
        // TODO: Chuyển đến ChatDetailFragment với conversationId
        if (getActivity() != null) {
            ChatDetailFragment chatDetailFragment = ChatDetailFragment.newInstance(conversationId, conversationTitle);

            getActivity().getSupportFragmentManager()
                    .beginTransaction()
                    .replace(R.id.fragment_container, chatDetailFragment)
                    .addToBackStack(null)
                    .commit();
        }
    }

    @Override
    public void navigateToNewChat() {
        // TODO: Chuyển đến NewChatFragment hoặc ChatDetailFragment với conversationId mới
        if (getActivity() != null) {
            NewChatFragment newChatFragment = NewChatFragment.newInstance();

            getActivity().getSupportFragmentManager()
                    .beginTransaction()
                    .replace(R.id.fragment_container, newChatFragment)
                    .addToBackStack(null)
                    .commit();
        }
    }

    @Override
    public void showMessage(String message) {
        if (getContext() != null) {
            Toast.makeText(getContext(), message, Toast.LENGTH_SHORT).show();
        }
    }

    @Override
    public void showError(String message) {
        if (getContext() != null) {
            Toast.makeText(getContext(), message, Toast.LENGTH_LONG).show();
        }
    }

    @Override
    public void showLoading() {
        showLoadingConversations();
    }

    @Override
    public void hideLoading() {
        hideLoadingConversations();
    }

    @Override
    public void onDestroyView() {
        if (presenter != null) {
            presenter.detachView();
        }
        super.onDestroyView();
    }

    /**
     * Method để refresh danh sách từ bên ngoài
     */
    public void refreshConversations() {
        if (presenter != null) {
            presenter.refreshConversations();
        }
    }

    /**
     * Hiển thị menu ngữ cảnh khi nhấn giữ vào cuộc trò chuyện
     */
    private void showConversationContextMenu(Conversation conversation, View anchorView) {
        if (getContext() == null) return;

        PopupMenu popupMenu = new PopupMenu(getContext(), anchorView);
        popupMenu.getMenuInflater().inflate(R.menu.menu_conversation_context, popupMenu.getMenu());

        // Cập nhật trạng thái menu items dựa trên trạng thái cuộc trò chuyện
        updateMenuItemsState(popupMenu, conversation);

        popupMenu.setOnMenuItemClickListener(item -> {
            int itemId = item.getItemId();

            if (itemId == R.id.action_rename_conversation) {
                showRenameConversationDialog(conversation);
                return true;
            } else if (itemId == R.id.action_pin_conversation) {
                togglePinConversation(conversation);
                return true;
            } else if (itemId == R.id.action_delete_conversation) {
                showDeleteConversationDialog(conversation);
                return true;
            }

            return false;
        });

        popupMenu.show();
    }

    /**
     * Cập nhật trạng thái các menu items dựa trên trạng thái cuộc trò chuyện
     */
    private void updateMenuItemsState(PopupMenu popupMenu, Conversation conversation) {
        // Cập nhật text cho pin/unpin
        if (conversation.isPinned()) {
            popupMenu.getMenu().findItem(R.id.action_pin_conversation)
                    .setTitle(R.string.unpin_conversation);
        } else {
            popupMenu.getMenu().findItem(R.id.action_pin_conversation)
                    .setTitle(R.string.pin_conversation);
        }
    }

    /**
     * Hiển thị dialog đổi tên cuộc trò chuyện
     */
    private void showRenameConversationDialog(Conversation conversation) {
        if (getContext() == null) return;

        EditText editText = new EditText(getContext());
        editText.setText(conversation.getTitle());
        editText.setSelectAllOnFocus(true);

        new AlertDialog.Builder(getContext())
                .setTitle(R.string.rename_conversation)
                .setMessage(R.string.enter_new_name)
                .setView(editText)
                .setPositiveButton(R.string.rename, (dialog, which) -> {
                    String newName = editText.getText().toString().trim();
                    if (!newName.isEmpty() && presenter != null) {
                        presenter.renameConversation(conversation, newName);
                    }
                })
                .setNegativeButton(R.string.cancel, null)
                .show();
    }

    /**
     * Hiển thị dialog xác nhận xóa cuộc trò chuyện
     */
    private void showDeleteConversationDialog(Conversation conversation) {
        if (getContext() == null) return;

        new AlertDialog.Builder(getContext())
                .setTitle(R.string.delete_conversation_confirm)
                .setMessage(R.string.delete_conversation_confirm_message)
                .setPositiveButton(R.string.delete, (dialog, which) -> {
                    if (presenter != null) {
                        presenter.deleteConversation(conversation);
                    }
                })
                .setNegativeButton(R.string.cancel, null)
                .show();
    }

    /**
     * Bật/tắt ghim cuộc trò chuyện
     */
    private void togglePinConversation(Conversation conversation) {
        if (presenter != null) {
            presenter.togglePinConversation(conversation);
        }
    }

    /**
     * Bật/tắt thông báo cuộc trò chuyện
     */
    private void toggleMuteConversation(Conversation conversation) {
        if (presenter != null) {
            presenter.toggleMuteConversation(conversation);
        }
    }

    /**
     * Chia sẻ nội dung cuộc trò chuyện
     */
    private void shareConversation(Conversation conversation) {
        if (getContext() == null || presenter == null) return;

        // Tạo nội dung chia sẻ
        String shareText = "Cuộc trò chuyện: " + conversation.getTitle() + "\n\n";
        if (conversation.getLastMessage() != null) {
            shareText += "Tin nhắn cuối: " + conversation.getLastMessage();
        }
        shareText += "\n\nỨng dụng HealthTips - Chat AI về sức khỏe";

        Intent shareIntent = new Intent(Intent.ACTION_SEND);
        shareIntent.setType("text/plain");
        shareIntent.putExtra(Intent.EXTRA_TEXT, shareText);
        shareIntent.putExtra(Intent.EXTRA_SUBJECT, "Cuộc trò chuyện từ HealthTips");

        try {
            startActivity(Intent.createChooser(shareIntent, getString(R.string.share_conversation)));
        } catch (Exception e) {
            showError("Không thể chia sẻ cuộc trò chuyện");
        }
    }

    /**
     * Sao chép tin nhắn của cuộc trò chuyện
     */
    private void copyConversationMessages(Conversation conversation) {
        if (getContext() == null || presenter == null) return;

        // Tạo nội dung sao chép
        String copyText = "Cuộc trò chuyện: " + conversation.getTitle() + "\n\n";
        if (conversation.getLastMessage() != null) {
            copyText += conversation.getLastMessage();
        }

        ClipboardManager clipboard = (ClipboardManager) getContext().getSystemService(Context.CLIPBOARD_SERVICE);
        ClipData clip = ClipData.newPlainText("Conversation", copyText);

        if (clipboard != null) {
            clipboard.setPrimaryClip(clip);
            showMessage(getString(R.string.messages_copied));
        }
    }

    /**
     * Đánh dấu cuộc trò chuyện là đã đọc
     */
    private void markConversationAsRead(Conversation conversation) {
        if (presenter != null) {
            presenter.markConversationAsRead(conversation);
        }
    }
}

package com.vhn.doan.presentation.chat;

import android.util.Log;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.vhn.doan.data.Conversation;
import com.vhn.doan.data.repository.ChatRepository;
import com.vhn.doan.data.repository.RepositoryCallback;

import java.util.ArrayList;
import java.util.List;

/**
 * Presenter cho ChatList feature - Quản lý danh sách cuộc trò chuyện
 */
public class ChatListPresenter implements ChatListContract.Presenter {

    private static final String TAG = "ChatListPresenter";
    private static final int DEFAULT_CONVERSATIONS_LIMIT = 8;
    private static final int LOAD_MORE_CONVERSATIONS_LIMIT = 3;

    private final ChatRepository chatRepository;
    private final FirebaseAuth firebaseAuth;
    private ChatListContract.View view;

    // State management
    private List<Conversation> conversationsList;
    private Long lastConversationTime;
    private boolean isLoading = false;
    private boolean isLoadingMore = false;
    private boolean hasMoreConversations = true;

    public ChatListPresenter(ChatRepository chatRepository) {
        this.chatRepository = chatRepository;
        this.firebaseAuth = FirebaseAuth.getInstance();
        this.conversationsList = new ArrayList<>();
    }

    @Override
    public void attachView(ChatListContract.View view) {
        this.view = view;
        // Tự động tải danh sách cuộc trò chuyện khi attach view
        loadConversations();
    }

    @Override
    public void detachView() {
        this.view = null;
    }

    private boolean isViewAttached() {
        return view != null;
    }

    @Override
    public void loadConversations() {
        if (isLoading) {
            Log.d(TAG, "Already loading conversations, skip");
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadConversationsError("Bạn cần đăng nhập để xem danh sách cuộc trò chuyện");
            }
            return;
        }

        isLoading = true;
        lastConversationTime = null; // Reset phân trang
        hasMoreConversations = true;

        if (isViewAttached()) {
            view.showLoadingConversations();
            view.hideEmptyState();
        }

        chatRepository.getConversations(currentUser.getUid(), DEFAULT_CONVERSATIONS_LIMIT, null,
            new RepositoryCallback<List<Conversation>>() {
                @Override
                public void onSuccess(List<Conversation> conversations) {
                    isLoading = false;

                    if (isViewAttached()) {
                        view.hideLoadingConversations();

                        if (conversations == null || conversations.isEmpty()) {
                            view.showEmptyState();
                            view.showLoadMoreButton(false);
                        } else {
                            view.hideEmptyState();
                            view.showConversations(conversations);

                            // Cập nhật state cho phân trang
                            conversationsList.clear();
                            conversationsList.addAll(conversations);

                            if (!conversations.isEmpty()) {
                                lastConversationTime = conversations.get(conversations.size() - 1).getLastMessageTime();
                            }

                            // Kiểm tra xem có thêm cuộc trò chuyện để load không
                            checkHasMoreConversations();
                        }
                    }
                }

                @Override
                public void onError(String error) {
                    isLoading = false;
                    Log.e(TAG, "Failed to load conversations: " + error);

                    if (isViewAttached()) {
                        view.hideLoadingConversations();
                        view.showLoadConversationsError(error);
                    }
                }
            });
    }

    @Override
    public void loadMoreConversations() {
        if (isLoadingMore || !hasMoreConversations) {
            Log.d(TAG, "Skip load more: isLoadingMore=" + isLoadingMore + ", hasMore=" + hasMoreConversations);
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadMoreError("Bạn cần đăng nhập để tải thêm cuộc trò chuyện");
            }
            return;
        }

        isLoadingMore = true;

        if (isViewAttached()) {
            view.showLoadingMore();
        }

        chatRepository.getConversations(currentUser.getUid(), LOAD_MORE_CONVERSATIONS_LIMIT, lastConversationTime,
            new RepositoryCallback<List<Conversation>>() {
                @Override
                public void onSuccess(List<Conversation> conversations) {
                    isLoadingMore = false;

                    if (isViewAttached()) {
                        view.hideLoadingMore();

                        if (conversations != null && !conversations.isEmpty()) {
                            view.addMoreConversations(conversations);

                            // Cập nhật state
                            conversationsList.addAll(conversations);
                            lastConversationTime = conversations.get(conversations.size() - 1).getLastMessageTime();

                            // Kiểm tra xem còn thêm cuộc trò chuyện không
                            checkHasMoreConversations();
                        } else {
                            // Không còn cuộc trò chuyện nào để tải
                            hasMoreConversations = false;
                            view.showLoadMoreButton(false);
                        }
                    }
                }

                @Override
                public void onError(String error) {
                    isLoadingMore = false;
                    Log.e(TAG, "Failed to load more conversations: " + error);

                    if (isViewAttached()) {
                        view.hideLoadingMore();
                        view.showLoadMoreError(error);
                    }
                }
            });
    }

    @Override
    public void refreshConversations() {
        // Reset tất cả state và tải lại từ đầu
        conversationsList.clear();
        lastConversationTime = null;
        hasMoreConversations = true;
        isLoading = false;
        isLoadingMore = false;

        loadConversations();
    }

    @Override
    public void onConversationClicked(Conversation conversation) {
        if (conversation == null || conversation.getId() == null) {
            if (isViewAttached()) {
                view.showError("Thông tin cuộc trò chuyện không hợp lệ");
            }
            return;
        }

        Log.d(TAG, "Conversation clicked: " + conversation.getId() + " - " + conversation.getTitle());

        if (isViewAttached()) {
            view.navigateToChatDetail(conversation.getId(), conversation.getTitle());
        }
    }

    @Override
    public void onNewChatClicked() {
        if (isViewAttached()) {
            view.navigateToNewChat();
        }
    }

    @Override
    public void onLoadMoreClicked() {
        if (!isLoadingMore && hasMoreConversations) {
            loadMoreConversations();
        }
    }

    // ========== CONTEXT MENU ACTIONS IMPLEMENTATION ==========

    @Override
    public void renameConversation(Conversation conversation, String newName) {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showError("Bạn cần đăng nhập để đổi tên cuộc trò chuyện");
            }
            return;
        }

        if (newName == null || newName.trim().isEmpty()) {
            if (isViewAttached()) {
                view.showError("Tên cuộc trò chuyện không được để trống");
            }
            return;
        }

        // Cập nhật tên cuộc trò chuyện
        conversation.setTitle(newName.trim());

        chatRepository.updateConversation(conversation, new RepositoryCallback<Conversation>() {
            @Override
            public void onSuccess(Conversation updatedConversation) {
                Log.d(TAG, "Conversation renamed successfully: " + updatedConversation.getTitle());

                if (isViewAttached()) {
                    view.showMessage("Đã đổi tên cuộc trò chuyện");
                    // Cập nhật trong danh sách local
                    updateConversationInList(updatedConversation);
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to rename conversation: " + error);
                if (isViewAttached()) {
                    view.showError("Không thể đổi tên cuộc trò chuyện: " + error);
                }
            }
        });
    }

    @Override
    public void togglePinConversation(Conversation conversation) {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showError("Bạn cần đăng nhập để ghim cuộc trò chuyện");
            }
            return;
        }

        // Toggle trạng thái pin
        boolean newPinState = !conversation.isPinned();
        conversation.setPinned(newPinState);

        chatRepository.updateConversation(conversation, new RepositoryCallback<Conversation>() {
            @Override
            public void onSuccess(Conversation updatedConversation) {
                String message = newPinState ? "Đã ghim cuộc trò chuyện" : "Đã bỏ ghim cuộc trò chuyện";
                Log.d(TAG, message + ": " + updatedConversation.getTitle());

                if (isViewAttached()) {
                    view.showMessage(message);
                    // Cập nhật trong danh sách local
                    updateConversationInList(updatedConversation);
                    // Có thể cần sắp xếp lại danh sách để đưa cuộc trò chuyện được ghim lên đầu
                    refreshConversations();
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to toggle pin conversation: " + error);
                // Revert lại trạng thái cũ
                conversation.setPinned(!newPinState);
                if (isViewAttached()) {
                    view.showError("Không thể cập nhật trạng thái ghim: " + error);
                }
            }
        });
    }

    @Override
    public void toggleMuteConversation(Conversation conversation) {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showError("Bạn cần đăng nhập để tắt/bật thông báo");
            }
            return;
        }

        // Toggle trạng thái mute
        boolean newMuteState = !conversation.isMuted();
        conversation.setMuted(newMuteState);

        chatRepository.updateConversation(conversation, new RepositoryCallback<Conversation>() {
            @Override
            public void onSuccess(Conversation updatedConversation) {
                String message = newMuteState ? "Đã tắt thông báo" : "Đã bật thông báo";
                Log.d(TAG, message + " cho cuộc trò chuyện: " + updatedConversation.getTitle());

                if (isViewAttached()) {
                    view.showMessage(message);
                    // Cập nhật trong danh sách local
                    updateConversationInList(updatedConversation);
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to toggle mute conversation: " + error);
                // Revert lại trạng thái cũ
                conversation.setMuted(!newMuteState);
                if (isViewAttached()) {
                    view.showError("Không thể cập nhật trạng thái thông báo: " + error);
                }
            }
        });
    }

    @Override
    public void deleteConversation(Conversation conversation) {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showError("Bạn cần đăng nhập để xóa cuộc trò chuyện");
            }
            return;
        }

        if (conversation.getId() == null) {
            if (isViewAttached()) {
                view.showError("Thông tin cuộc trò chuyện không hợp lệ");
            }
            return;
        }

        chatRepository.deleteConversation(conversation.getId(), new RepositoryCallback<Boolean>() {
            @Override
            public void onSuccess(Boolean success) {
                Log.d(TAG, "Conversation deleted successfully: " + conversation.getTitle());

                if (isViewAttached()) {
                    view.showMessage("Đã xóa cuộc trò chuyện");
                    // Xóa khỏi danh sách local
                    removeConversationFromList(conversation);

                    // Nếu danh sách trống, hiển thị empty state
                    if (conversationsList.isEmpty()) {
                        view.showEmptyState();
                    }
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to delete conversation: " + error);
                if (isViewAttached()) {
                    view.showError("Không thể xóa cuộc trò chuyện: " + error);
                }
            }
        });
    }

    @Override
    public void markConversationAsRead(Conversation conversation) {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showError("Bạn cần đăng nhập để đánh dấu đã đọc");
            }
            return;
        }

        // Đánh dấu đã đọc
        conversation.setRead(true);

        chatRepository.updateConversation(conversation, new RepositoryCallback<Conversation>() {
            @Override
            public void onSuccess(Conversation updatedConversation) {
                Log.d(TAG, "Conversation marked as read: " + updatedConversation.getTitle());

                if (isViewAttached()) {
                    view.showMessage("Đã đánh dấu là đã đọc");
                    // Cập nhật trong danh sách local
                    updateConversationInList(updatedConversation);
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to mark conversation as read: " + error);
                // Revert lại trạng thái cũ
                conversation.setRead(false);
                if (isViewAttached()) {
                    view.showError("Không thể đánh dấu đã đọc: " + error);
                }
            }
        });
    }

    // ========== HELPER METHODS ==========

    /**
     * Kiểm tra xem còn cuộc trò chuyện nào để load không
     */
    private void checkHasMoreConversations() {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null || lastConversationTime == null) {
            hasMoreConversations = false;
            if (isViewAttached()) {
                view.showLoadMoreButton(false);
            }
            return;
        }

        chatRepository.hasMoreConversations(currentUser.getUid(), lastConversationTime, new RepositoryCallback<Boolean>() {
            @Override
            public void onSuccess(Boolean hasMore) {
                hasMoreConversations = hasMore;
                if (isViewAttached()) {
                    view.showLoadMoreButton(hasMore);
                }
                Log.d(TAG, "Has more conversations: " + hasMore);
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Error checking more conversations: " + error);
                // Trong trường hợp lỗi, giả định không còn cuộc trò chuyện nào
                hasMoreConversations = false;
                if (isViewAttached()) {
                    view.showLoadMoreButton(false);
                }
            }
        });
    }

    /**
     * Cập nhật một cuộc trò chuyện trong danh sách local
     */
    private void updateConversationInList(Conversation updatedConversation) {
        for (int i = 0; i < conversationsList.size(); i++) {
            if (conversationsList.get(i).getId().equals(updatedConversation.getId())) {
                conversationsList.set(i, updatedConversation);
                if (isViewAttached()) {
                    view.showConversations(new ArrayList<>(conversationsList));
                }
                break;
            }
        }
    }

    /**
     * Xóa một cuộc trò chuyện khỏi danh sách local
     */
    private void removeConversationFromList(Conversation conversation) {
        conversationsList.removeIf(c -> c.getId().equals(conversation.getId()));
        if (isViewAttached()) {
            view.showConversations(new ArrayList<>(conversationsList));
        }
    }
}

package com.vhn.doan.presentation.chat;

import android.util.Log;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.vhn.doan.data.Conversation;
import com.vhn.doan.data.repository.ChatRepository;
import com.vhn.doan.data.repository.RepositoryCallback;

import java.util.ArrayList;
import java.util.List;

/**
 * Presenter cho ChatList feature - Quản lý danh sách cuộc trò chuyện
 */
public class ChatListPresenter implements ChatListContract.Presenter {

    private static final String TAG = "ChatListPresenter";
    private static final int DEFAULT_CONVERSATIONS_LIMIT = 8;
    private static final int LOAD_MORE_CONVERSATIONS_LIMIT = 3;

    private final ChatRepository chatRepository;
    private final FirebaseAuth firebaseAuth;
    private ChatListContract.View view;

    // State management
    private List<Conversation> conversationsList;
    private Long lastConversationTime;
    private boolean isLoading = false;
    private boolean isLoadingMore = false;
    private boolean hasMoreConversations = true;

    public ChatListPresenter(ChatRepository chatRepository) {
        this.chatRepository = chatRepository;
        this.firebaseAuth = FirebaseAuth.getInstance();
        this.conversationsList = new ArrayList<>();
    }

    @Override
    public void attachView(ChatListContract.View view) {
        this.view = view;
        // Tự động tải danh sách cuộc trò chuyện khi attach view
        loadConversations();
    }

    @Override
    public void detachView() {
        this.view = null;
    }

    private boolean isViewAttached() {
        return view != null;
    }

    @Override
    public void loadConversations() {
        if (isLoading) {
            Log.d(TAG, "Already loading conversations, skip");
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadConversationsError("Bạn cần đăng nhập để xem danh sách cuộc trò chuyện");
            }
            return;
        }

        isLoading = true;
        lastConversationTime = null; // Reset phân trang
        hasMoreConversations = true;

        if (isViewAttached()) {
            view.showLoadingConversations();
            view.hideEmptyState();
        }

        chatRepository.getConversations(currentUser.getUid(), DEFAULT_CONVERSATIONS_LIMIT, null,
            new RepositoryCallback<List<Conversation>>() {
                @Override
                public void onSuccess(List<Conversation> conversations) {
                    isLoading = false;

                    if (isViewAttached()) {
                        view.hideLoadingConversations();

                        if (conversations == null || conversations.isEmpty()) {
                            view.showEmptyState();
                            view.showLoadMoreButton(false);
                        } else {
                            view.hideEmptyState();
                            view.showConversations(conversations);

                            // Cập nhật state cho phân trang
                            conversationsList.clear();
                            conversationsList.addAll(conversations);

                            if (!conversations.isEmpty()) {
                                lastConversationTime = conversations.get(conversations.size() - 1).getLastMessageTime();
                            }

                            // Kiểm tra xem có thêm cuộc trò chuyện để load không
                            checkHasMoreConversations();
                        }
                    }
                }

                @Override
                public void onError(String error) {
                    isLoading = false;
                    Log.e(TAG, "Failed to load conversations: " + error);

                    if (isViewAttached()) {
                        view.hideLoadingConversations();
                        view.showLoadConversationsError(error);
                    }
                }
            });
    }

    @Override
    public void loadMoreConversations() {
        if (isLoadingMore || !hasMoreConversations) {
            Log.d(TAG, "Skip load more: isLoadingMore=" + isLoadingMore + ", hasMore=" + hasMoreConversations);
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadMoreError("Bạn cần đăng nhập để tải thêm cuộc trò chuyện");
            }
            return;
        }

        isLoadingMore = true;

        if (isViewAttached()) {
            view.showLoadingMore();
        }

        chatRepository.getConversations(currentUser.getUid(), LOAD_MORE_CONVERSATIONS_LIMIT, lastConversationTime,
            new RepositoryCallback<List<Conversation>>() {
                @Override
                public void onSuccess(List<Conversation> conversations) {
                    isLoadingMore = false;

                    if (isViewAttached()) {
                        view.hideLoadingMore();

                        if (conversations != null && !conversations.isEmpty()) {
                            view.addMoreConversations(conversations);

                            // Cập nhật state
                            conversationsList.addAll(conversations);
                            lastConversationTime = conversations.get(conversations.size() - 1).getLastMessageTime();

                            // Kiểm tra xem còn thêm cuộc trò chuyện không
                            checkHasMoreConversations();
                        } else {
                            // Không còn cuộc trò chuyện nào để tải
                            hasMoreConversations = false;
                            view.showLoadMoreButton(false);
                        }
                    }
                }

                @Override
                public void onError(String error) {
                    isLoadingMore = false;
                    Log.e(TAG, "Failed to load more conversations: " + error);

                    if (isViewAttached()) {
                        view.hideLoadingMore();
                        view.showLoadMoreError(error);
                    }
                }
            });
    }

    @Override
    public void refreshConversations() {
        // Reset tất cả state và tải lại từ đầu
        conversationsList.clear();
        lastConversationTime = null;
        hasMoreConversations = true;
        isLoading = false;
        isLoadingMore = false;

        loadConversations();
    }

    @Override
    public void onConversationClicked(Conversation conversation) {
        if (conversation == null || conversation.getId() == null) {
            if (isViewAttached()) {
                view.showError("Thông tin cuộc trò chuyện không hợp lệ");
            }
            return;
        }

        Log.d(TAG, "Conversation clicked: " + conversation.getId() + " - " + conversation.getTitle());

        if (isViewAttached()) {
            view.navigateToChatDetail(conversation.getId(), conversation.getTitle());
        }
    }

    @Override
    public void onNewChatClicked() {
        if (isViewAttached()) {
            view.navigateToNewChat();
        }
    }

    @Override
    public void onLoadMoreClicked() {
        if (!isLoadingMore && hasMoreConversations) {
            loadMoreConversations();
        }
    }

    // ========== CONTEXT MENU ACTIONS IMPLEMENTATION ==========

    @Override
    public void renameConversation(Conversation conversation, String newName) {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showError("Bạn cần đăng nhập để đổi tên cuộc trò chuyện");
            }
            return;
        }

        if (newName == null || newName.trim().isEmpty()) {
            if (isViewAttached()) {
                view.showError("Tên cuộc trò chuyện không được để trống");
            }
            return;
        }

        // Cập nhật tên cuộc trò chuyện
        conversation.setTitle(newName.trim());

        chatRepository.updateConversation(conversation, new RepositoryCallback<Conversation>() {
            @Override
            public void onSuccess(Conversation updatedConversation) {
                Log.d(TAG, "Conversation renamed successfully: " + updatedConversation.getTitle());

                if (isViewAttached()) {
                    view.showMessage("Đã đổi tên cuộc trò chuyện");
                    // Cập nhật trong danh sách local
                    updateConversationInList(updatedConversation);
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to rename conversation: " + error);
                if (isViewAttached()) {
                    view.showError("Không thể đổi tên cuộc trò chuyện: " + error);
                }
            }
        });
    }

    @Override
    public void togglePinConversation(Conversation conversation) {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showError("Bạn cần đăng nhập để ghim cuộc trò chuyện");
            }
            return;
        }

        // Nếu đang ghim và hiện tại chưa được ghim
        if (!conversation.isPinned()) {
            // Kiểm tra số lượng cuộc trò chuyện đã được ghim
            long pinnedCount = conversationsList.stream().filter(Conversation::isPinned).count();
            if (pinnedCount >= 5) {
                if (isViewAttached()) {
                    view.showError("Bạn chỉ có thể ghim tối đa 5 cuộc trò chuyện");
                }
                return;
            }
        }

        // Toggle trạng thái pin
        boolean newPinState = !conversation.isPinned();
        conversation.setPinned(newPinState);

        chatRepository.updateConversation(conversation, new RepositoryCallback<Conversation>() {
            @Override
            public void onSuccess(Conversation updatedConversation) {
                String message = newPinState ? "Đã ghim cuộc trò chuyện" : "Đã bỏ ghim cuộc trò chuyện";
                Log.d(TAG, message + ": " + updatedConversation.getTitle());

                if (isViewAttached()) {
                    view.showMessage(message);
                    // Cập nhật trong danh sách local
                    updateConversationInList(updatedConversation);
                    // Sắp xếp lại danh sách để đưa cuộc trò chuyện được ghim lên đầu
                    sortConversationsWithPinnedFirst();
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to toggle pin conversation: " + error);
                // Revert lại trạng thái cũ
                conversation.setPinned(!newPinState);
                if (isViewAttached()) {
                    view.showError("Không thể cập nhật trạng thái ghim: " + error);
                }
            }
        });
    }

    @Override
    public void toggleMuteConversation(Conversation conversation) {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showError("Bạn cần đăng nhập để tắt/bật thông báo");
            }
            return;
        }

        // Toggle trạng thái mute
        boolean newMuteState = !conversation.isMuted();
        conversation.setMuted(newMuteState);

        chatRepository.updateConversation(conversation, new RepositoryCallback<Conversation>() {
            @Override
            public void onSuccess(Conversation updatedConversation) {
                String message = newMuteState ? "Đã tắt thông báo" : "Đã bật thông báo";
                Log.d(TAG, message + " cho cuộc trò chuyện: " + updatedConversation.getTitle());

                if (isViewAttached()) {
                    view.showMessage(message);
                    // Cập nhật trong danh sách local
                    updateConversationInList(updatedConversation);
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to toggle mute conversation: " + error);
                // Revert lại trạng thái cũ
                conversation.setMuted(!newMuteState);
                if (isViewAttached()) {
                    view.showError("Không thể cập nhật trạng thái thông báo: " + error);
                }
            }
        });
    }

    @Override
    public void deleteConversation(Conversation conversation) {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showError("Bạn cần đăng nhập để xóa cuộc trò chuyện");
            }
            return;
        }

        if (conversation.getId() == null) {
            if (isViewAttached()) {
                view.showError("Thông tin cuộc trò chuyện không hợp lệ");
            }
            return;
        }

        chatRepository.deleteConversation(conversation.getId(), new RepositoryCallback<Boolean>() {
            @Override
            public void onSuccess(Boolean success) {
                Log.d(TAG, "Conversation deleted successfully: " + conversation.getTitle());

                if (isViewAttached()) {
                    view.showMessage("Đã xóa cuộc trò chuyện");
                    // Xóa khỏi danh sách local
                    removeConversationFromList(conversation);

                    // Nếu danh sách trống, hiển thị empty state
                    if (conversationsList.isEmpty()) {
                        view.showEmptyState();
                    }
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to delete conversation: " + error);
                if (isViewAttached()) {
                    view.showError("Không thể xóa cuộc trò chuyện: " + error);
                }
            }
        });
    }

    @Override
    public void markConversationAsRead(Conversation conversation) {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showError("Bạn cần đăng nhập để đánh dấu đã đọc");
            }
            return;
        }

        // Đánh dấu đã đọc
        conversation.setRead(true);

        chatRepository.updateConversation(conversation, new RepositoryCallback<Conversation>() {
            @Override
            public void onSuccess(Conversation updatedConversation) {
                Log.d(TAG, "Conversation marked as read: " + updatedConversation.getTitle());

                if (isViewAttached()) {
                    view.showMessage("Đã đánh dấu là đã đọc");
                    // Cập nhật trong danh sách local
                    updateConversationInList(updatedConversation);
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to mark conversation as read: " + error);
                // Revert lại trạng thái cũ
                conversation.setRead(false);
                if (isViewAttached()) {
                    view.showError("Không thể đánh dấu đã đọc: " + error);
                }
            }
        });
    }

    // ========== HELPER METHODS ==========

    /**
     * Kiểm tra xem còn cuộc trò chuyện nào để load không
     */
    private void checkHasMoreConversations() {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null || lastConversationTime == null) {
            hasMoreConversations = false;
            if (isViewAttached()) {
                view.showLoadMoreButton(false);
            }
            return;
        }

        chatRepository.hasMoreConversations(currentUser.getUid(), lastConversationTime, new RepositoryCallback<Boolean>() {
            @Override
            public void onSuccess(Boolean hasMore) {
                hasMoreConversations = hasMore;
                if (isViewAttached()) {
                    view.showLoadMoreButton(hasMore);
                }
                Log.d(TAG, "Has more conversations: " + hasMore);
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Error checking more conversations: " + error);
                // Trong trường hợp lỗi, giả định không còn cuộc trò chuyện nào
                hasMoreConversations = false;
                if (isViewAttached()) {
                    view.showLoadMoreButton(false);
                }
            }
        });
    }

    /**
     * Cập nhật một cuộc trò chuyện trong danh sách local
     */
    private void updateConversationInList(Conversation updatedConversation) {
        for (int i = 0; i < conversationsList.size(); i++) {
            if (conversationsList.get(i).getId().equals(updatedConversation.getId())) {
                conversationsList.set(i, updatedConversation);
                if (isViewAttached()) {
                    view.showConversations(new ArrayList<>(conversationsList));
                }
                break;
            }
        }
    }

    /**
     * Xóa một cuộc trò chuyện khỏi danh sách local
     */
    private void removeConversationFromList(Conversation conversation) {
        conversationsList.removeIf(c -> c.getId().equals(conversation.getId()));
        if (isViewAttached()) {
            view.showConversations(new ArrayList<>(conversationsList));
        }
    }

    /**
     * Sắp xếp danh sách cuộc trò chuyện với các cuộc trò chuyện được ghim lên đầu
     */
    private void sortConversationsWithPinnedFirst() {
        conversationsList.sort((c1, c2) -> {
            if (c1.isPinned() && !c2.isPinned()) {
                return -1;
            } else if (!c1.isPinned() && c2.isPinned()) {
                return 1;
            } else {
                return 0;
            }
        });

        if (isViewAttached()) {
            view.showConversations(new ArrayList<>(conversationsList));
        }
    }
}

package com.vhn.doan.presentation.chat;

import android.util.Log;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.repository.ChatRepository;
import com.vhn.doan.data.repository.RepositoryCallback;

import java.util.List;

/**
 * Presenter cho Chat feature theo kiến trúc MVP
 */
public class ChatPresenter implements ChatContract.Presenter {

    private static final String TAG = "ChatPresenter";

    private final ChatRepository chatRepository;
    private final FirebaseAuth firebaseAuth;
    private ChatContract.View view;

    public ChatPresenter(ChatRepository chatRepository) {
        this.chatRepository = chatRepository;
        this.firebaseAuth = FirebaseAuth.getInstance();
    }

    @Override
    public void attachView(ChatContract.View view) {
        this.view = view;
        // Tự động tải tin nhắn khi attach view
        loadMessages();
    }

    @Override
    public void detachView() {
        this.view = null;
    }

    private boolean isViewAttached() {
        return view != null;
    }

    @Override
    public void loadMessages() {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadMessagesError("Bạn cần đăng nhập để sử dụng tính năng chat");
            }
            return;
        }

        if (isViewAttached()) {
            view.showLoadingMessages();
        }

        chatRepository.getChatMessages(currentUser.getUid(), new RepositoryCallback<List<ChatMessage>>() {
            @Override
            public void onSuccess(List<ChatMessage> messages) {
                if (isViewAttached()) {
                    view.hideLoadingMessages();
                    view.showMessages(messages);

                    if (!messages.isEmpty()) {
                        view.scrollToLatestMessage();
                    }
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to load messages: " + error);
                if (isViewAttached()) {
                    view.hideLoadingMessages();
                    view.showLoadMessagesError(error);
                }
            }
        });
    }

    @Override
    public void sendMessage(String content) {
        if (content == null || content.trim().isEmpty()) {
            if (isViewAttached()) {
                view.showSendMessageError("Vui lòng nhập nội dung tin nhắn");
            }
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showSendMessageError("Bạn cần đăng nhập để gửi tin nhắn");
            }
            return;
        }

        String trimmedContent = content.trim();
        long timestamp = System.currentTimeMillis();
        String userId = currentUser.getUid();

        // Hiển thị tin nhắn của người dùng ngay lập tức
        ChatMessage userMessage = new ChatMessage(userId, trimmedContent, true, timestamp);
        String topic = chatRepository.extractTopic(trimmedContent);
        userMessage.setTopic(topic);

        if (isViewAttached()) {
            view.addMessage(userMessage);
            view.clearMessageInput();
            view.scrollToLatestMessage();
            view.showSendingMessage();
        }

        // Lưu tin nhắn người dùng vào Firebase
        chatRepository.saveChatMessage(userMessage, new RepositoryCallback<ChatMessage>() {
            @Override
            public void onSuccess(ChatMessage savedMessage) {
                Log.d(TAG, "User message saved successfully");
                if (isViewAttached()) {
                    view.hideSendingMessage();
                    view.showAiTyping();
                }

                // Gửi tin nhắn tới AI
                chatRepository.sendMessageToAI(trimmedContent, new RepositoryCallback<String>() {
                    @Override
                    public void onSuccess(String aiResponse) {
                        Log.d(TAG, "AI response received: " + aiResponse);

                        // Tạo tin nhắn phản hồi từ AI
                        ChatMessage aiMessage = new ChatMessage(conversationId, userId, aiResponse, false, System.currentTimeMillis());
                        ChatMessage aiMessage = new ChatMessage(userId, aiResponse, false, System.currentTimeMillis());

                        if (isViewAttached()) {
                            view.hideAiTyping();
                            view.addMessage(aiMessage);
                            view.scrollToLatestMessage();
                        }

                        // Lưu phản hồi AI vào Firebase
                        chatRepository.saveChatMessage(aiMessage, new RepositoryCallback<ChatMessage>() {
                            @Override
                            public void onSuccess(ChatMessage savedAiMessage) {
                                Log.d(TAG, "AI message saved successfully");
                            }

                            @Override
                            public void onError(String error) {
                                Log.e(TAG, "Failed to save AI message: " + error);
                                // Không hiển thị lỗi cho người dùng vì tin nhắn đã được hiển thị
                            }
                        });
                    }

                    @Override
                    public void onError(String error) {
                        Log.e(TAG, "Failed to get AI response: " + error);

                        if (isViewAttached()) {
                            view.hideAiTyping();

                            // Hiển thị tin nhắn lỗi từ AI
                            ChatMessage errorMessage = new ChatMessage(conversationId, userId,
                            ChatMessage errorMessage = new ChatMessage(userId,
                                "Xin lỗi, tôi không thể trả lời câu hỏi của bạn lúc này. Vui lòng thử lại sau.",
                            view.addMessage(errorMessage);
                            view.scrollToLatestMessage();
                            view.showSendMessageError("Không thể nhận phản hồi từ AI: " + error);
                        }
                    }
                });
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to save user message: " + error);
                if (isViewAttached()) {
                    view.hideSendingMessage();
                    view.showSendMessageError("Không thể lưu tin nhắn: " + error);
                }
            }
        });
    }

    @Override
    public void clearChatHistory() {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadMessagesError("Bạn cần đăng nhập để xóa lịch sử chat");
            }
            return;
        }

        chatRepository.clearChatHistory(currentUser.getUid(), new RepositoryCallback<Boolean>() {
            @Override
            public void onSuccess(Boolean success) {
                Log.d(TAG, "Chat history cleared successfully");
                if (isViewAttached()) {
                    view.showMessage("Đã xóa lịch sử chat thành công");
                    // Tải lại danh sách tin nhắn (sẽ rỗng)
                    loadMessages();
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to clear chat history: " + error);
                if (isViewAttached()) {
                    view.showError("Không thể xóa lịch sử chat: " + error);
                }
            }
        });
    }

    @Override
    public void refreshMessages() {
        loadMessages();
    }
}

package com.vhn.doan.presentation.chat;

import android.util.Log;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.repository.ChatRepository;
import com.vhn.doan.data.repository.RepositoryCallback;

import java.util.List;

/**
 * Presenter cho Chat feature theo kiến trúc MVP
 */
public class ChatPresenter implements ChatContract.Presenter {

    private static final String TAG = "ChatPresenter";

    private final ChatRepository chatRepository;
    private final FirebaseAuth firebaseAuth;
    private ChatContract.View view;

    public ChatPresenter(ChatRepository chatRepository) {
        this.chatRepository = chatRepository;
        this.firebaseAuth = FirebaseAuth.getInstance();
    }

    @Override
    public void attachView(ChatContract.View view) {
        this.view = view;
        // Tự động tải tin nhắn khi attach view
        loadMessages();
    }

    @Override
    public void detachView() {
        this.view = null;
    }

    private boolean isViewAttached() {
        return view != null;
    }

    @Override
    public void loadMessages() {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadMessagesError("Bạn cần đăng nhập để sử dụng tính năng chat");
            }
            return;
        }

        if (isViewAttached()) {
            view.showLoadingMessages();
        }

        chatRepository.getChatMessages(currentUser.getUid(), new RepositoryCallback<List<ChatMessage>>() {
            @Override
            public void onSuccess(List<ChatMessage> messages) {
                if (isViewAttached()) {
                    view.hideLoadingMessages();
                    view.showMessages(messages);

                    if (!messages.isEmpty()) {
                        view.scrollToLatestMessage();
                    }
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to load messages: " + error);
                if (isViewAttached()) {
                    view.hideLoadingMessages();
                    view.showLoadMessagesError(error);
                }
            }
        });
    }

    @Override
    public void sendMessage(String content) {
        if (content == null || content.trim().isEmpty()) {
            if (isViewAttached()) {
                view.showSendMessageError("Vui lòng nhập nội dung tin nhắn");
            }
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showSendMessageError("Bạn cần đăng nhập để gửi tin nhắn");
            }
            return;
        }

        String trimmedContent = content.trim();
        long timestamp = System.currentTimeMillis();
        String userId = currentUser.getUid();
        
        // Tạo conversationId tạm thời hoặc sử dụng conversationId hiện tại
        String conversationId = "temp_" + userId + "_" + timestamp; // Tạo conversationId tạm thời

        // Hiển thị tin nhắn của người dùng ngay lập tức
        ChatMessage userMessage = new ChatMessage(conversationId, userId, trimmedContent, true, timestamp);
        String topic = chatRepository.extractTopic(trimmedContent);
        userMessage.setTopic(topic);

        if (isViewAttached()) {
            view.addMessage(userMessage);
            view.clearMessageInput();
            view.scrollToLatestMessage();
            view.showSendingMessage();
        }

        // Lưu tin nhắn người dùng vào Firebase
        chatRepository.saveChatMessage(userMessage, new RepositoryCallback<ChatMessage>() {
            @Override
            public void onSuccess(ChatMessage savedMessage) {
                Log.d(TAG, "User message saved successfully");
                if (isViewAttached()) {
                    view.hideSendingMessage();
                    view.showAiTyping();
                }

                // Gửi tin nhắn tới AI
                chatRepository.sendMessageToAI(trimmedContent, new RepositoryCallback<String>() {
                    @Override
                    public void onSuccess(String aiResponse) {
                        Log.d(TAG, "AI response received: " + aiResponse);

                        // Tạo tin nhắn phản hồi từ AI
                        ChatMessage aiMessage = new ChatMessage(conversationId, userId, aiResponse, false, System.currentTimeMillis());
                        aiMessage.setTopic(topic);

                        if (isViewAttached()) {
                            view.hideAiTyping();
                            view.addMessage(aiMessage);
                            view.scrollToLatestMessage();
                        }

                        // Lưu phản hồi AI vào Firebase
                        chatRepository.saveChatMessage(aiMessage, new RepositoryCallback<ChatMessage>() {
                            @Override
                            public void onSuccess(ChatMessage savedAiMessage) {
                                Log.d(TAG, "AI message saved successfully");
                            }

                            @Override
                            public void onError(String error) {
                                Log.e(TAG, "Failed to save AI message: " + error);
                                // Không hiển thị lỗi cho người dùng vì tin nhắn đã được hiển thị
                            }
                        });
                    }

                    @Override
                    public void onError(String error) {
                        Log.e(TAG, "Failed to get AI response: " + error);

                        if (isViewAttached()) {
                            view.hideAiTyping();

                            // Hiển thị tin nhắn lỗi từ AI
                            ChatMessage errorMessage = new ChatMessage(conversationId, userId,
                                "Xin lỗi, tôi không thể trả lời câu hỏi của bạn lúc này. Vui lòng thử lại sau.",
                                false, System.currentTimeMillis());
                            view.addMessage(errorMessage);
                            view.scrollToLatestMessage();
                            view.showSendMessageError("Không thể nhận phản hồi từ AI: " + error);
                        }
                    }
                });
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to save user message: " + error);
                if (isViewAttached()) {
                    view.hideSendingMessage();
                    view.showSendMessageError("Không thể lưu tin nhắn: " + error);
                }
            }
        });
    }

    @Override
    public void clearChatHistory() {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadMessagesError("Bạn cần đăng nhập để xóa lịch sử chat");
            }
            return;
        }

        chatRepository.clearChatHistory(currentUser.getUid(), new RepositoryCallback<Boolean>() {
            @Override
            public void onSuccess(Boolean success) {
                Log.d(TAG, "Chat history cleared successfully");
                if (isViewAttached()) {
                    view.showMessage("Đã xóa lịch sử chat thành công");
                    // Tải lại danh sách tin nhắn (sẽ rỗng)
                    loadMessages();
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to clear chat history: " + error);
                if (isViewAttached()) {
                    view.showError("Không thể xóa lịch sử chat: " + error);
                }
            }
        });
    }

    @Override
    public void refreshMessages() {
        loadMessages();
    }
}

package com.vhn.doan.presentation.chat;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.google.android.material.card.MaterialCardView;
import com.vhn.doan.R;
import com.vhn.doan.data.Conversation;

import java.text.SimpleDateFormat;
import java.util.ArrayList;
import java.util.Date;
import java.util.List;
import java.util.Locale;

/**
 * Adapter cho danh sách các cuộc trò chuyện chat
 */
public class ConversationAdapter extends RecyclerView.Adapter<ConversationAdapter.ConversationViewHolder> {

    private List<Conversation> conversations;
    private OnConversationClickListener onConversationClickListener;
    private OnConversationLongClickListener onConversationLongClickListener;
    private final SimpleDateFormat timeFormat;
    private final SimpleDateFormat dateFormat;

    public interface OnConversationClickListener {
        void onConversationClick(Conversation conversation);
    }

    public interface OnConversationLongClickListener {
        void onConversationLongClick(Conversation conversation, View view);
    }

    public ConversationAdapter() {
        this.conversations = new ArrayList<>();
        this.timeFormat = new SimpleDateFormat("HH:mm", Locale.getDefault());
        this.dateFormat = new SimpleDateFormat("dd/MM/yyyy", Locale.getDefault());
    }

    public void setOnConversationClickListener(OnConversationClickListener listener) {
        this.onConversationClickListener = listener;
    }

    public void setOnConversationLongClickListener(OnConversationLongClickListener listener) {
        this.onConversationLongClickListener = listener;
    }

    @NonNull
    @Override
    public ConversationViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_conversation, parent, false);
        return new ConversationViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull ConversationViewHolder holder, int position) {
        Conversation conversation = conversations.get(position);
        holder.bind(conversation);
    }

    @Override
    public int getItemCount() {
        return conversations.size();
    }

    /**
     * Cập nhật danh sách cuộc trò chuyện (reset toàn bộ)
     */
    public void setConversations(List<Conversation> conversations) {
        this.conversations.clear();
        if (conversations != null) {
            this.conversations.addAll(conversations);
        }
        notifyDataSetChanged();
    }

    /**
     * Thêm danh sách cuộc trò chuyện mới vào cuối (cho load more)
     */
    public void addConversations(List<Conversation> conversations) {
        if (conversations != null && !conversations.isEmpty()) {
            int startPosition = this.conversations.size();
            this.conversations.addAll(conversations);
            notifyItemRangeInserted(startPosition, conversations.size());
        }
    }

    /**
     * Xóa tất cả cuộc trò chuyện
     */
    public void clearConversations() {
        int size = conversations.size();
        conversations.clear();
        notifyItemRangeRemoved(0, size);
    }

    /**
     * Cập nhật một cuộc trò chuyện cụ thể
     */
    public void updateConversation(Conversation updatedConversation) {
        for (int i = 0; i < conversations.size(); i++) {
            if (conversations.get(i).getId().equals(updatedConversation.getId())) {
                conversations.set(i, updatedConversation);
                notifyItemChanged(i);
                break;
            }
        }
    }

    class ConversationViewHolder extends RecyclerView.ViewHolder {
        private TextView titleText;
        private TextView lastMessageText;
        private TextView timeText;
        private TextView messageCountText;
        private View unreadIndicator;
        private ImageView pinIndicator;
        private MaterialCardView cardView;

        public ConversationViewHolder(@NonNull View itemView) {
            super(itemView);
            titleText = itemView.findViewById(R.id.tv_conversation_title);
            lastMessageText = itemView.findViewById(R.id.tv_last_message);
            timeText = itemView.findViewById(R.id.tv_conversation_time);
            messageCountText = itemView.findViewById(R.id.tv_message_count);
            unreadIndicator = itemView.findViewById(R.id.view_unread_indicator);
            pinIndicator = itemView.findViewById(R.id.iv_pin_indicator);
            cardView = (MaterialCardView) itemView;

            itemView.setOnClickListener(v -> {
                int position = getAdapterPosition();
                if (position != RecyclerView.NO_POSITION && onConversationClickListener != null) {
                    onConversationClickListener.onConversationClick(conversations.get(position));
                }
            });

            itemView.setOnLongClickListener(v -> {
                int position = getAdapterPosition();
                if (position != RecyclerView.NO_POSITION && onConversationLongClickListener != null) {
                    onConversationLongClickListener.onConversationLongClick(conversations.get(position), v);
                    return true;
                }
                return false;
            });
        }

        public void bind(Conversation conversation) {
            // Tiêu đề cuộc trò chuyện
            titleText.setText(conversation.getTitle() != null ? conversation.getTitle() : "Cuộc trò chuyện");

            // Tin nhắn cuối cùng
            if (conversation.getLastMessage() != null && !conversation.getLastMessage().isEmpty()) {
                String lastMessage = conversation.getLastMessage();
                // Giới hạn độ dài tin nhắn hiển thị
                if (lastMessage.length() > 50) {
                    lastMessage = lastMessage.substring(0, 47) + "...";
                }

                // Thêm prefix để biết tin nhắn từ ai
                String prefix = conversation.isFromUser() ? "Bạn: " : "AI: ";
                lastMessageText.setText(prefix + lastMessage);
                lastMessageText.setVisibility(View.VISIBLE);
            } else {
                lastMessageText.setText("Chưa có tin nhắn");
                lastMessageText.setVisibility(View.VISIBLE);
            }

            // Thời gian
            String timeStr = formatTime(conversation.getLastMessageTime());
            timeText.setText(timeStr);

            // Số lượng tin nhắn
            int count = conversation.getMessageCount();
            if (count > 0) {
                messageCountText.setText(String.valueOf(count));
                messageCountText.setVisibility(View.VISIBLE);
            } else {
                messageCountText.setVisibility(View.GONE);
            }

            // Hiển thị icon ghim và thay đổi màu nền
            if (conversation.isPinned()) {
                pinIndicator.setVisibility(View.VISIBLE);
                // Thay đổi màu nền cho cuộc trò chuyện được ghim
                cardView.setCardBackgroundColor(itemView.getContext().getResources().getColor(R.color.pinned_conversation_background, itemView.getContext().getTheme()));
                cardView.setStrokeColor(itemView.getContext().getResources().getColor(R.color.pinned_conversation_stroke, itemView.getContext().getTheme()));
                cardView.setStrokeWidth(2);
            } else {
                pinIndicator.setVisibility(View.GONE);
                // Màu nền bình thường
                cardView.setCardBackgroundColor(itemView.getContext().getResources().getColor(android.R.attr.colorSurface, itemView.getContext().getTheme()));
                cardView.setStrokeWidth(0);
            }

            // Unread indicator (tạm thời ẩn, có thể implement sau)
            unreadIndicator.setVisibility(View.GONE);
        }

        /**
         * Format thời gian hiển thị
         */
        private String formatTime(long timestamp) {
            if (timestamp <= 0) {
                return "";
            }

            try {
                Date messageDate = new Date(timestamp);
                Date currentDate = new Date();

                // Tính số ngày chênh lệch
                long diffInMillies = currentDate.getTime() - messageDate.getTime();
                long diffInDays = diffInMillies / (24 * 60 * 60 * 1000);

                if (diffInDays == 0) {
                    // Hôm nay - hiển thị giờ
                    return timeFormat.format(messageDate);
                } else if (diffInDays == 1) {
                    // Hôm qua
                    return "Hôm qua";
                } else if (diffInDays < 7) {
                    // Trong tuần - hiển thị số ngày
                    return diffInDays + " ngày trước";
                } else {
                    // Lâu hơn - hiển thị ngày tháng
                    return dateFormat.format(messageDate);
                }
            } catch (Exception e) {
                return "";
            }
        }
    }
}

package com.vhn.doan.presentation.chat;

import android.util.TypedValue;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.google.android.material.card.MaterialCardView;
import com.vhn.doan.R;
import com.vhn.doan.data.Conversation;

import java.text.SimpleDateFormat;
import java.util.ArrayList;
import java.util.Date;
import java.util.List;
import java.util.Locale;

/**
 * Adapter cho danh sách các cuộc trò chuyện chat
 */
public class ConversationAdapter extends RecyclerView.Adapter<ConversationAdapter.ConversationViewHolder> {

    private List<Conversation> conversations;
    private OnConversationClickListener onConversationClickListener;
    private OnConversationLongClickListener onConversationLongClickListener;
    private final SimpleDateFormat timeFormat;
    private final SimpleDateFormat dateFormat;

    public interface OnConversationClickListener {
        void onConversationClick(Conversation conversation);
    }

    public interface OnConversationLongClickListener {
        void onConversationLongClick(Conversation conversation, View view);
    }

    public ConversationAdapter() {
        this.conversations = new ArrayList<>();
        this.timeFormat = new SimpleDateFormat("HH:mm", Locale.getDefault());
        this.dateFormat = new SimpleDateFormat("dd/MM/yyyy", Locale.getDefault());
    }

    public void setOnConversationClickListener(OnConversationClickListener listener) {
        this.onConversationClickListener = listener;
    }

    public void setOnConversationLongClickListener(OnConversationLongClickListener listener) {
        this.onConversationLongClickListener = listener;
    }

    @NonNull
    @Override
    public ConversationViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_conversation, parent, false);
        return new ConversationViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull ConversationViewHolder holder, int position) {
        Conversation conversation = conversations.get(position);
        holder.bind(conversation);
    }

    @Override
    public int getItemCount() {
        return conversations.size();
    }

    /**
     * Cập nhật danh sách cuộc trò chuyện (reset toàn bộ)
     */
    public void setConversations(List<Conversation> conversations) {
        this.conversations.clear();
        if (conversations != null) {
            this.conversations.addAll(conversations);
        }
        notifyDataSetChanged();
    }

    /**
     * Thêm danh sách cuộc trò chuyện mới vào cuối (cho load more)
     */
    public void addConversations(List<Conversation> conversations) {
        if (conversations != null && !conversations.isEmpty()) {
            int startPosition = this.conversations.size();
            this.conversations.addAll(conversations);
            notifyItemRangeInserted(startPosition, conversations.size());
        }
    }

    /**
     * Xóa tất cả cuộc trò chuyện
     */
    public void clearConversations() {
        int size = conversations.size();
        conversations.clear();
        notifyItemRangeRemoved(0, size);
    }

    /**
     * Cập nhật một cuộc trò chuyện cụ thể
     */
    public void updateConversation(Conversation updatedConversation) {
        for (int i = 0; i < conversations.size(); i++) {
            if (conversations.get(i).getId().equals(updatedConversation.getId())) {
                conversations.set(i, updatedConversation);
                notifyItemChanged(i);
                break;
            }
        }
    }

    class ConversationViewHolder extends RecyclerView.ViewHolder {
        private TextView titleText;
        private TextView lastMessageText;
        private TextView timeText;
        private TextView messageCountText;
        private View unreadIndicator;
        private ImageView pinIndicator;
        private MaterialCardView cardView;

        public ConversationViewHolder(@NonNull View itemView) {
            super(itemView);
            titleText = itemView.findViewById(R.id.tv_conversation_title);
            lastMessageText = itemView.findViewById(R.id.tv_last_message);
            timeText = itemView.findViewById(R.id.tv_conversation_time);
            messageCountText = itemView.findViewById(R.id.tv_message_count);
            unreadIndicator = itemView.findViewById(R.id.view_unread_indicator);
            pinIndicator = itemView.findViewById(R.id.iv_pin_indicator);
            cardView = (MaterialCardView) itemView;

            itemView.setOnClickListener(v -> {
                int position = getAdapterPosition();
                if (position != RecyclerView.NO_POSITION && onConversationClickListener != null) {
                    onConversationClickListener.onConversationClick(conversations.get(position));
                }
            });

            itemView.setOnLongClickListener(v -> {
                int position = getAdapterPosition();
                if (position != RecyclerView.NO_POSITION && onConversationLongClickListener != null) {
                    onConversationLongClickListener.onConversationLongClick(conversations.get(position), v);
                    return true;
                }
                return false;
            });
        }

        public void bind(Conversation conversation) {
            // Tiêu đề cuộc trò chuyện
            titleText.setText(conversation.getTitle() != null ? conversation.getTitle() : "Cuộc trò chuyện");

            // Tin nhắn cuối cùng
            if (conversation.getLastMessage() != null && !conversation.getLastMessage().isEmpty()) {
                String lastMessage = conversation.getLastMessage();
                // Giới hạn độ dài tin nhắn hiển thị
                if (lastMessage.length() > 50) {
                    lastMessage = lastMessage.substring(0, 47) + "...";
                }

                // Thêm prefix để biết tin nhắn từ ai
                String prefix = conversation.isFromUser() ? "Bạn: " : "AI: ";
                lastMessageText.setText(prefix + lastMessage);
                lastMessageText.setVisibility(View.VISIBLE);
            } else {
                lastMessageText.setText("Chưa có tin nhắn");
                lastMessageText.setVisibility(View.VISIBLE);
            }

            // Thời gian
            String timeStr = formatTime(conversation.getLastMessageTime());
            timeText.setText(timeStr);

            // Số lượng tin nhắn
            int count = conversation.getMessageCount();
            if (count > 0) {
                messageCountText.setText(String.valueOf(count));
                messageCountText.setVisibility(View.VISIBLE);
            } else {
                messageCountText.setVisibility(View.GONE);
            }

            // Hiển thị icon ghim và thay đổi màu nền
            if (conversation.isPinned()) {
                pinIndicator.setVisibility(View.VISIBLE);
                // Thay đổi màu nền cho cuộc trò chuyện được ghim
                cardView.setCardBackgroundColor(itemView.getContext().getResources().getColor(R.color.pinned_conversation_background, itemView.getContext().getTheme()));
                cardView.setStrokeColor(itemView.getContext().getResources().getColor(R.color.pinned_conversation_stroke, itemView.getContext().getTheme()));
                cardView.setStrokeWidth(2);
            } else {
                pinIndicator.setVisibility(View.GONE);
                // Màu nền bình thường - sử dụng màu từ theme
                TypedValue typedValue = new TypedValue();
                itemView.getContext().getTheme().resolveAttribute(com.google.android.material.R.attr.colorSurface, typedValue, true);
                cardView.setCardBackgroundColor(typedValue.data);
                cardView.setStrokeWidth(0);
            }

            // Unread indicator (tạm thời ẩn, có thể implement sau)
            unreadIndicator.setVisibility(View.GONE);
        }

        /**
         * Format thời gian hiển thị
         */
        private String formatTime(long timestamp) {
            if (timestamp <= 0) {
                return "";
            }

            try {
                Date messageDate = new Date(timestamp);
                Date currentDate = new Date();

                // Tính số ngày chênh lệch
                long diffInMillies = currentDate.getTime() - messageDate.getTime();
                long diffInDays = diffInMillies / (24 * 60 * 60 * 1000);

                if (diffInDays == 0) {
                    // Hôm nay - hiển thị giờ
                    return timeFormat.format(messageDate);
                } else if (diffInDays == 1) {
                    // Hôm qua
                    return "Hôm qua";
                } else if (diffInDays < 7) {
                    // Trong tuần - hiển thị số ngày
                    return diffInDays + " ngày trước";
                } else {
                    // Lâu hơn - hiển thị ngày tháng
                    return dateFormat.format(messageDate);
                }
            } catch (Exception e) {
                return "";
            }
        }
    }
}
