/**
 * Contract cho ChatDetail feature - Hiển thị và quản lý chi tiết một cuộc trò chuyện
 */
public interface ChatDetailContract {

    interface View extends BaseView {
        /**
         * Hiển thị danh sách tin nhắn của cuộc trò chuyện
         */
        void showMessages(List<ChatMessage> messages);

        /**
         * Thêm tin nhắn mới vào cuộc trò chuyện
         */
        void addMessage(ChatMessage message);

        /**
         * Hiển thị trạng thái đang gửi tin nhắn
         */
        void showSendingMessage();

        /**
         * Ẩn trạng thái đang gửi tin nhắn
         */
        void hideSendingMessage();

        /**
         * Hiển thị trạng thái AI đang trả lời
         */
        void showAiTyping();

        /**
         * Ẩn trạng thái AI đang trả lời
         */
        void hideAiTyping();

        /**
         * Xóa nội dung trong ô nhập tin nhắn
         */
        void clearMessageInput();

        /**
         * Cuộn xuống tin nhắn mới nhất
         */
        void scrollToLatestMessage();

        /**
         * Hiển thị trạng thái đang tải tin nhắn
         */
        void showLoadingMessages();

        /**
         * Ẩn trạng thái đang tải tin nhắn
         */
        void hideLoadingMessages();

        /**
         * Hiển thị trạng thái cuộc trò chuyện trống
         */
        void showEmptyConversation();

        /**
         * Ẩn trạng thái cuộc trò chuyện trống
         */
        void hideEmptyConversation();

        /**
         * Hiển thị thông báo lỗi khi gửi tin nhắn thất bại
         */
        void showSendMessageError(String error);

        /**
         * Hiển thị thông báo lỗi khi tải tin nhắn thất bại
         */
        void showLoadMessagesError(String error);

        /**
         * Cập nhật tiêu đề cuộc trò chuyện
         */
        void updateConversationTitle(String title);

        /**
         * Hiển thị thông báo thành công
         */
        void showMessage(String message);
    }

    interface Presenter {
        /**
         * Gắn view vào presenter
         */
        void attachView(View view);

        /**
         * Gỡ bỏ view khỏi presenter
         */
        void detachView();

        /**
         * Khởi tạo với ID cuộc trò chuyện
         */
        void initialize(String conversationId, String conversationTitle);

        /**
         * Tải danh sách tin nhắn của cuộc trò chuyện
         */
        void loadMessages();

        /**
         * Gửi tin nhắn mới trong cuộc trò chuyện
         */
        void sendMessage(String content);

        /**
         * Làm mới danh sách tin nhắn
         */
        void refreshMessages();
    }
}

package com.vhn.doan.presentation.chat;

import android.os.Bundle;
import android.text.TextUtils;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageButton;
import android.widget.ProgressBar;
import android.widget.TextView;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.google.android.material.button.MaterialButton;
import com.google.android.material.textfield.TextInputEditText;
import com.vhn.doan.R;
import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.repository.ChatRepositoryImpl;

import java.util.List;

/**
 * Fragment hiển thị chi tiết một cuộc trò chuyện cụ thể với AI
 */
public class ChatDetailFragment extends Fragment implements ChatDetailContract.View {

    private static final String TAG = "ChatDetailFragment";
    private static final String ARG_CONVERSATION_ID = "conversation_id";
    private static final String ARG_CONVERSATION_TITLE = "conversation_title";

    // Views
    private ImageButton btnBack;
    private ImageButton btnRefresh;
    private TextView tvConversationTitle;
    private RecyclerView rvChatMessages;
    private TextInputEditText etMessageInput;
    private MaterialButton btnSendMessage;
    private View layoutLoading;
    private View layoutEmpty;
    private View layoutStatus;
    private ProgressBar progressStatus;
    private TextView tvStatus;

    // Components
    private ChatAdapter chatAdapter;
    private LinearLayoutManager layoutManager;
    private ChatDetailContract.Presenter presenter;

    // Arguments
    private String conversationId;
    private String conversationTitle;

    public static ChatDetailFragment newInstance(String conversationId, String conversationTitle) {
        ChatDetailFragment fragment = new ChatDetailFragment();
        Bundle args = new Bundle();
        args.putString(ARG_CONVERSATION_ID, conversationId);
        args.putString(ARG_CONVERSATION_TITLE, conversationTitle);
        fragment.setArguments(args);
        return fragment;
    }

    @Override
    public void onCreate(@Nullable Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        if (getArguments() != null) {
            conversationId = getArguments().getString(ARG_CONVERSATION_ID);
            conversationTitle = getArguments().getString(ARG_CONVERSATION_TITLE);
        }
    }

    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        return inflater.inflate(R.layout.fragment_chat_detail, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        initViews(view);
        setupRecyclerView();
        setupListeners();
        initPresenter();
        presenter.attachView(this);
        presenter.initialize(conversationId, conversationTitle);
    }

    private void initViews(View view) {
        btnBack = view.findViewById(R.id.btn_back);
        btnRefresh = view.findViewById(R.id.btn_refresh);
        tvConversationTitle = view.findViewById(R.id.tv_conversation_title);
        rvChatMessages = view.findViewById(R.id.rv_chat_messages);
        etMessageInput = view.findViewById(R.id.et_message_input);
        btnSendMessage = view.findViewById(R.id.btn_send_message);
        layoutLoading = view.findViewById(R.id.layout_loading);
        layoutEmpty = view.findViewById(R.id.layout_empty);
        layoutStatus = view.findViewById(R.id.layout_status);
        progressStatus = view.findViewById(R.id.progress_status);
        tvStatus = view.findViewById(R.id.tv_status);
    }

    private void initPresenter() {
        ChatRepositoryImpl chatRepository = new ChatRepositoryImpl();
        presenter = new ChatDetailPresenter(chatRepository);
    }

    private void setupRecyclerView() {
        chatAdapter = new ChatAdapter();
        layoutManager = new LinearLayoutManager(getContext());
        layoutManager.setStackFromEnd(true); // Bắt đầu từ cuối danh sách

        rvChatMessages.setLayoutManager(layoutManager);
        rvChatMessages.setAdapter(chatAdapter);
        rvChatMessages.setHasFixedSize(false);
    }

    private void setupListeners() {
        btnBack.setOnClickListener(v -> {
            if (getActivity() != null) {
                getActivity().onBackPressed();
            }
        });

        btnRefresh.setOnClickListener(v -> {
            if (presenter != null) {
                presenter.refreshMessages();
            }
        });

        btnSendMessage.setOnClickListener(v -> sendMessage());

        // Gửi tin nhắn khi nhấn Enter
        etMessageInput.setOnEditorActionListener((v, actionId, event) -> {
            sendMessage();
            return true;
        });
    }

    private void sendMessage() {
        String message = etMessageInput.getText() != null ? etMessageInput.getText().toString().trim() : "";

        if (!TextUtils.isEmpty(message)) {
            if (presenter != null) {
                presenter.sendMessage(message);
            }
        } else {
            showError("Vui lòng nhập nội dung tin nhắn");
        }
    }

    // Implementation của ChatDetailContract.View

    @Override
    public void showMessages(List<ChatMessage> messages) {
        chatAdapter.setMessages(messages);
    }

    @Override
    public void addMessage(ChatMessage message) {
        chatAdapter.addMessage(message);
    }

    @Override
    public void showSendingMessage() {
        layoutStatus.setVisibility(View.VISIBLE);
        tvStatus.setText("Đang gửi tin nhắn...");
        btnSendMessage.setEnabled(false);
    }

    @Override
    public void hideSendingMessage() {
        btnSendMessage.setEnabled(true);
        if (!chatAdapter.isAiTyping()) {
            layoutStatus.setVisibility(View.GONE);
        }
    }

    @Override
    public void showAiTyping() {
        chatAdapter.showAiTyping();
        layoutStatus.setVisibility(View.VISIBLE);
        tvStatus.setText("AI đang trả lời...");
    }

    @Override
    public void hideAiTyping() {
        chatAdapter.hideAiTyping();
        layoutStatus.setVisibility(View.GONE);
    }

    @Override
    public void clearMessageInput() {
        if (etMessageInput != null) {
            etMessageInput.setText("");
        }
    }

    @Override
    public void scrollToLatestMessage() {
        if (chatAdapter.getItemCount() > 0) {
            rvChatMessages.smoothScrollToPosition(chatAdapter.getItemCount() - 1);
        }
    }

    @Override
    public void showLoadingMessages() {
        layoutLoading.setVisibility(View.VISIBLE);
        rvChatMessages.setVisibility(View.GONE);
        layoutEmpty.setVisibility(View.GONE);
    }

    @Override
    public void hideLoadingMessages() {
        layoutLoading.setVisibility(View.GONE);
        rvChatMessages.setVisibility(View.VISIBLE);
    }

    @Override
    public void showEmptyConversation() {
        layoutEmpty.setVisibility(View.VISIBLE);
        rvChatMessages.setVisibility(View.GONE);
    }

    @Override
    public void hideEmptyConversation() {
        layoutEmpty.setVisibility(View.GONE);
        rvChatMessages.setVisibility(View.VISIBLE);
    }

    @Override
    public void showSendMessageError(String error) {
        showError("Lỗi gửi tin nhắn: " + error);
        layoutStatus.setVisibility(View.GONE);
        btnSendMessage.setEnabled(true);
    }

    @Override
    public void showLoadMessagesError(String error) {
        showError("Lỗi tải tin nhắn: " + error);
    }

    @Override
    public void updateConversationTitle(String title) {
        if (tvConversationTitle != null) {
            tvConversationTitle.setText(title);
        }
    }

    @Override
    public void showMessage(String message) {
        if (getContext() != null) {
            Toast.makeText(getContext(), message, Toast.LENGTH_SHORT).show();
        }
    }

    @Override
    public void showError(String message) {
        if (getContext() != null) {
            Toast.makeText(getContext(), message, Toast.LENGTH_LONG).show();
        }
    }

    @Override
    public void showLoading() {
        showLoadingMessages();
    }

    @Override
    public void hideLoading() {
        hideLoadingMessages();
    }

    @Override
    public void onDestroyView() {
        if (presenter != null) {
            presenter.detachView();
        }
        super.onDestroyView();
    }
}

package com.vhn.doan.presentation.chat;

import android.util.Log;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.Conversation;
import com.vhn.doan.data.repository.ChatRepository;
import com.vhn.doan.data.repository.RepositoryCallback;

import java.util.List;

/**
 * Presenter cho ChatDetail feature - Quản lý chi tiết một cuộc trò chuyện
 */
public class ChatDetailPresenter implements ChatDetailContract.Presenter {

    private static final String TAG = "ChatDetailPresenter";

    private final ChatRepository chatRepository;
    private final FirebaseAuth firebaseAuth;
    private ChatDetailContract.View view;

    // State management
    private String conversationId;
    private String conversationTitle;
    private Conversation currentConversation;
    private boolean isLoading = false;
    private boolean isSending = false;

    public ChatDetailPresenter(ChatRepository chatRepository) {
        this.chatRepository = chatRepository;
        this.firebaseAuth = FirebaseAuth.getInstance();
    }

    @Override
    public void attachView(ChatDetailContract.View view) {
        this.view = view;
    }

    @Override
    public void detachView() {
        this.view = null;
    }

    private boolean isViewAttached() {
        return view != null;
    }

    @Override
    public void initialize(String conversationId, String conversationTitle) {
        this.conversationId = conversationId;
        this.conversationTitle = conversationTitle;

        if (isViewAttached()) {
            view.updateConversationTitle(conversationTitle != null ? conversationTitle : "Cuộc trò chuyện");
        }

        // Tự động tải tin nhắn khi khởi tạo
        loadMessages();
    }

    @Override
    public void loadMessages() {
        if (isLoading || conversationId == null) {
            Log.d(TAG, "Skip loading messages: isLoading=" + isLoading + ", conversationId=" + conversationId);
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadMessagesError("Bạn cần đăng nhập để xem tin nhắn");
            }
            return;
        }

        isLoading = true;

        if (isViewAttached()) {
            view.showLoadingMessages();
            view.hideEmptyConversation();
        }

        chatRepository.getChatMessages(conversationId, new RepositoryCallback<List<ChatMessage>>() {
            @Override
            public void onSuccess(List<ChatMessage> messages) {
                isLoading = false;

                if (isViewAttached()) {
                    view.hideLoadingMessages();

                    if (messages == null || messages.isEmpty()) {
                        view.showEmptyConversation();
                    } else {
                        view.hideEmptyConversation();
                        view.showMessages(messages);
                        view.scrollToLatestMessage();
                    }
                }

                Log.d(TAG, "Loaded " + (messages != null ? messages.size() : 0) + " messages for conversation: " + conversationId);
            }

            @Override
            public void onError(String error) {
                isLoading = false;
                Log.e(TAG, "Failed to load messages: " + error);

                if (isViewAttached()) {
                    view.hideLoadingMessages();
                    view.showLoadMessagesError(error);
                }
            }
        });
    }

    @Override
    public void sendMessage(String content) {
        if (isSending || content == null || content.trim().isEmpty()) {
            if (content == null || content.trim().isEmpty()) {
                if (isViewAttached()) {
                    view.showSendMessageError("Vui lòng nhập nội dung tin nhắn");
                }
            }
            return;
        }

        if (conversationId == null) {
            if (isViewAttached()) {
                view.showSendMessageError("Không tìm thấy cuộc trò chuyện");
            }
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showSendMessageError("Bạn cần đăng nhập để gửi tin nhắn");
            }
            return;
        }

        String trimmedContent = content.trim();
        String userId = currentUser.getUid();
        long timestamp = System.currentTimeMillis();

        isSending = true;

        // Hiển thị tin nhắn của người dùng ngay lập tức
        ChatMessage userMessage = new ChatMessage(conversationId, userId, trimmedContent, true, timestamp);
        String topic = chatRepository.extractTopic(trimmedContent);
        userMessage.setTopic(topic);

        if (isViewAttached()) {
            view.addMessage(userMessage);
            view.clearMessageInput();
            view.scrollToLatestMessage();
            view.showSendingMessage();
            view.hideEmptyConversation();
        }

        // Lưu tin nhắn người dùng vào Firebase
        chatRepository.saveChatMessage(userMessage, new RepositoryCallback<ChatMessage>() {
            @Override
            public void onSuccess(ChatMessage savedUserMessage) {
                Log.d(TAG, "User message saved successfully");

                if (isViewAttached()) {
                    view.hideSendingMessage();
                    view.showAiTyping();
                }

                // Gửi tin nhắn tới AI
                chatRepository.sendMessageToAI(trimmedContent, new RepositoryCallback<String>() {
                    @Override
                    public void onSuccess(String aiResponse) {
                        Log.d(TAG, "AI response received: " + aiResponse);

                        // Tạo tin nhắn phản hồi từ AI
                        ChatMessage aiMessage = new ChatMessage(conversationId, userId, aiResponse, false, System.currentTimeMillis());
                        aiMessage.setTopic(topic);

                        if (isViewAttached()) {
                            view.hideAiTyping();
                            view.addMessage(aiMessage);
                            view.scrollToLatestMessage();
                        }

                        // Lưu phản hồi AI vào Firebase
                        chatRepository.saveChatMessage(aiMessage, new RepositoryCallback<ChatMessage>() {
                            @Override
                            public void onSuccess(ChatMessage savedAiMessage) {
                                Log.d(TAG, "AI message saved successfully");

                                // Cập nhật thông tin cuộc trò chuyện
                                updateConversationInfo(aiMessage);
                                isSending = false;
                            }

                            @Override
                            public void onError(String error) {
                                Log.e(TAG, "Failed to save AI message: " + error);
                                isSending = false;
                                // Không hiển thị lỗi cho người dùng vì tin nhắn đã được hiển thị
                            }
                        });
                    }

                    @Override
                    public void onError(String error) {
                        Log.e(TAG, "Failed to get AI response: " + error);
                        isSending = false;

                        if (isViewAttached()) {
                            view.hideAiTyping();

                            // Hiển thị tin nhắn lỗi từ AI
                            ChatMessage errorMessage = new ChatMessage(conversationId, userId, 
                                "Xin lỗi, tôi không thể trả lời câu hỏi của bạn lúc này. Vui lòng thử lại sau.", 
                                false, System.currentTimeMillis());
                            view.addMessage(errorMessage);
                            view.scrollToLatestMessage();
                            view.showSendMessageError("Không thể nhận phản hồi từ AI: " + error);
                        }
                    }
                });
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to save user message: " + error);
                isSending = false;

                if (isViewAttached()) {
                    view.hideSendingMessage();
                    view.showSendMessageError("Không thể lưu tin nhắn: " + error);
                }
            }
        });
    }

    @Override
    public void refreshMessages() {
        loadMessages();
    }

    /**
     * Cập nhật thông tin cuộc trò chuyện sau khi có tin nhắn mới
     */
    private void updateConversationInfo(ChatMessage lastMessage) {
        if (currentConversation != null && lastMessage != null) {
            currentConversation.setLastMessage(lastMessage.getContent());
            currentConversation.setLastMessageTime(lastMessage.getTimestamp());
            currentConversation.setFromUser(lastMessage.isFromUser());

            // Tăng số lượng tin nhắn
            currentConversation.setMessageCount(currentConversation.getMessageCount() + 2); // User + AI message

            chatRepository.updateConversation(currentConversation, new RepositoryCallback<Conversation>() {
                @Override
                public void onSuccess(Conversation updatedConversation) {
                    Log.d(TAG, "Conversation updated successfully");
                    currentConversation = updatedConversation;
                }

                @Override
                public void onError(String error) {
                    Log.e(TAG, "Failed to update conversation: " + error);
                    // Không hiển thị lỗi cho người dùng vì không ảnh hưởng đến UX
                }
            });
        }
    }

    /**
     * Getter methods cho testing và debug
     */
    public String getConversationId() {
        return conversationId;
    }

    public String getConversationTitle() {
        return conversationTitle;
    }

    public boolean isLoading() {
        return isLoading;
    }

    public boolean isSending() {
        return isSending;
    }
}

package com.vhn.doan.presentation.chat;

import android.os.Bundle;
import android.text.TextUtils;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageButton;
import android.widget.ProgressBar;
import android.widget.TextView;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.google.android.material.button.MaterialButton;
import com.google.android.material.dialog.MaterialAlertDialogBuilder;
import com.google.android.material.textfield.TextInputEditText;
import com.vhn.doan.R;
import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.repository.ChatRepositoryImpl;
import com.vhn.doan.presentation.base.BaseFragment;

import java.util.List;

/**
 * Fragment hiển thị giao diện chat với AI về sức khỏe
 */
public class ChatFragment extends BaseFragment<ChatContract.Presenter> implements ChatContract.View {

    private static final String TAG = "ChatFragment";

    // Views
    private RecyclerView rvChatMessages;
    private TextInputEditText etMessageInput;
    private MaterialButton btnSendMessage;
    private ImageButton btnClearChat;
    private View layoutLoading;
    private View layoutEmpty;
    private View layoutStatus;
    private ProgressBar progressSending;
    private TextView tvStatus;

    // Components
    private ChatAdapter chatAdapter;
    private LinearLayoutManager layoutManager;

    public static ChatFragment newInstance() {
        return new ChatFragment();
    }

    @Override
    protected int getLayoutId() {
        return R.layout.fragment_chat;
    }

    @Override
    protected void initPresenter() {
        ChatRepositoryImpl chatRepository = new ChatRepositoryImpl();
        presenter = new ChatPresenter(chatRepository);
    }

    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        View view = super.onCreateView(inflater, container, savedInstanceState);
        initViews(view);
        setupRecyclerView();
        setupListeners();
        return view;
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        presenter.attachView(this);
    }

    private void initViews(View view) {
        rvChatMessages = view.findViewById(R.id.rv_chat_messages);
        etMessageInput = view.findViewById(R.id.et_message_input);
        btnSendMessage = view.findViewById(R.id.btn_send_message);
        btnClearChat = view.findViewById(R.id.btn_clear_chat);
        layoutLoading = view.findViewById(R.id.layout_loading);
        layoutEmpty = view.findViewById(R.id.layout_empty);
        layoutStatus = view.findViewById(R.id.layout_status);
        progressSending = view.findViewById(R.id.progress_sending);
        tvStatus = view.findViewById(R.id.tv_status);
    }

    private void setupRecyclerView() {
        chatAdapter = new ChatAdapter();
        layoutManager = new LinearLayoutManager(getContext());
        layoutManager.setStackFromEnd(true); // Bắt đầu từ cuối danh sách

        rvChatMessages.setLayoutManager(layoutManager);
        rvChatMessages.setAdapter(chatAdapter);
        rvChatMessages.setHasFixedSize(false);
    }

    private void setupListeners() {
        btnSendMessage.setOnClickListener(v -> sendMessage());

        btnClearChat.setOnClickListener(v -> showClearChatDialog());

        // Gửi tin nhắn khi nhấn Enter (nếu không phải Shift+Enter)
        etMessageInput.setOnEditorActionListener((v, actionId, event) -> {
            sendMessage();
            return true;
        });
    }

    private void sendMessage() {
        String message = etMessageInput.getText() != null ? etMessageInput.getText().toString().trim() : "";

        if (!TextUtils.isEmpty(message)) {
            presenter.sendMessage(message);
        } else {
            showError("Vui lòng nhập nội dung tin nhắn");
        }
    }

    private void showClearChatDialog() {
        new MaterialAlertDialogBuilder(requireContext())
                .setTitle("Xóa lịch sử chat")
                .setMessage("Bạn có chắc chắn muốn xóa toàn bộ lịch sử cuộc trò chuyện không?")
                .setPositiveButton("Xóa", (dialog, which) -> {
                    presenter.clearChatHistory();
                })
                .setNegativeButton("Hủy", null)
                .show();
    }

    // Implementation của ChatContract.View

    @Override
    public void showMessages(List<ChatMessage> messages) {
        if (messages == null || messages.isEmpty()) {
            layoutEmpty.setVisibility(View.VISIBLE);
            rvChatMessages.setVisibility(View.GONE);
        } else {
            layoutEmpty.setVisibility(View.GONE);
            rvChatMessages.setVisibility(View.VISIBLE);
            chatAdapter.setMessages(messages);
        }
    }

    @Override
    public void addMessage(ChatMessage message) {
        layoutEmpty.setVisibility(View.GONE);
        rvChatMessages.setVisibility(View.VISIBLE);
        chatAdapter.addMessage(message);
    }

    @Override
    public void showSendingMessage() {
        layoutStatus.setVisibility(View.VISIBLE);
        progressSending.setVisibility(View.VISIBLE);
        tvStatus.setText("Đang gửi tin nhắn...");
        btnSendMessage.setEnabled(false);
    }

    @Override
    public void hideSendingMessage() {
        btnSendMessage.setEnabled(true);
        if (!chatAdapter.isAiTyping) {
            layoutStatus.setVisibility(View.GONE);
        }
    }

    @Override
    public void showAiTyping() {
        chatAdapter.showAiTyping();
        layoutStatus.setVisibility(View.VISIBLE);
        progressSending.setVisibility(View.VISIBLE);
        tvStatus.setText("AI đang trả lời...");
    }

    @Override
    public void hideAiTyping() {
        chatAdapter.hideAiTyping();
        layoutStatus.setVisibility(View.GONE);
    }

    @Override
    public void clearMessageInput() {
        if (etMessageInput != null) {
            etMessageInput.setText("");
        }
    }

    @Override
    public void scrollToLatestMessage() {
        if (chatAdapter.getItemCount() > 0) {
            rvChatMessages.smoothScrollToPosition(chatAdapter.getItemCount() - 1);
        }
    }

    @Override
    public void showSendMessageError(String error) {
        showError("Lỗi gửi tin nhắn: " + error);
    }

    @Override
    public void showLoadMessagesError(String error) {
        showError("Lỗi tải tin nhắn: " + error);
    }

    @Override
    public void showLoadingMessages() {
        layoutLoading.setVisibility(View.VISIBLE);
        rvChatMessages.setVisibility(View.GONE);
        layoutEmpty.setVisibility(View.GONE);
    }

    @Override
    public void hideLoadingMessages() {
        layoutLoading.setVisibility(View.GONE);
    }

    @Override
    public void showError(String message) {
        if (getContext() != null) {
            Toast.makeText(getContext(), message, Toast.LENGTH_LONG).show();
        }
    }

    @Override
    public void showMessage(String message) {
        if (getContext() != null) {
            Toast.makeText(getContext(), message, Toast.LENGTH_SHORT).show();
        }
    }

    @Override
    public void showLoading() {
        showLoadingMessages();
    }

    @Override
    public void hideLoading() {
        hideLoadingMessages();
    }

    @Override
    public void onDestroyView() {
        if (presenter != null) {
            presenter.detachView();
        }
        super.onDestroyView();
    }
}

package com.vhn.doan.presentation.chat;

import android.os.Bundle;
import android.text.TextUtils;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageButton;
import android.widget.ProgressBar;
import android.widget.TextView;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.google.android.material.button.MaterialButton;
import com.google.android.material.dialog.MaterialAlertDialogBuilder;
import com.google.android.material.textfield.TextInputEditText;
import com.vhn.doan.R;
import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.repository.ChatRepositoryImpl;

import java.util.List;

/**
 * Fragment hiển thị giao diện chat với AI về sức khỏe
 */
public class ChatFragment extends Fragment implements ChatContract.View {

    private static final String TAG = "ChatFragment";

    // Views
    private RecyclerView rvChatMessages;
    private TextInputEditText etMessageInput;
    private MaterialButton btnSendMessage;
    private ImageButton btnClearChat;
    private View layoutLoading;
    private View layoutEmpty;
    private View layoutStatus;
    private ProgressBar progressSending;
    private TextView tvStatus;

    // Components
    private ChatAdapter chatAdapter;
    private LinearLayoutManager layoutManager;
    private ChatContract.Presenter presenter;

    public static ChatFragment newInstance() {
        return new ChatFragment();
    }

    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        return inflater.inflate(R.layout.fragment_chat, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        initViews(view);
        setupRecyclerView();
        setupListeners();
        initPresenter();
        presenter.attachView(this);
    }

    private void initViews(View view) {
        rvChatMessages = view.findViewById(R.id.rv_chat_messages);
        etMessageInput = view.findViewById(R.id.et_message_input);
        btnSendMessage = view.findViewById(R.id.btn_send_message);
        btnClearChat = view.findViewById(R.id.btn_clear_chat);
        layoutLoading = view.findViewById(R.id.layout_loading);
        layoutEmpty = view.findViewById(R.id.layout_empty);
        layoutStatus = view.findViewById(R.id.layout_status);
        progressSending = view.findViewById(R.id.progress_sending);
        tvStatus = view.findViewById(R.id.tv_status);
    }

    private void initPresenter() {
        ChatRepositoryImpl chatRepository = new ChatRepositoryImpl();
        presenter = new ChatPresenter(chatRepository);
    }

    private void setupRecyclerView() {
        chatAdapter = new ChatAdapter();
        layoutManager = new LinearLayoutManager(getContext());
        layoutManager.setStackFromEnd(true); // Bắt đầu từ cuối danh sách

        rvChatMessages.setLayoutManager(layoutManager);
        rvChatMessages.setAdapter(chatAdapter);
        rvChatMessages.setHasFixedSize(false);
    }

    private void setupListeners() {
        btnSendMessage.setOnClickListener(v -> sendMessage());

        btnClearChat.setOnClickListener(v -> showClearChatDialog());

        // Gửi tin nhắn khi nhấn Enter (nếu không phải Shift+Enter)
        etMessageInput.setOnEditorActionListener((v, actionId, event) -> {
            sendMessage();
            return true;
        });
    }

    private void sendMessage() {
        String message = etMessageInput.getText() != null ? etMessageInput.getText().toString().trim() : "";

        if (!TextUtils.isEmpty(message)) {
            presenter.sendMessage(message);
        } else {
            showError("Vui lòng nhập nội dung tin nhắn");
        }
    }

    private void showClearChatDialog() {
        new MaterialAlertDialogBuilder(requireContext())
                .setTitle("Xóa lịch sử chat")
                .setMessage("Bạn có chắc chắn muốn xóa toàn bộ lịch sử cuộc trò chuyện không?")
                .setPositiveButton("Xóa", (dialog, which) -> {
                    presenter.clearChatHistory();
                })
                .setNegativeButton("Hủy", null)
                .show();
    }

    // Implementation của ChatContract.View

    @Override
    public void showMessages(List<ChatMessage> messages) {
        if (messages == null || messages.isEmpty()) {
            layoutEmpty.setVisibility(View.VISIBLE);
            rvChatMessages.setVisibility(View.GONE);
        } else {
            layoutEmpty.setVisibility(View.GONE);
            rvChatMessages.setVisibility(View.VISIBLE);
            chatAdapter.setMessages(messages);
        }
    }

    @Override
    public void addMessage(ChatMessage message) {
        layoutEmpty.setVisibility(View.GONE);
        rvChatMessages.setVisibility(View.VISIBLE);
        chatAdapter.addMessage(message);
    }

    @Override
    public void showSendingMessage() {
        layoutStatus.setVisibility(View.VISIBLE);
        progressSending.setVisibility(View.VISIBLE);
        tvStatus.setText("Đang gửi tin nhắn...");
        btnSendMessage.setEnabled(false);
    }

    @Override
    public void hideSendingMessage() {
        btnSendMessage.setEnabled(true);
        if (!chatAdapter.isAiTyping()) {
            layoutStatus.setVisibility(View.GONE);
        }
    }

    @Override
    public void showAiTyping() {
        chatAdapter.showAiTyping();
        layoutStatus.setVisibility(View.VISIBLE);
        progressSending.setVisibility(View.VISIBLE);
        tvStatus.setText("AI đang trả lời...");
    }

    @Override
    public void hideAiTyping() {
        chatAdapter.hideAiTyping();
        layoutStatus.setVisibility(View.GONE);
    }

    @Override
    public void clearMessageInput() {
        if (etMessageInput != null) {
            etMessageInput.setText("");
        }
    }

    @Override
    public void scrollToLatestMessage() {
        if (chatAdapter.getItemCount() > 0) {
            rvChatMessages.smoothScrollToPosition(chatAdapter.getItemCount() - 1);
        }
    }

    @Override
    public void showSendMessageError(String error) {
        showError("Lỗi gửi tin nhắn: " + error);
    }

    @Override
    public void showLoadMessagesError(String error) {
        showError("Lỗi tải tin nhắn: " + error);
    }

    @Override
    public void showLoadingMessages() {
        layoutLoading.setVisibility(View.VISIBLE);
        rvChatMessages.setVisibility(View.GONE);
        layoutEmpty.setVisibility(View.GONE);
    }

    @Override
    public void hideLoadingMessages() {
        layoutLoading.setVisibility(View.GONE);
    }

    @Override
    public void showError(String message) {
        if (getContext() != null) {
            Toast.makeText(getContext(), message, Toast.LENGTH_LONG).show();
        }
    }

    @Override
    public void showMessage(String message) {
        if (getContext() != null) {
            Toast.makeText(getContext(), message, Toast.LENGTH_SHORT).show();
        }
    }

    @Override
    public void showLoading() {
        showLoadingMessages();
    }

    @Override
    public void hideLoading() {
        hideLoadingMessages();
    }

    @Override
    public void onDestroyView() {
        if (presenter != null) {
            presenter.detachView();
        }
        super.onDestroyView();
    }
}

package com.vhn.doan.presentation.chat;

import com.vhn.doan.data.Conversation;
import com.vhn.doan.presentation.base.BaseView;

import java.util.List;

/**
 * Contract cho ChatList feature - Hiển thị danh sách các cuộc trò chuyện
 */
public interface ChatListContract {

    interface View extends BaseView {
        /**
         * Hiển thị danh sách cuộc trò chuyện
         */
        void showConversations(List<Conversation> conversations);

        /**
         * Thêm danh sách cuộc trò chuyện mới vào cuối (load more)
         */
        void addMoreConversations(List<Conversation> conversations);

        /**
         * Hiển thị trạng thái đang tải danh sách cuộc trò chuyện
         */
        void showLoadingConversations();

        /**
         * Ẩn trạng thái đang tải danh sách cuộc trò chuyện
         */
        void hideLoadingConversations();

        /**
         * Hiển thị trạng thái đang tải thêm cuộc trò chuyện
         */
        void showLoadingMore();

        /**
         * Ẩn trạng thái đang tải thêm cuộc trò chuyện
         */
        void hideLoadingMore();

        /**
         * Hiển thị/ẩn nút "Xem thêm"
         */
        void showLoadMoreButton(boolean show);

        /**
         * Hiển thị trạng thái danh sách trống
         */
        void showEmptyState();

        /**
         * Ẩn trạng thái danh sách trống
         */
        void hideEmptyState();

        /**
         * Hiển thị thông báo lỗi khi tải danh sách cuộc trò chuyện
         */
        void showLoadConversationsError(String error);

        /**
         * Hiển thị thông báo lỗi khi tải thêm cuộc trò chuyện
         */
        void showLoadMoreError(String error);

        /**
         * Điều hướng đến màn hình chat chi tiết
         */
        void navigateToChatDetail(String conversationId, String conversationTitle);

        /**
         * Điều hướng đến màn hình tạo cuộc trò chuyện mới
         */
        void navigateToNewChat();

        /**
         * Hiển thị thông báo thành công
         */
        void showMessage(String message);
    }

    interface Presenter {
        /**
         * Gắn view vào presenter
         */
        void attachView(View view);

        /**
         * Gỡ bỏ view khỏi presenter
         */
        void detachView();

        /**
         * Tải danh sách cuộc trò chuyện (lần đầu)
         */
        void loadConversations();

        /**
         * Tải thêm cuộc trò chuyện (phân trang)
         */
        void loadMoreConversations();

        /**
         * Làm mới danh sách cuộc trò chuyện
         */
        void refreshConversations();

        /**
         * Xử lý khi người dùng nhấn vào một cuộc trò chuyện
         */
        void onConversationClicked(Conversation conversation);

        /**
         * Xử lý khi người dùng muốn tạo cuộc trò chuyện mới
         */
        void onNewChatClicked();

        /**
         * Xử lý khi người dùng nhấn "Xem thêm"
         */
        void onLoadMoreClicked();
    }
}

package com.vhn.doan.presentation.chat;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.google.android.material.button.MaterialButton;
import com.vhn.doan.R;
import com.vhn.doan.data.Conversation;
import com.vhn.doan.data.repository.ChatRepositoryImpl;

import java.util.List;

/**
 * Fragment hiển thị danh sách các cuộc trò chuyện chat AI
 */
public class ChatListFragment extends Fragment implements ChatListContract.View {

    private static final String TAG = "ChatListFragment";

    // Views
    private RecyclerView rvConversations;
    private MaterialButton btnNewChat;
    private MaterialButton btnStartNewChat;
    private MaterialButton btnLoadMore;
    private View layoutLoading;
    private View layoutEmpty;
    private View layoutLoadMore;
    private View layoutLoadingMore;

    // Components
    private ConversationAdapter conversationAdapter;
    private ChatListContract.Presenter presenter;

    public static ChatListFragment newInstance() {
        return new ChatListFragment();
    }

    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        return inflater.inflate(R.layout.fragment_chat_list, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        initViews(view);
        setupRecyclerView();
        setupListeners();
        initPresenter();
        presenter.attachView(this);
    }

    private void initViews(View view) {
        rvConversations = view.findViewById(R.id.rv_conversations);
        btnNewChat = view.findViewById(R.id.btn_new_chat);
        btnStartNewChat = view.findViewById(R.id.btn_start_new_chat);
        btnLoadMore = view.findViewById(R.id.btn_load_more);
        layoutLoading = view.findViewById(R.id.layout_loading);
        layoutEmpty = view.findViewById(R.id.layout_empty);
        layoutLoadMore = view.findViewById(R.id.layout_load_more);
        layoutLoadingMore = view.findViewById(R.id.layout_loading_more);
    }

    private void initPresenter() {
        ChatRepositoryImpl chatRepository = new ChatRepositoryImpl();
        presenter = new ChatListPresenter(chatRepository);
    }

    private void setupRecyclerView() {
        conversationAdapter = new ConversationAdapter();
        rvConversations.setLayoutManager(new LinearLayoutManager(getContext()));
        rvConversations.setAdapter(conversationAdapter);

        conversationAdapter.setOnConversationClickListener(conversation -> {
            if (presenter != null) {
                presenter.onConversationClicked(conversation);
            }
        });
    }

    private void setupListeners() {
        btnNewChat.setOnClickListener(v -> {
            if (presenter != null) {
                presenter.onNewChatClicked();
            }
        });

        btnStartNewChat.setOnClickListener(v -> {
            if (presenter != null) {
                presenter.onNewChatClicked();
            }
        });

        btnLoadMore.setOnClickListener(v -> {
            if (presenter != null) {
                presenter.onLoadMoreClicked();
            }
        });
    }

    // Implementation của ChatListContract.View

    @Override
    public void showConversations(List<Conversation> conversations) {
        conversationAdapter.setConversations(conversations);
    }

    @Override
    public void addMoreConversations(List<Conversation> conversations) {
        conversationAdapter.addConversations(conversations);
    }

    @Override
    public void showLoadingConversations() {
        layoutLoading.setVisibility(View.VISIBLE);
        rvConversations.setVisibility(View.GONE);
        layoutEmpty.setVisibility(View.GONE);
    }

    @Override
    public void hideLoadingConversations() {
        layoutLoading.setVisibility(View.GONE);
        rvConversations.setVisibility(View.VISIBLE);
    }

    @Override
    public void showLoadingMore() {
        layoutLoadingMore.setVisibility(View.VISIBLE);
        btnLoadMore.setVisibility(View.GONE);
    }

    @Override
    public void hideLoadingMore() {
        layoutLoadingMore.setVisibility(View.GONE);
        btnLoadMore.setVisibility(View.VISIBLE);
    }

    @Override
    public void showLoadMoreButton(boolean show) {
        layoutLoadMore.setVisibility(show ? View.VISIBLE : View.GONE);
        if (show) {
            btnLoadMore.setVisibility(View.VISIBLE);
            layoutLoadingMore.setVisibility(View.GONE);
        }
    }

    @Override
    public void showEmptyState() {
        layoutEmpty.setVisibility(View.VISIBLE);
        rvConversations.setVisibility(View.GONE);
    }

    @Override
    public void hideEmptyState() {
        layoutEmpty.setVisibility(View.GONE);
        rvConversations.setVisibility(View.VISIBLE);
    }

    @Override
    public void showLoadConversationsError(String error) {
        showError("Lỗi tải cuộc trò chuyện: " + error);
    }

    @Override
    public void showLoadMoreError(String error) {
        showError("Lỗi tải thêm: " + error);
    }

    @Override
    public void navigateToChatDetail(String conversationId, String conversationTitle) {
        // TODO: Chuyển đến ChatDetailFragment với conversationId
        if (getActivity() != null) {
            ChatDetailFragment chatDetailFragment = ChatDetailFragment.newInstance(conversationId, conversationTitle);

            getActivity().getSupportFragmentManager()
                    .beginTransaction()
                    .replace(R.id.fragment_container, chatDetailFragment)
                    .addToBackStack(null)
                    .commit();
        }
    }

    @Override
    public void navigateToNewChat() {
        // TODO: Chuyển đến NewChatFragment hoặc ChatDetailFragment với conversationId mới
        if (getActivity() != null) {
            NewChatFragment newChatFragment = NewChatFragment.newInstance();

            getActivity().getSupportFragmentManager()
                    .beginTransaction()
                    .replace(R.id.fragment_container, newChatFragment)
                    .addToBackStack(null)
                    .commit();
        }
    }

    @Override
    public void showMessage(String message) {
        if (getContext() != null) {
            Toast.makeText(getContext(), message, Toast.LENGTH_SHORT).show();
        }
    }

    @Override
    public void showError(String message) {
        if (getContext() != null) {
            Toast.makeText(getContext(), message, Toast.LENGTH_LONG).show();
        }
    }

    @Override
    public void showLoading() {
        showLoadingConversations();
    }

    @Override
    public void hideLoading() {
        hideLoadingConversations();
    }

    @Override
    public void onDestroyView() {
        if (presenter != null) {
            presenter.detachView();
        }
        super.onDestroyView();
    }

    /**
     * Method để refresh danh sách từ bên ngoài
     */
    public void refreshConversations() {
        if (presenter != null) {
            presenter.refreshConversations();
        }
    }
}

package com.vhn.doan.presentation.chat;

import android.util.Log;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.vhn.doan.data.Conversation;
import com.vhn.doan.data.repository.ChatRepository;
import com.vhn.doan.data.repository.RepositoryCallback;

import java.util.ArrayList;
import java.util.List;

/**
 * Presenter cho ChatList feature - Quản lý danh sách cuộc trò chuyện
 */
public class ChatListPresenter implements ChatListContract.Presenter {

    private static final String TAG = "ChatListPresenter";
    private static final int DEFAULT_CONVERSATIONS_LIMIT = 8;
    private static final int LOAD_MORE_CONVERSATIONS_LIMIT = 3;

    private final ChatRepository chatRepository;
    private final FirebaseAuth firebaseAuth;
    private ChatListContract.View view;

    // State management
    private List<Conversation> conversationsList;
    private Long lastConversationTime;
    private boolean isLoading = false;
    private boolean isLoadingMore = false;
    private boolean hasMoreConversations = true;

    public ChatListPresenter(ChatRepository chatRepository) {
        this.chatRepository = chatRepository;
        this.firebaseAuth = FirebaseAuth.getInstance();
        this.conversationsList = new ArrayList<>();
    }

    @Override
    public void attachView(ChatListContract.View view) {
        this.view = view;
        // Tự động tải danh sách cuộc trò chuyện khi attach view
        loadConversations();
    }

    @Override
    public void detachView() {
        this.view = null;
    }

    private boolean isViewAttached() {
        return view != null;
    }

    @Override
    public void loadConversations() {
        if (isLoading) {
            Log.d(TAG, "Already loading conversations, skip");
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadConversationsError("Bạn cần đăng nhập để xem danh sách cuộc trò chuyện");
            }
            return;
        }

        isLoading = true;
        lastConversationTime = null; // Reset phân trang
        hasMoreConversations = true;

        if (isViewAttached()) {
            view.showLoadingConversations();
            view.hideEmptyState();
        }

        chatRepository.getConversations(currentUser.getUid(), DEFAULT_CONVERSATIONS_LIMIT, null, 
            new RepositoryCallback<List<Conversation>>() {
                @Override
                public void onSuccess(List<Conversation> conversations) {
                    isLoading = false;

                    if (isViewAttached()) {
                        view.hideLoadingConversations();

                        if (conversations == null || conversations.isEmpty()) {
                            view.showEmptyState();
                            view.showLoadMoreButton(false);
                        } else {
                            view.hideEmptyState();
                            view.showConversations(conversations);

                            // Cập nhật state cho phân trang
                            conversationsList.clear();
                            conversationsList.addAll(conversations);

                            if (!conversations.isEmpty()) {
                                lastConversationTime = conversations.get(conversations.size() - 1).getLastMessageTime();
                            }

                            // Kiểm tra xem có thêm cuộc trò chuyện để load không
                            checkHasMoreConversations();
                        }
                    }
                }

                @Override
                public void onError(String error) {
                    isLoading = false;
                    Log.e(TAG, "Failed to load conversations: " + error);

                    if (isViewAttached()) {
                        view.hideLoadingConversations();
                        view.showLoadConversationsError(error);
                    }
                }
            });
    }

    @Override
    public void loadMoreConversations() {
        if (isLoadingMore || !hasMoreConversations) {
            Log.d(TAG, "Skip load more: isLoadingMore=" + isLoadingMore + ", hasMore=" + hasMoreConversations);
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadMoreError("Bạn cần đăng nhập để tải thêm cuộc trò chuyện");
            }
            return;
        }

        isLoadingMore = true;

        if (isViewAttached()) {
            view.showLoadingMore();
        }

        chatRepository.getConversations(currentUser.getUid(), LOAD_MORE_CONVERSATIONS_LIMIT, lastConversationTime,
            new RepositoryCallback<List<Conversation>>() {
                @Override
                public void onSuccess(List<Conversation> conversations) {
                    isLoadingMore = false;

                    if (isViewAttached()) {
                        view.hideLoadingMore();

                        if (conversations != null && !conversations.isEmpty()) {
                            view.addMoreConversations(conversations);

                            // Cập nhật state
                            conversationsList.addAll(conversations);
                            lastConversationTime = conversations.get(conversations.size() - 1).getLastMessageTime();

                            // Kiểm tra xem còn thêm cuộc trò chuyện không
                            checkHasMoreConversations();
                        } else {
                            // Không còn cuộc trò chuyện nào để tải
                            hasMoreConversations = false;
                            view.showLoadMoreButton(false);
                        }
                    }
                }

                @Override
                public void onError(String error) {
                    isLoadingMore = false;
                    Log.e(TAG, "Failed to load more conversations: " + error);

                    if (isViewAttached()) {
                        view.hideLoadingMore();
                        view.showLoadMoreError(error);
                    }
                }
            });
    }

    @Override
    public void refreshConversations() {
        // Reset tất cả state và tải lại từ đầu
        conversationsList.clear();
        lastConversationTime = null;
        hasMoreConversations = true;
        isLoading = false;
        isLoadingMore = false;

        loadConversations();
    }

    @Override
    public void onConversationClicked(Conversation conversation) {
        if (conversation == null || conversation.getId() == null) {
            if (isViewAttached()) {
                view.showError("Thông tin cuộc trò chuyện không hợp lệ");
            }
            return;
        }

        Log.d(TAG, "Conversation clicked: " + conversation.getId() + " - " + conversation.getTitle());

        if (isViewAttached()) {
            view.navigateToChatDetail(conversation.getId(), conversation.getTitle());
        }
    }

    @Override
    public void onNewChatClicked() {
        Log.d(TAG, "New chat clicked");

        if (isViewAttached()) {
            view.navigateToNewChat();
        }
    }

    @Override
    public void onLoadMoreClicked() {
        Log.d(TAG, "Load more clicked");
        loadMoreConversations();
    }

    /**
     * Kiểm tra xem còn cuộc trò chuyện nào để load không
     */
    private void checkHasMoreConversations() {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null || lastConversationTime == null) {
            hasMoreConversations = false;
            if (isViewAttached()) {
                view.showLoadMoreButton(false);
            }
            return;
        }

        chatRepository.hasMoreConversations(currentUser.getUid(), lastConversationTime,
            new RepositoryCallback<Boolean>() {
                @Override
                public void onSuccess(Boolean hasMore) {
                    hasMoreConversations = hasMore;

                    if (isViewAttached()) {
                        view.showLoadMoreButton(hasMore);
                    }

                    Log.d(TAG, "Has more conversations: " + hasMore);
                }

                @Override
                public void onError(String error) {
                    Log.e(TAG, "Failed to check more conversations: " + error);
                    // Trong trường hợp lỗi, giả sử không còn cuộc trò chuyện nào
                    hasMoreConversations = false;

                    if (isViewAttached()) {
                        view.showLoadMoreButton(false);
                    }
                }
            });
    }

    /**
     * Getter cho testing hoặc debug
     */
    public List<Conversation> getConversationsList() {
        return new ArrayList<>(conversationsList);
    }

    public boolean isHasMoreConversations() {
        return hasMoreConversations;
    }

    public boolean isLoading() {
        return isLoading;
    }

    public boolean isLoadingMore() {
        return isLoadingMore;
    }
}

package com.vhn.doan.presentation.chat;

import android.util.Log;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.repository.ChatRepository;
import com.vhn.doan.data.repository.RepositoryCallback;
import com.vhn.doan.presentation.base.BasePresenter;

import java.util.List;

/**
 * Presenter cho Chat feature theo kiến trúc MVP
 */
public class ChatPresenter extends BasePresenter<ChatContract.View> implements ChatContract.Presenter {

    private static final String TAG = "ChatPresenter";

    private final ChatRepository chatRepository;
    private final FirebaseAuth firebaseAuth;

    public ChatPresenter(ChatRepository chatRepository) {
        this.chatRepository = chatRepository;
        this.firebaseAuth = FirebaseAuth.getInstance();
    }

    @Override
    public void loadMessages() {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (getView() != null) {
                getView().showLoadMessagesError("Bạn cần đăng nhập để sử dụng tính năng chat");
            }
            return;
        }

        if (getView() != null) {
            getView().showLoadingMessages();
        }

        chatRepository.getChatMessages(currentUser.getUid(), new RepositoryCallback<List<ChatMessage>>() {
            @Override
            public void onSuccess(List<ChatMessage> messages) {
                if (getView() != null) {
                    getView().hideLoadingMessages();
                    getView().showMessages(messages);

                    if (!messages.isEmpty()) {
                        getView().scrollToLatestMessage();
                    }
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to load messages: " + error);
                if (getView() != null) {
                    getView().hideLoadingMessages();
                    getView().showLoadMessagesError(error);
                }
            }
        });
    }

    @Override
    public void sendMessage(String content) {
        if (content == null || content.trim().isEmpty()) {
            if (getView() != null) {
                getView().showSendMessageError("Vui lòng nhập nội dung tin nhắn");
            }
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (getView() != null) {
                getView().showSendMessageError("Bạn cần đăng nhập để gửi tin nhắn");
            }
            return;
        }

        String trimmedContent = content.trim();
        long timestamp = System.currentTimeMillis();
        String userId = currentUser.getUid();

        // Hiển thị tin nhắn của người dùng ngay lập tức
        ChatMessage userMessage = new ChatMessage(userId, trimmedContent, true, timestamp);
        String topic = chatRepository.extractTopic(trimmedContent);
        userMessage.setTopic(topic);

        if (getView() != null) {
            getView().addMessage(userMessage);
            getView().clearMessageInput();
            getView().scrollToLatestMessage();
            getView().showSendingMessage();
        }

        // Lưu tin nhắn người dùng vào Firebase
        chatRepository.saveChatMessage(userMessage, new RepositoryCallback<ChatMessage>() {
            @Override
            public void onSuccess(ChatMessage savedMessage) {
                Log.d(TAG, "User message saved successfully");
                if (getView() != null) {
                    getView().hideSendingMessage();
                    getView().showAiTyping();
                }

                // Gửi tin nhắn tới AI
                chatRepository.sendMessageToAI(trimmedContent, new RepositoryCallback<String>() {
                    @Override
                    public void onSuccess(String aiResponse) {
                        Log.d(TAG, "AI response received: " + aiResponse);

                        // Tạo tin nhắn phản hồi từ AI
                        ChatMessage aiMessage = new ChatMessage(userId, aiResponse, false, System.currentTimeMillis());
                        aiMessage.setTopic(topic);

                        if (getView() != null) {
                            getView().hideAiTyping();
                            getView().addMessage(aiMessage);
                            getView().scrollToLatestMessage();
                        }

                        // Lưu phản hồi AI vào Firebase
                        chatRepository.saveChatMessage(aiMessage, new RepositoryCallback<ChatMessage>() {
                            @Override
                            public void onSuccess(ChatMessage savedAiMessage) {
                                Log.d(TAG, "AI message saved successfully");
                            }

                            @Override
                            public void onError(String error) {
                                Log.e(TAG, "Failed to save AI message: " + error);
                                // Không hiển thị lỗi cho người dùng vì tin nhắn đã được hiển thị
                            }
                        });
                    }

                    @Override
                    public void onError(String error) {
                        Log.e(TAG, "Failed to get AI response: " + error);

                        if (getView() != null) {
                            getView().hideAiTyping();

                            // Hiển thị tin nhắn lỗi từ AI
                            ChatMessage errorMessage = new ChatMessage(userId,
                                "Xin lỗi, tôi không thể trả lời câu hỏi của bạn lúc này. Vui lòng thử lại sau.",
                                false, System.currentTimeMillis());
                            getView().addMessage(errorMessage);
                            getView().scrollToLatestMessage();
                            getView().showSendMessageError("Không thể nhận phản hồi từ AI: " + error);
                        }
                    }
                });
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to save user message: " + error);
                if (getView() != null) {
                    getView().hideSendingMessage();
                    getView().showSendMessageError("Không thể lưu tin nhắn: " + error);
                }
            }
        });
    }

    @Override
    public void clearChatHistory() {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (getView() != null) {
                getView().showLoadMessagesError("Bạn cần đăng nhập để xóa lịch sử chat");
            }
            return;
        }

        chatRepository.clearChatHistory(currentUser.getUid(), new RepositoryCallback<Boolean>() {
            @Override
            public void onSuccess(Boolean success) {
                Log.d(TAG, "Chat history cleared successfully");
                if (getView() != null) {
                    getView().showMessage("Đã xóa lịch sử chat thành công");
                    // Tải lại danh sách tin nhắn (sẽ rỗng)
                    loadMessages();
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to clear chat history: " + error);
                if (getView() != null) {
                    getView().showError("Không thể xóa lịch sử chat: " + error);
                }
            }
        });
    }

    @Override
    public void refreshMessages() {
        loadMessages();
    }

    @Override
    public void attachView(ChatContract.View view) {
        super.attachView(view);
        // Tự động tải tin nhắn khi attach view
        loadMessages();
    }
}

package com.vhn.doan.presentation.chat;

import android.util.Log;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.repository.ChatRepository;
import com.vhn.doan.data.repository.RepositoryCallback;

import java.util.List;

/**
 * Presenter cho Chat feature theo kiến trúc MVP
 */
public class ChatPresenter implements ChatContract.Presenter {
    
    private static final String TAG = "ChatPresenter";
    
    private final ChatRepository chatRepository;
    private final FirebaseAuth firebaseAuth;
    private ChatContract.View view;

    public ChatPresenter(ChatRepository chatRepository) {
        this.chatRepository = chatRepository;
        this.firebaseAuth = FirebaseAuth.getInstance();
    }

    @Override
    public void attachView(ChatContract.View view) {
        this.view = view;
        // Tự động tải tin nhắn khi attach view
        loadMessages();
    }

    @Override
    public void detachView() {
        this.view = null;
    }

    private boolean isViewAttached() {
        return view != null;
    }

    @Override
    public void loadMessages() {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadMessagesError("Bạn cần đăng nhập để sử dụng tính năng chat");
            }
            return;
        }

        if (isViewAttached()) {
            view.showLoadingMessages();
        }

        chatRepository.getChatMessages(currentUser.getUid(), new RepositoryCallback<List<ChatMessage>>() {
            @Override
            public void onSuccess(List<ChatMessage> messages) {
                if (isViewAttached()) {
                    view.hideLoadingMessages();
                    view.showMessages(messages);
                    
                    if (!messages.isEmpty()) {
                        view.scrollToLatestMessage();
                    }
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to load messages: " + error);
                if (isViewAttached()) {
                    view.hideLoadingMessages();
                    view.showLoadMessagesError(error);
                }
            }
        });
    }

    @Override
    public void sendMessage(String content) {
        if (content == null || content.trim().isEmpty()) {
            if (isViewAttached()) {
                view.showSendMessageError("Vui lòng nhập nội dung tin nhắn");
            }
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showSendMessageError("Bạn cần đăng nhập để gửi tin nhắn");
            }
            return;
        }

        String trimmedContent = content.trim();
        long timestamp = System.currentTimeMillis();
        String userId = currentUser.getUid();

        // Hiển thị tin nhắn của người dùng ngay lập tức
        ChatMessage userMessage = new ChatMessage(userId, trimmedContent, true, timestamp);
        String topic = chatRepository.extractTopic(trimmedContent);
        userMessage.setTopic(topic);

        if (isViewAttached()) {
            view.addMessage(userMessage);
            view.clearMessageInput();
            view.scrollToLatestMessage();
            view.showSendingMessage();
        }

        // Lưu tin nhắn người dùng vào Firebase
        chatRepository.saveChatMessage(userMessage, new RepositoryCallback<ChatMessage>() {
            @Override
            public void onSuccess(ChatMessage savedMessage) {
                Log.d(TAG, "User message saved successfully");
                if (isViewAttached()) {
                    view.hideSendingMessage();
                    view.showAiTyping();
                }

                // Gửi tin nhắn tới AI
                chatRepository.sendMessageToAI(trimmedContent, new RepositoryCallback<String>() {
                    @Override
                    public void onSuccess(String aiResponse) {
                        Log.d(TAG, "AI response received: " + aiResponse);
                        
                        // Tạo tin nhắn phản hồi từ AI
                        ChatMessage aiMessage = new ChatMessage(userId, aiResponse, false, System.currentTimeMillis());
                        aiMessage.setTopic(topic);

                        if (isViewAttached()) {
                            view.hideAiTyping();
                            view.addMessage(aiMessage);
                            view.scrollToLatestMessage();
                        }

                        // Lưu phản hồi AI vào Firebase
                        chatRepository.saveChatMessage(aiMessage, new RepositoryCallback<ChatMessage>() {
                            @Override
                            public void onSuccess(ChatMessage savedAiMessage) {
                                Log.d(TAG, "AI message saved successfully");
                            }

                            @Override
                            public void onError(String error) {
                                Log.e(TAG, "Failed to save AI message: " + error);
                                // Không hiển thị lỗi cho người dùng vì tin nhắn đã được hiển thị
                            }
                        });
                    }

                    @Override
                    public void onError(String error) {
                        Log.e(TAG, "Failed to get AI response: " + error);
                        
                        if (isViewAttached()) {
                            view.hideAiTyping();
                            
                            // Hiển thị tin nhắn lỗi từ AI
                            ChatMessage errorMessage = new ChatMessage(userId, 
                                "Xin lỗi, tôi không thể trả lời câu hỏi của bạn lúc này. Vui lòng thử lại sau.", 
                                false, System.currentTimeMillis());
                            view.addMessage(errorMessage);
                            view.scrollToLatestMessage();
                            view.showSendMessageError("Không thể nhận phản hồi từ AI: " + error);
                        }
                    }
                });
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to save user message: " + error);
                if (isViewAttached()) {
                    view.hideSendingMessage();
                    view.showSendMessageError("Không thể lưu tin nhắn: " + error);
                }
            }
        });
    }

    @Override
    public void clearChatHistory() {
        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showLoadMessagesError("Bạn cần đăng nhập để xóa lịch sử chat");
            }
            return;
        }

        chatRepository.clearChatHistory(currentUser.getUid(), new RepositoryCallback<Boolean>() {
            @Override
            public void onSuccess(Boolean success) {
                Log.d(TAG, "Chat history cleared successfully");
                if (isViewAttached()) {
                    view.showMessage("Đã xóa lịch sử chat thành công");
                    // Tải lại danh sách tin nhắn (sẽ rỗng)
                    loadMessages();
                }
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to clear chat history: " + error);
                if (isViewAttached()) {
                    view.showError("Không thể xóa lịch sử chat: " + error);
                }
            }
        });
    }

    @Override
    public void refreshMessages() {
        loadMessages();
    }
}

package com.vhn.doan.presentation.chat;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.vhn.doan.R;
import com.vhn.doan.data.Conversation;

import java.text.SimpleDateFormat;
import java.util.ArrayList;
import java.util.Date;
import java.util.List;
import java.util.Locale;

/**
 * Adapter cho danh sách các cuộc trò chuyện chat
 */
public class ConversationAdapter extends RecyclerView.Adapter<ConversationAdapter.ConversationViewHolder> {

    private List<Conversation> conversations;
    private OnConversationClickListener onConversationClickListener;
    private final SimpleDateFormat timeFormat;
    private final SimpleDateFormat dateFormat;

    public interface OnConversationClickListener {
        void onConversationClick(Conversation conversation);
    }

    public ConversationAdapter() {
        this.conversations = new ArrayList<>();
        this.timeFormat = new SimpleDateFormat("HH:mm", Locale.getDefault());
        this.dateFormat = new SimpleDateFormat("dd/MM/yyyy", Locale.getDefault());
    }

    public void setOnConversationClickListener(OnConversationClickListener listener) {
        this.onConversationClickListener = listener;
    }

    @NonNull
    @Override
    public ConversationViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_conversation, parent, false);
        return new ConversationViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull ConversationViewHolder holder, int position) {
        Conversation conversation = conversations.get(position);
        holder.bind(conversation);
    }

    @Override
    public int getItemCount() {
        return conversations.size();
    }

    /**
     * Cập nhật danh sách cuộc trò chuyện (reset toàn bộ)
     */
    public void setConversations(List<Conversation> conversations) {
        this.conversations.clear();
        if (conversations != null) {
            this.conversations.addAll(conversations);
        }
        notifyDataSetChanged();
    }

    /**
     * Thêm danh sách cuộc trò chuyện mới vào cuối (cho load more)
     */
    public void addConversations(List<Conversation> conversations) {
        if (conversations != null && !conversations.isEmpty()) {
            int startPosition = this.conversations.size();
            this.conversations.addAll(conversations);
            notifyItemRangeInserted(startPosition, conversations.size());
        }
    }

    /**
     * Xóa tất cả cuộc trò chuyện
     */
    public void clearConversations() {
        int size = conversations.size();
        conversations.clear();
        notifyItemRangeRemoved(0, size);
    }

    /**
     * Cập nhật một cuộc trò chuyện cụ thể
     */
    public void updateConversation(Conversation updatedConversation) {
        for (int i = 0; i < conversations.size(); i++) {
            if (conversations.get(i).getId().equals(updatedConversation.getId())) {
                conversations.set(i, updatedConversation);
                notifyItemChanged(i);
                break;
            }
        }
    }

    class ConversationViewHolder extends RecyclerView.ViewHolder {
        private TextView titleText;
        private TextView lastMessageText;
        private TextView timeText;
        private TextView messageCountText;
        private View unreadIndicator;

        public ConversationViewHolder(@NonNull View itemView) {
            super(itemView);
            titleText = itemView.findViewById(R.id.tv_conversation_title);
            lastMessageText = itemView.findViewById(R.id.tv_last_message);
            timeText = itemView.findViewById(R.id.tv_conversation_time);
            messageCountText = itemView.findViewById(R.id.tv_message_count);
            unreadIndicator = itemView.findViewById(R.id.view_unread_indicator);

            itemView.setOnClickListener(v -> {
                int position = getAdapterPosition();
                if (position != RecyclerView.NO_POSITION && onConversationClickListener != null) {
                    onConversationClickListener.onConversationClick(conversations.get(position));
                }
            });
        }

        public void bind(Conversation conversation) {
            // Tiêu đề cuộc trò chuyện
            titleText.setText(conversation.getTitle() != null ? conversation.getTitle() : "Cuộc trò chuyện");

            // Tin nhắn cuối cùng
            if (conversation.getLastMessage() != null && !conversation.getLastMessage().isEmpty()) {
                String lastMessage = conversation.getLastMessage();
                // Giới hạn độ dài tin nhắn hiển thị
                if (lastMessage.length() > 50) {
                    lastMessage = lastMessage.substring(0, 47) + "...";
                }

                // Thêm prefix để biết tin nhắn từ ai
                String prefix = conversation.isFromUser() ? "Bạn: " : "AI: ";
                lastMessageText.setText(prefix + lastMessage);
                lastMessageText.setVisibility(View.VISIBLE);
            } else {
                lastMessageText.setText("Chưa có tin nhắn");
                lastMessageText.setVisibility(View.VISIBLE);
            }

            // Thời gian
            String timeStr = formatTime(conversation.getLastMessageTime());
            timeText.setText(timeStr);

            // Số lượng tin nhắn
            int count = conversation.getMessageCount();
            if (count > 0) {
                messageCountText.setText(String.valueOf(count));
                messageCountText.setVisibility(View.VISIBLE);
            } else {
                messageCountText.setVisibility(View.GONE);
            }

            // Unread indicator (tạm thời ẩn, có thể implement sau)
            unreadIndicator.setVisibility(View.GONE);
        }

        /**
         * Format thời gian hiển thị
         */
        private String formatTime(long timestamp) {
            if (timestamp <= 0) {
                return "";
            }

            try {
                Date messageDate = new Date(timestamp);
                Date currentDate = new Date();

                // Tính số ngày chênh lệch
                long diffInMillies = currentDate.getTime() - messageDate.getTime();
                long diffInDays = diffInMillies / (24 * 60 * 60 * 1000);

                if (diffInDays == 0) {
                    // Hôm nay - hiển thị giờ
                    return timeFormat.format(messageDate);
                } else if (diffInDays == 1) {
                    // Hôm qua
                    return "Hôm qua";
                } else if (diffInDays < 7) {
                    // Trong tuần - hiển thị số ngày
                    return diffInDays + " ngày trước";
                } else {
                    // Lâu hơn - hiển thị ngày tháng
                    return dateFormat.format(messageDate);
                }
            } catch (Exception e) {
                return "";
            }
        }
    }
}

package com.vhn.doan.presentation.chat;

import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.Conversation;
import com.vhn.doan.presentation.base.BaseView;

import java.util.List;

/**
 * Contract cho NewChat feature - Tạo cuộc trò chuyện mới
 */
public interface NewChatContract {

    interface View extends BaseView {
        /**
         * Hiển thị trạng thái đang tạo cuộc trò chuyện
         */
        void showCreatingConversation();

        /**
         * Ẩn trạng thái đang tạo cuộc trò chuyện
         */
        void hideCreatingConversation();

        /**
         * Hiển thị trạng thái đang gửi tin nhắn
         */
        void showSendingMessage();

        /**
         * Ẩn trạng thái đang gửi tin nhắn
         */
        void hideSendingMessage();

        /**
         * Hiển thị trạng thái AI đang trả lời
         */
        void showAiTyping();

        /**
         * Ẩn trạng thái AI đang trả lời
         */
        void hideAiTyping();

        /**
         * Xóa nội dung trong ô nhập tin nhắn
         */
        void clearMessageInput();

        /**
         * Hiển thị thông báo lỗi khi tạo cuộc trò chuyện thất bại
         */
        void showCreateConversationError(String error);

        /**
         * Hiển thị thông báo lỗi khi gửi tin nhắn thất bại
         */
        void showSendMessageError(String error);

        /**
         * Điều hướng đến màn hình chat detail với cuộc trò chuyện đã tạo
         */
        void navigateToChatDetail(String conversationId, String conversationTitle);

        /**
         * Hiển thị thông báo thành công
         */
        void showMessage(String message);
    }

    interface Presenter {
        /**
         * Gắn view vào presenter
         */
        void attachView(View view);

        /**
         * Gỡ bỏ view khỏi presenter
         */
        void detachView();

        /**
         * Tạo cuộc trò chuyện mới và gửi tin nhắn đầu tiên
         * @param firstMessage Tin nhắn đầu tiên
         */
        void createConversationAndSendMessage(String firstMessage);
    }
}

package com.vhn.doan.presentation.chat;

import android.os.Bundle;
import android.text.TextUtils;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageButton;
import android.widget.ProgressBar;
import android.widget.TextView;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import com.google.android.material.button.MaterialButton;
import com.google.android.material.card.MaterialCardView;
import com.google.android.material.textfield.TextInputEditText;
import com.vhn.doan.R;
import com.vhn.doan.data.repository.ChatRepositoryImpl;

/**
 * Fragment để tạo cuộc trò chuyện mới với AI
 */
public class NewChatFragment extends Fragment implements NewChatContract.View {

    private static final String TAG = "NewChatFragment";

    // Views
    private ImageButton btnBack;
    private TextInputEditText etMessageInput;
    private MaterialButton btnSendMessage;
    private MaterialCardView cardQuestion1;
    private MaterialCardView cardQuestion2;
    private MaterialCardView cardQuestion3;
    private View layoutStatus;
    private ProgressBar progressStatus;
    private TextView tvStatus;

    // Components
    private NewChatContract.Presenter presenter;

    // Suggested questions
    private static final String QUESTION_1 = "Làm thế nào để tăng cường sức đề kháng?";
    private static final String QUESTION_2 = "Chế độ ăn nào tốt cho tim mạch?";
    private static final String QUESTION_3 = "Cách cải thiện chất lượng giấc ngủ?";

    public static NewChatFragment newInstance() {
        return new NewChatFragment();
    }

    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {
        return inflater.inflate(R.layout.fragment_new_chat, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        initViews(view);
        setupListeners();
        initPresenter();
        presenter.attachView(this);
    }

    private void initViews(View view) {
        btnBack = view.findViewById(R.id.btn_back);
        etMessageInput = view.findViewById(R.id.et_message_input);
        btnSendMessage = view.findViewById(R.id.btn_send_message);
        cardQuestion1 = view.findViewById(R.id.card_question_1);
        cardQuestion2 = view.findViewById(R.id.card_question_2);
        cardQuestion3 = view.findViewById(R.id.card_question_3);
        layoutStatus = view.findViewById(R.id.layout_status);
        progressStatus = view.findViewById(R.id.progress_status);
        tvStatus = view.findViewById(R.id.tv_status);
    }

    private void initPresenter() {
        ChatRepositoryImpl chatRepository = new ChatRepositoryImpl();
        presenter = new NewChatPresenter(chatRepository);
    }

    private void setupListeners() {
        btnBack.setOnClickListener(v -> {
            if (getActivity() != null) {
                getActivity().onBackPressed();
            }
        });

        btnSendMessage.setOnClickListener(v -> sendMessage());

        cardQuestion1.setOnClickListener(v -> sendSuggestedQuestion(QUESTION_1));
        cardQuestion2.setOnClickListener(v -> sendSuggestedQuestion(QUESTION_2));
        cardQuestion3.setOnClickListener(v -> sendSuggestedQuestion(QUESTION_3));

        etMessageInput.setOnEditorActionListener((v, actionId, event) -> {
            sendMessage();
            return true;
        });
    }

    private void sendMessage() {
        String message = etMessageInput.getText() != null ? etMessageInput.getText().toString().trim() : "";

        if (!TextUtils.isEmpty(message)) {
            if (presenter != null) {
                presenter.createConversationAndSendMessage(message);
            }
        } else {
            showError("Vui lòng nhập nội dung tin nhắn");
        }
    }

    private void sendSuggestedQuestion(String question) {
        etMessageInput.setText(question);
        if (presenter != null) {
            presenter.createConversationAndSendMessage(question);
        }
    }

    // Implementation của NewChatContract.View

    @Override
    public void showCreatingConversation() {
        layoutStatus.setVisibility(View.VISIBLE);
        tvStatus.setText("Đang tạo cuộc trò chuyện...");
        btnSendMessage.setEnabled(false);
        setQuestionCardsEnabled(false);
    }

    @Override
    public void hideCreatingConversation() {
        // Không ẩn layoutStatus vì có thể chuyển sang trạng thái khác
        btnSendMessage.setEnabled(true);
        setQuestionCardsEnabled(true);
    }

    @Override
    public void showSendingMessage() {
        layoutStatus.setVisibility(View.VISIBLE);
        tvStatus.setText("Đang gửi tin nhắn...");
        btnSendMessage.setEnabled(false);
        setQuestionCardsEnabled(false);
    }

    @Override
    public void hideSendingMessage() {
        // Không ẩn layoutStatus vì có thể chuyển sang trạng thái khác
        btnSendMessage.setEnabled(true);
        setQuestionCardsEnabled(true);
    }

    @Override
    public void showAiTyping() {
        layoutStatus.setVisibility(View.VISIBLE);
        tvStatus.setText("AI đang trả lời...");
        btnSendMessage.setEnabled(false);
        setQuestionCardsEnabled(false);
    }

    @Override
    public void hideAiTyping() {
        layoutStatus.setVisibility(View.GONE);
        btnSendMessage.setEnabled(true);
        setQuestionCardsEnabled(true);
    }

    @Override
    public void clearMessageInput() {
        if (etMessageInput != null) {
            etMessageInput.setText("");
        }
    }

    @Override
    public void showCreateConversationError(String error) {
        showError("Lỗi tạo cuộc trò chuyện: " + error);
        layoutStatus.setVisibility(View.GONE);
        btnSendMessage.setEnabled(true);
        setQuestionCardsEnabled(true);
    }

    @Override
    public void showSendMessageError(String error) {
        showError("Lỗi gửi tin nhắn: " + error);
        layoutStatus.setVisibility(View.GONE);
        btnSendMessage.setEnabled(true);
        setQuestionCardsEnabled(true);
    }

    @Override
    public void navigateToChatDetail(String conversationId, String conversationTitle) {
        if (getActivity() != null) {
            ChatDetailFragment chatDetailFragment = ChatDetailFragment.newInstance(conversationId, conversationTitle);

            getActivity().getSupportFragmentManager()
                    .beginTransaction()
                    .replace(R.id.fragment_container, chatDetailFragment)
                    .addToBackStack(null)
                    .commit();
        }
    }

    @Override
    public void showMessage(String message) {
        if (getContext() != null) {
            Toast.makeText(getContext(), message, Toast.LENGTH_SHORT).show();
        }
    }

    @Override
    public void showError(String message) {
        if (getContext() != null) {
            Toast.makeText(getContext(), message, Toast.LENGTH_LONG).show();
        }
    }

    @Override
    public void showLoading() {
        showCreatingConversation();
    }

    @Override
    public void hideLoading() {
        hideCreatingConversation();
    }

    /**
     * Enable/disable question cards
     */
    private void setQuestionCardsEnabled(boolean enabled) {
        cardQuestion1.setEnabled(enabled);
        cardQuestion2.setEnabled(enabled);
        cardQuestion3.setEnabled(enabled);

        float alpha = enabled ? 1.0f : 0.6f;
        cardQuestion1.setAlpha(alpha);
        cardQuestion2.setAlpha(alpha);
        cardQuestion3.setAlpha(alpha);
    }

    @Override
    public void onDestroyView() {
        if (presenter != null) {
            presenter.detachView();
        }
        super.onDestroyView();
    }
}

package com.vhn.doan.presentation.chat;

import android.util.Log;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.vhn.doan.data.ChatMessage;
import com.vhn.doan.data.Conversation;
import com.vhn.doan.data.repository.ChatRepository;
import com.vhn.doan.data.repository.RepositoryCallback;

/**
 * Presenter cho NewChat feature - Tạo cuộc trò chuyện mới
 */
public class NewChatPresenter implements NewChatContract.Presenter {

    private static final String TAG = "NewChatPresenter";

    private final ChatRepository chatRepository;
    private final FirebaseAuth firebaseAuth;
    private NewChatContract.View view;

    public NewChatPresenter(ChatRepository chatRepository) {
        this.chatRepository = chatRepository;
        this.firebaseAuth = FirebaseAuth.getInstance();
    }

    @Override
    public void attachView(NewChatContract.View view) {
        this.view = view;
    }

    @Override
    public void detachView() {
        this.view = null;
    }

    private boolean isViewAttached() {
        return view != null;
    }

    @Override
    public void createConversationAndSendMessage(String firstMessage) {
        if (firstMessage == null || firstMessage.trim().isEmpty()) {
            if (isViewAttached()) {
                view.showSendMessageError("Vui lòng nhập nội dung tin nhắn");
            }
            return;
        }

        FirebaseUser currentUser = firebaseAuth.getCurrentUser();
        if (currentUser == null) {
            if (isViewAttached()) {
                view.showCreateConversationError("Bạn cần đăng nhập để tạo cuộc trò chuyện");
            }
            return;
        }

        String trimmedMessage = firstMessage.trim();
        String userId = currentUser.getUid();

        if (isViewAttached()) {
            view.showCreatingConversation();
            view.clearMessageInput();
        }

        // Bước 1: Tạo cuộc trò chuyện mới
        chatRepository.createConversation(userId, trimmedMessage, new RepositoryCallback<Conversation>() {
            @Override
            public void onSuccess(Conversation conversation) {
                Log.d(TAG, "Conversation created successfully: " + conversation.getId());

                if (isViewAttached()) {
                    view.hideCreatingConversation();
                    view.showSendingMessage();
                }

                // Bước 2: Lưu tin nhắn đầu tiên của người dùng
                long timestamp = System.currentTimeMillis();
                ChatMessage userMessage = new ChatMessage(conversation.getId(), userId, trimmedMessage, true, timestamp);
                String topic = chatRepository.extractTopic(trimmedMessage);
                userMessage.setTopic(topic);

                chatRepository.saveChatMessage(userMessage, new RepositoryCallback<ChatMessage>() {
                    @Override
                    public void onSuccess(ChatMessage savedUserMessage) {
                        Log.d(TAG, "User message saved successfully");

                        if (isViewAttached()) {
                            view.hideSendingMessage();
                            view.showAiTyping();
                        }

                        // Bước 3: Gửi tin nhắn tới AI
                        chatRepository.sendMessageToAI(trimmedMessage, new RepositoryCallback<String>() {
                            @Override
                            public void onSuccess(String aiResponse) {
                                Log.d(TAG, "AI response received: " + aiResponse);

                                // Bước 4: Lưu phản hồi AI
                                ChatMessage aiMessage = new ChatMessage(conversation.getId(), userId, aiResponse, false, System.currentTimeMillis());
                                aiMessage.setTopic(topic);

                                chatRepository.saveChatMessage(aiMessage, new RepositoryCallback<ChatMessage>() {
                                    @Override
                                    public void onSuccess(ChatMessage savedAiMessage) {
                                        Log.d(TAG, "AI message saved successfully");

                                        // Bước 5: Cập nhật thông tin cuộc trò chuyện
                                        conversation.setLastMessage(aiResponse);
                                        conversation.setLastMessageTime(savedAiMessage.getTimestamp());
                                        conversation.setFromUser(false);
                                        conversation.setMessageCount(2); // User message + AI message

                                        chatRepository.updateConversation(conversation, new RepositoryCallback<Conversation>() {
                                            @Override
                                            public void onSuccess(Conversation updatedConversation) {
                                                Log.d(TAG, "Conversation updated successfully");

                                                if (isViewAttached()) {
                                                    view.hideAiTyping();
                                                    view.showMessage("Cuộc trò chuyện đã được tạo thành công!");

                                                    // Chuyển đến màn hình chat detail
                                                    view.navigateToChatDetail(updatedConversation.getId(), updatedConversation.getTitle());
                                                }
                                            }

                                            @Override
                                            public void onError(String error) {
                                                Log.e(TAG, "Failed to update conversation: " + error);

                                                if (isViewAttached()) {
                                                    view.hideAiTyping();
                                                    // Vẫn chuyển đến chat detail dù cập nhật conversation lỗi
                                                    view.navigateToChatDetail(conversation.getId(), conversation.getTitle());
                                                }
                                            }
                                        });
                                    }

                                    @Override
                                    public void onError(String error) {
                                        Log.e(TAG, "Failed to save AI message: " + error);

                                        if (isViewAttached()) {
                                            view.hideAiTyping();
                                            view.showSendMessageError("Không thể lưu phản hồi AI: " + error);
                                        }
                                    }
                                });
                            }

                            @Override
                            public void onError(String error) {
                                Log.e(TAG, "Failed to get AI response: " + error);

                                if (isViewAttached()) {
                                    view.hideAiTyping();
                                    view.showSendMessageError("Không thể nhận phản hồi từ AI: " + error);
                                }
                            }
                        });
                    }

                    @Override
                    public void onError(String error) {
                        Log.e(TAG, "Failed to save user message: " + error);

                        if (isViewAttached()) {
                            view.hideSendingMessage();
                            view.showSendMessageError("Không thể lưu tin nhắn: " + error);
                        }
                    }
                });
            }

            @Override
            public void onError(String error) {
                Log.e(TAG, "Failed to create conversation: " + error);

                if (isViewAttached()) {
                    view.hideCreatingConversation();
                    view.showCreateConversationError("Không thể tạo cuộc trò chuyện: " + error);
                }
            }
        });
    }
}

package com.vhn.doan.presentation.home;

import android.content.Intent;
import android.os.Bundle;
import android.util.Log;
import android.view.MenuItem;

import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.fragment.app.Fragment;

import com.google.android.material.bottomnavigation.BottomNavigationView;
import com.google.android.material.navigation.NavigationBarView;
import com.vhn.doan.R;
import com.vhn.doan.presentation.auth.LoginActivity;
import com.vhn.doan.presentation.chat.ChatFragment;
import com.vhn.doan.presentation.profile.ProfileFragment;
import com.vhn.doan.presentation.reminder.ReminderFragment;
import com.vhn.doan.services.AuthManager;
import com.vhn.doan.services.ReminderManager;
import com.vhn.doan.utils.UserSessionManager;

/**
 * HomeActivity là màn hình chính của ứng dụng sau khi đăng nhập
 * Chứa HomeFragment và các Fragment khác thông qua BottomNavigationView
 */
public class HomeActivity extends AppCompatActivity {

    private static final String TAG = "HomeActivity";
    private BottomNavigationView bottomNavigationView;
    private AuthManager authManager;
    private ReminderManager reminderManager;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_home);

        // Khởi tạo AuthManager
        authManager = new AuthManager(this);

        // Kiểm tra đăng nhập
        if (!authManager.isUserLoggedIn()) {
            // Chuyển về màn hình đăng nhập nếu chưa đăng nhập
            Intent intent = new Intent(this, LoginActivity.class);
            intent.setFlags(Intent.FLAG_ACTIVITY_CLEAR_TOP | Intent.FLAG_ACTIVITY_NEW_TASK);
            startActivity(intent);
            finish();
            return;
        }

        // Khởi tạo ReminderManager
        reminderManager = new ReminderManager(new UserSessionManager(this));

        // Khởi động ReminderForegroundService
        reminderManager.startReminderService(this);
        Log.d(TAG, "ReminderForegroundService đã được khởi động từ HomeActivity");

        // Khởi tạo và thiết lập BottomNavigationView
        setupBottomNavigation();

        // Mặc định hiển thị HomeFragment khi khởi động
        if (savedInstanceState == null) {
            loadFragment(new HomeFragment());
        }
    }

    @Override
    protected void onResume() {
        super.onResume();

        // Khởi động lại ReminderForegroundService khi activity được resume
        if (reminderManager != null) {
            // Khởi động lại service để đảm bảo nó đang chạy
            reminderManager.startReminderService(this);

            // Khởi động lại tất cả reminders đang active
            reminderManager.restartAllReminders(this);

            // Kiểm tra và hiển thị reminders đã bị miss
            reminderManager.checkAndShowMissedReminders(this);

            Log.d(TAG, "Đã khởi động lại và kiểm tra reminders trong onResume");
        }
    }

    /**
     * Method để xử lý onClick từ XML layout cho nút tạo reminder
     * Sửa lỗi: IllegalStateException: Could not find method onCreateReminderClick
     */
    public void onCreateReminderClick(android.view.View view) {
        // Tìm ReminderFragment hiện tại và gọi method tạo reminder
        Fragment currentFragment = getSupportFragmentManager().findFragmentById(R.id.fragment_container);
        if (currentFragment instanceof ReminderFragment) {
            ReminderFragment reminderFragment = (ReminderFragment) currentFragment;
            reminderFragment.onCreateReminderClick();
        }
    }

    private void setupBottomNavigation() {
        bottomNavigationView = findViewById(R.id.bottom_navigation);
        bottomNavigationView.setOnItemSelectedListener(new NavigationBarView.OnItemSelectedListener() {
            @Override
            public boolean onNavigationItemSelected(@NonNull MenuItem item) {
                Fragment fragment = null;
                int itemId = item.getItemId();

                if (itemId == R.id.nav_home) {
                    fragment = HomeFragment.newInstance();
                } else if (itemId == R.id.nav_chat) {
                    // Kích hoạt ChatFragment - Trợ lý AI về sức khỏe
                    fragment = ChatFragment.newInstance();
                } else if (itemId == R.id.nav_reminders) {
                    // Kích hoạt ReminderFragment
                    fragment = ReminderFragment.newInstance();
                } else if (itemId == R.id.nav_videos) {
                    // Tạm thời vẫn dùng HomeFragment
                    // Sẽ thay thế bằng ShortVideoFragment khi phát triển
                    fragment = HomeFragment.newInstance();
                    // fragment = ShortVideoFragment.newInstance();
                } else if (itemId == R.id.nav_profile) {
                    // Sử dụng ProfileFragment - chức năng yêu thích đã được tích hợp vào đây
                    fragment = ProfileFragment.newInstance();
                }

                return loadFragment(fragment);
            }
        });
    }

    private boolean loadFragment(Fragment fragment) {
        if (fragment != null) {
            getSupportFragmentManager()
                    .beginTransaction()
                    .replace(R.id.fragment_container, fragment)
                    .commit();
            return true;
        }
        return false;
    }
}

